}

float angle(vec2 uv, float i) {
    float inc = i / float(STEP) * PI * 2.;
    float time = sin(2. * iTime + i * .12) * .5 + .5;
    uv.x += sin(inc) * time * .45;
    uv.y += cos(inc) * time * .45;
    uv = rotate(uv, -inc);
    float c = smoothstep(-PI * float(STEP), PI, atan(uv.x, uv.y));
    return c;
}

