#define ROTOZOOM
#define dt 0.05

// Utilities
#define drawSDF(dist, col) color = mix(color, col, smoothstep(unit, 0.0, dist))

mat2 Rotate2D(in float a) {
    float c = cos(a), s = sin(a);
    return mat2(c, s, -s, c);
}

// SDFs
float sdLine(in vec2 p, in vec2 a, in vec2 b) {
    vec2 pa = p - a, ba = b - a;
    return length(pa - ba * clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0));
}

float sdDisk(in vec2 p, in vec2 o, in float r) {
    return length(p - o) - r;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Boilerplate
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y * 2.0;
    float unit = 4.0 / iResolution.y;
    float time = 0.1 * iTime;
    vec3 color = vec3(1.0);

    // Point moving along the spiral
    vec2 pPrev = vec2(0.0), pCur = vec2(0.0);
    float pos = 10.0 * sin(time);
    for (float t=0.0; t < abs(pos); t += dt) {
        float t2 = t * t;
        vec2 dp = vec2(cos(t2), sin(t2)) * sign(pos) * dt;
        pPrev += dp;
        if (t + dt < abs(pos)) pCur += dp;
    }

    float lerp = fract(pos / dt);
    if (pos > 0.0) lerp = 1.0 - lerp;
    vec2 p = mix(pPrev, pCur, lerp);

    // Rotozoom
    #ifdef ROTOZOOM
    float pos2 = pos * pos;
    float rot = -atan(sin(pos2), cos(pos2)) + 1.57;
    uv *= Rotate2D(rot);
    float zoom = abs(cos(time));
    uv *= zoom;
    unit *= zoom;
    uv += p;
    #endif

    // Parametric plot
    vec2 suv = uv * sign(uv.x);
    vec2 prev = vec2(0.0);
    for (float t=0.0; t < 10.0; t += dt) {
        float t2 = t * t;
        vec2 cur = prev + vec2(cos(t2), sin(t2)) * dt; // Integrate (cos(t^2), sin(t^2))
        drawSDF(sdLine(suv, prev, cur), vec3(0.0));
        prev = cur;
    }

    fragColor = vec4(color, 1.0);
}

//	Simplex 3D Noise 
//	by Ian McEwan, Ashima Arts
//
vec4 permute(vec4 x){return mod(((x*34.0)+1.0)*x, 289.0);}
vec4 taylorInvSqrt(vec4 r){return 1.79284291400159 - 0.85373472095314 * r;}

float snoise(vec3 v){ 
  const vec2  C = vec2(1.0/6.0, 1.0/3.0) ;
  const vec4  D = vec4(0.0, 0.5, 1.0, 2.0);

// First corner
  vec3 i  = floor(v + dot(v, C.yyy) );
  vec3 x0 =   v - i + dot(i, C.xxx) ;

// Other corners
  vec3 g = step(x0.yzx, x0.xyz);
  vec3 l = 1.0 - g;
  vec3 i1 = min( g.xyz, l.zxy );
  vec3 i2 = max( g.xyz, l.zxy );

  //  x0 = x0 - 0. + 0.0 * C 
  vec3 x1 = x0 - i1 + 1.0 * C.xxx;
  vec3 x2 = x0 - i2 + 2.0 * C.xxx;
  vec3 x3 = x0 - 1. + 3.0 * C.xxx;

// Permutations
  i = mod(i, 289.0 ); 
  vec4 p = permute( permute( permute( 
             i.z + vec4(0.0, i1.z, i2.z, 1.0 ))
           + i.y + vec4(0.0, i1.y, i2.y, 1.0 )) 
           + i.x + vec4(0.0, i1.x, i2.x, 1.0 ));

// Gradients
// ( N*N points uniformly over a square, mapped onto an octahedron.)
  float n_ = 1.0/7.0; // N=7
  vec3  ns = n_ * D.wyz - D.xzx;

  vec4 j = p - 49.0 * floor(p * ns.z *ns.z);  //  mod(p,N*N)

  vec4 x_ = floor(j * ns.z);
  vec4 y_ = floor(j - 7.0 * x_ );    // mod(j,N)

  vec4 x = x_ *ns.x + ns.yyyy;
  vec4 y = y_ *ns.x + ns.yyyy;
  vec4 h = 1.0 - abs(x) - abs(y);

  vec4 b0 = vec4( x.xy, y.xy );
  vec4 b1 = vec4( x.zw, y.zw );

  vec4 s0 = floor(b0)*2.0 + 1.0;
  vec4 s1 = floor(b1)*2.0 + 1.0;
  vec4 sh = -step(h, vec4(0.0));

  vec4 a0 = b0.xzyw + s0.xzyw*sh.xxyy ;
  vec4 a1 = b1.xzyw + s1.xzyw*sh.zzww ;

  vec3 p0 = vec3(a0.xy,h.x);
  vec3 p1 = vec3(a0.zw,h.y);
  vec3 p2 = vec3(a1.xy,h.z);
  vec3 p3 = vec3(a1.zw,h.w);

//Normalise gradients
  vec4 norm = taylorInvSqrt(vec4(dot(p0,p0), dot(p1,p1), dot(p2, p2), dot(p3,p3)));
  p0 *= norm.x;
  p1 *= norm.y;
  p2 *= norm.z;
  p3 *= norm.w;

// Mix final noise value
  vec4 m = max(0.6 - vec4(dot(x0,x0), dot(x1,x1), dot(x2,x2), dot(x3,x3)), 0.0);
  m = m * m;
  return 42.0 * dot( m*m, vec4( dot(p0,x0), dot(p1,x1), 
                                dot(p2,x2), dot(p3,x3) ) );
}

vec2 rotate(vec2 v, float a) {
	float s = sin(a);
	float c = cos(a);
	mat2 m = mat2(c, -s, s, c);
	return m * v;
}

vec3 rgb(float r, float g, float b) {
    return vec3(r/255., g/255., b/255.);
}

float caustic(vec3 n) {
    float c = snoise(n);
    c = length(vec2(c * 2.));
    return smoothstep(0.02, 0.04, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // perspective
    uv.x *= (2.2 * (.4 + uv.y * .5));

    uv.x -= uv.y;

    // caustics
    float z = 8.;
    float n = caustic(vec3(uv.x * z, uv.y * z, iTime * .2));
    float n1 = caustic(vec3(uv.x * z, uv.y * z, iTime * .49));
    float n2 = caustic(vec3(uv.x * z, uv.y * z, iTime * .44));

    // colors
    vec3 baseCol = rgb(140., 134., 106.);
    vec3 topCol = rgb(79., 150., 153.);
    vec3 gradient = mix(baseCol, topCol, uv.y);
    vec3 col = gradient;

    // mix
    col *= vec3(1. - n * .1);
    col *= vec3(1. - n1 * .1);
    col *= vec3(1. - n2 * .1);
    col += col * 1.1;

    // noise
    col += .03 * snoise(vec3(uv.x * 100., uv.y * 500., uv.x * 200.));
    fragColor = vec4(col,1.0);
}

#define hue(v) ( .6 + .6 * cos( 2.*PI*(v) + vec3(0,-2.*PI/3.,2.*PI/3.) ) )     // Credit: https: shadertoyunofficial.wordpress.com/2019/01/02/programming-tricks-in-shadertoy-glsl/
#define rotate(a) mat2(cos( a + vec4(0,33,11,0)))                             // or on shadertoy: FabriceNeyret2                  

#define PI 3.14159265359
#define TAU 6.28318530718


vec2 hash22(vec2 p)  // Random noise. Credit: https://www.youtube.com/watch?v=l-07BXzNdPw
{
    vec3 a = fract(p.xyx * vec3(123.34,234.34,345.65));    
    a += dot(a, a + 34.45);
    return fract(vec2(a.x * a.y, a.y * a.z));

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime;
    vec2 UV = (2.0 * fragCoord - iResolution.xy) / iResolution.y;  // Convert coordinate to (-1,-1) -> (1.0, 1.0)                    


    UV += smoothstep(0.25  ,  -0.25, length(UV)-0.6); // Distortion effect    
    UV *= (3.0 + (3.0 * abs(sin(t * 0.05))));  // Animate camera zoom    
    UV *= rotate(sin((t+ sin(1.2)) * 0.5)); // Animate camera rotation        
    UV += (2.0 + (1.0 * sin(t))) * vec2(sin(t), cos(t)); // Animate camera panning;



    vec2 GV = fract(UV) - 0.5;    // Voronoi  Credit: https://www.youtube.com/watch?v=l-07BXzNdPw
    float minDist = 100.0;
    vec2 ide = floor(UV);    
    for (float y = -1.0; y <= 1.0; y++)
        for (float x = -1.0; x <= 1.0; x++)
        {
            vec2 offs = vec2(x, y);
            vec2 n = hash22(ide + offs);
            vec2 p = offs + sin(n * t) * 0.5;
            float d = length(GV - p);

            if (d < minDist)
            {
                minDist = d;                

            }

        }                       // end of Voronoi



    minDist = abs(sin(minDist * (4.0 + (3.0 * sin(t))))); // Animate mindist

    minDist -= smoothstep(0.8, 1.0, minDist); // make spaces between voronoi black
    vec3 col = hue(fract(t * 0.1) + (0.85 *fract(minDist + (t * 0.2))));    // Apply hue color
    fragColor = vec4(mix(vec3(0), col, minDist),1) ; // final color output

}

const vec3  eye      = vec3 ( 0, 0, 6 );
const vec3  light    = vec3  ( 0.0, 1.0, 5.0 );
const int   maxSteps = 100;
const float eps      = 0.01;

// Rotation matrix around the X axis.
mat3 rotateX(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(1, 0, 0),
        vec3(0, c, s),
        vec3(0, -s, c)
    );
}

// Rotation matrix around the Y axis.
mat3 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, 0, s),
        vec3(0, 1, 0),
        vec3(-s, 0, c)
    );
}

float dSphere ( vec3 p, float r, in vec3 c )
{
	return length ( p - c ) - r;
}

float dMoon ( vec3 p, in vec3 c )
{
    float rad = length(c.xz);
    vec3 pos = vec3(rad*sin(0.2*iTime), c.y, rad*cos(0.2*iTime));
	return length ( p - pos ) - 0.2 + 0.005*sin(p.x*50.0 + 3.0*iTime) + 0.007*sin(p.y*20.0) +  0.005*sin(p.z*50.0);
}


float length8 ( in vec2 p )
{
    return pow ( pow ( p.x, 8.0 ) + pow ( p.y, 8.0 ), 1.0/ 8.0 );
}

float length8 ( in vec3 p )
{
    return pow ( pow ( p.x, 8.0 ) + pow ( p.y, 8.0 ) + pow ( p.z, 8.0 ), 1.0/ 8.0 );
}


float dTorus ( vec3 p, vec2 t )
{
	vec2	q = vec2 ( length ( p.xz ) - t.x, p.y );

	return length8 ( q ) - t.y;
}

float dCyl ( vec3 p, vec3 c )
{
    return length ( p.xz - c.xy ) - c.z;
}

float dPlane ( vec3 p, vec4 n )
{
    return dot ( p, n.xyz ) + n.w;
}

float dCone ( vec3 p, vec2 c )
{
    float q = length ( p.yz );
    return dot ( c, vec2 ( q, p.x ) );
}

float dBox ( vec3 p, vec3 b, vec3 c )
{
return length ( max ( abs ( p - c ) - b, 0.0 ) );
}

float smin ( float a, float b, float k )
{
	float res = exp ( -k*a ) + exp ( -k*b );
	return -log ( res ) / k;
}


float sdf ( in vec3 p, in mat3 m )
{
    vec3 q = m * p;
    return 
        min(
            max(
                max(
                    dSphere(q, 1.0, vec3(0,0,0)), -dTorus(q, vec2(1.0, 0.05))
                ), 
                -dSphere(q, 0.4 + 0.005*sin((0.6*q.y + q.z)*100.0), vec3(0, 0.6, 1))
            ), dMoon(q, vec3(1.5, 1.5, 2.2)) 
        );

}

vec3 trace ( in vec3 from, in vec3 dir, out bool hit, in mat3 m )
{
	vec3	p         = from;
	float	totalDist = 0.0;

	hit = false;

	for ( int steps = 0; steps < maxSteps; steps++ )
	{
		float	dist = sdf ( p, m );

		if ( dist < 0.01 )
		{
			hit = true;
			break;
		}

		totalDist += dist;

		if ( totalDist > 10.0 )
			break;

		p += dist * dir;
	}

	return p;
}

vec3 generateNormal ( vec3 z, float d, in mat3 m )
{
    float e   = max (d * 0.5, eps );
    float dx1 = sdf(z + vec3(e, 0, 0), m);
    float dx2 = sdf(z - vec3(e, 0, 0), m);
    float dy1 = sdf(z + vec3(0, e, 0), m);
    float dy2 = sdf(z - vec3(0, e, 0), m);
    float dz1 = sdf(z + vec3(0, 0, e), m);
    float dz2 = sdf(z - vec3(0, 0, e), m);

    return normalize ( vec3 ( dx1 - dx2, dy1 - dy2, dz1 - dz2 ) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

        // Normalized pixel coordinates (from 0 to 1)
    bool hit;
	vec3 mouse = vec3(iMouse.xy/iResolution.xy - 0.5,iMouse.z-.5);
    mat3 m     = rotateX ( 6.0*mouse.y ) * rotateY ( 6.0*mouse.x);
    vec2 scale = 9.0 * iResolution.xy / max ( iResolution.x, iResolution.y ) ;
    vec2 uv    = scale * ( fragCoord/iResolution.xy - vec2 ( 0.5 ) );
	vec3 dir   = normalize ( vec3 ( uv, 0 ) - eye );
    vec4 color = vec4 ( 0, 0, 0, 1 );
    vec3 p     = trace ( eye, dir, hit, m );


	if ( hit )
	{
		vec3  l  = normalize        ( light - p );
        vec3  v  = normalize        ( eye - p );
		vec3  n  = generateNormal   ( p, 0.001, m );
		float nl = max ( 0.0, dot ( n, l ) );
        vec3  h  = normalize ( l + v );
        float hn = max ( 0.0, dot ( h, n ) );
        float sp = pow ( hn, 150.0 );

		color = 0.5*vec4 ( nl ) + 0.5 * sp * vec4 ( 0, 1, 1, 1 );
	} 
    // output to screen
    fragColor = color;
}

