float map(vec3 p){

 p.z-=iTime*20.;

   float t=floor(iTime*2.0)+pow(fract(iTime*2.0),.75);
p.xy*=rot(t);

  p.xy=fmod(p.xy,12.0);
float k=10.5;
p=mod(p,k)-k*0.5;

