vec3 gn(vec3 p){
  vec2 t=vec2(0.001,0.0);
  return normalize(
      vec3(
        map(p+t.xyy)-map(p-t.xyy),
        map(p+t.yxy)-map(p-t.yxy),
        map(p+t.yyx)-map(p-t.yyx)
        )
    );
}




