void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float foob = iTime * .5;
    float myTime = (int(foob) % 2 == 0 ? fract(foob) : 1.0 - fract(foob));

    vec2 loc1 = vec2(myTime, .67);
    if (distance(uv, loc1) < .01)
        col = vec3(1);

    vec2 loc2 = vec2(smoothstep(0.0, 1.0, myTime), .33);
    if (distance(uv, loc2) < .01)
        col = vec3(1);

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define M_PI 3.1415926535897932384626433832795

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
	vec2 uvTrue = fragCoord.xy / iResolution.xy;

    float uv = uvTrue.y - .5;

    //float glowWidth = abs(10. / (500.0 * uv));
    float glowWidth = 1. - pow(abs(uv + sin(uvTrue.x * 10. + iTime ) / 5.), .13);
    vec3 color = vec3(glowWidth * 2. , glowWidth, glowWidth);

	fragColor = vec4(color, 1.0);
}

float tex(vec2 uv, float s)
{    
    float col;
    float ntiles = 10.0;

    // Calculate the shape of the LEDs
    vec2 r = mod(uv * ntiles, 1.0) - vec2(0.5);
    col = 1.0 - dot(r, r);

    // Add some light to the tunnel
    vec4 n = texture(iChannel1, floor(uv * ntiles) / ntiles);
    col *= mod(n.r * n.g * n.b * s + iTime * 0.1 + clamp(s, 0.0, 0.6), 1.0);    
    return clamp(col, 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 uv1 = uv * 2.0 - 1.0;

    uv1.x *= iResolution.x / iResolution.y;

    // Calculate new UV coordinates
    vec2 center = vec2(0.0, 0.0) + 
        vec2(0.075*(0.5 + 0.5 * sin(iTime*4.0)),
             0.05*(0.5 + 0.5 * sin(iTime*0.01 + 1.7)));
    vec2 p = uv1 - center;
    float r = length(p);
    float a = atan(p.y, p.x) * 3.0 / 3.14;
    vec2 uv2= vec2(1.0 / r + iTime*0.25, a);

    // Read the sound texture
    float sound = texture(iChannel2, vec2(0.01, 1.0 - r)).r;
    sound = pow(sound, 1.5);

    // Calculate the colors
    vec3 c1 = vec3(0.02, 0.1, 4.02);
    vec3 c2 = mix( vec3(2.0, 0.6, 0.6), vec3(0.6, 0.6, 1.0), vec3(0.5 + 0.5 * sin(iTime*0.1)));
    vec3 c  = mix(c1, c2, r);
    vec3 coltunnel =  sound * c * tex(uv2, sound) + ( 0.15 * texture(iChannel0, uv2).rgb);
    vec3 colback   = vec3(0.15,0.15,0.14);

    // Mix the colors
	fragColor = vec4(r * coltunnel + (3.0 - r) * colback, 2.6);
}

#define STEPS 220.0
#define MDIST 150.0
#define pi 3.1415926535
#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))
#define pmod(p,x) (mod(p,x)-0.5*(x))
vec3 glow = vec3(0);
vec3 rlim(vec3 p, float c, vec3 l ){
    return p-c*clamp(round(p/c),-l,l);
}
float h21(vec2 a){
    return fract(sin(dot(a,vec2(43.234,25.433)))*944.3434);
}
float frame(vec3 p, vec3 b, float e){
  p = abs(p)-b;
  vec3 q = abs(p+e)-e;
  return min(min(
      length(max(vec3(p.x,q.y,q.z),0.0))+min(max(p.x,max(q.y,q.z)),0.0),
      length(max(vec3(q.x,p.y,q.z),0.0))+min(max(q.x,max(p.y,q.z)),0.0)),
      length(max(vec3(q.x,q.y,p.z),0.0))+min(max(q.x,max(q.y,p.z)),0.0));
}
