float box(vec3 p, vec3 b){
    vec3 d= abs(p)-b;
    return max(d.x,max(d.y,d.z));
}
//https://www.desmos.com/calculator/dxixbmrvmb
float easeOutElastic(float x) {
    float c4 = (2. * pi) / 3.;
    return x = pow(1.63,-10.*x) * sin((x*5.25-.75)*c4)+1.;
}
//I would like to make a more procdeural version of this but so far this one still
//looks the best. 
float easeOutBounce(float x) {
    float n1 = 7.5625;
    float d1 = 2.75;
    if (x < 1. / d1) {
        return n1 * x * x;
    } else if (x < 2. / d1) {
        return n1 * (x -= 1.5 / d1) * x + 0.75;
    } else if (x < 2.5 / d1) {
        return n1 * (x -= 2.25 / d1) * x + 0.9375;
    } else {
        return n1 * (x -= 2.625 / d1) * x + 0.984375;
    }
}
//https://www.shadertoy.com/view/fdsXRf
float timeRemap (float t, float s1, float s2){
    return (s1-s2)*(fract(t)*step(1.0,mod(t,2.0))+floor(t*0.5))+t*s2;
}
vec2 map(vec3 p){
    float t = iTime*0.8*(6./5.);
    float ttt = t;
    vec3 po = p;
    vec2 a = vec2(1);
    vec2 b = vec2(2);
    float m= 2.4;
    vec2 id = floor(clamp(p.xz,-13.,13.)/m-0.5);
    float off = 0.;

    float cycle = mod(floor(t/4.0+0.5),6.0);
    //cycle = .0;
    if(cycle == 0.0){
        t+=length(id+1.0)*0.07;
    }
    if(cycle == 1.0){
        t+=length(id-21.0)*0.07-1.685;
    }
    if(cycle == 2.0){
        if(mod(id.y+mod(id.x,2.0),2.0)<1.0)t+=0.15;
        id = abs(id+1.);
        t+=max(id.x,id.y)*0.2;
        t-=0.15;
    }
    if(cycle == 3.0){
        t+=0.562;
        t+=id.y*0.08;
        t+=id.x*0.01;
    }
    /*
    if(cycle == 4.0){
        t+=0.4;
        //id=id+1.0;
        id.y+=1.0;
        id.x+=7.0;
        float ang = atan(id.x,id.y)*0.2;
        t+=ang;
    }
    */
    if(cycle == 4.0){
        t+=0.3;
        id+=1.0;
        t+=(mod(id.y,2.0)*3.0-2.0)*abs(id.x)*0.0175;
    }
    if(cycle == 5.0){
        t+=h21(id);
    }
    t = timeRemap(t,1.0,0.0);//changing the third value here makes some interesting bugs




    float tt = t;
    t = (mod(t,2.0)>1.0)?easeOutBounce(fract(t))+floor(t):easeOutElastic(fract(t))+floor(t);

    if(mod(tt,2.0)<1.0) off = t-floor(tt);
    else off = 1.0-(t-floor(tt));

    //CUBES
    p.y-=off*13.;
    p = rlim(p,m,vec3(5,0,5));
    a.x = box(p,vec3(0.99));
    //CUBE FRAMES
    b.x = frame(p,vec3(1.0),0.06)*0.8;
    float gl = 0.05;
    glow+=gl/(gl+b.x*b.x)*vec3(0.373,0.200,1.000)*clamp((0.5-abs(0.5-off))*5.0,0.0,1.0);

    a=(a.x<b.x)?a:b;

    //Domain fuckery
    //#define rdt(a) pow(smoothstep(0.5,1.0,abs(a)),2.0)*1.0
    vec3 d = abs(p)-vec3(m/1.7,0,m/1.7);
    vec3 d2 = abs(po)-m*5.4;
    float domBox = max(d.x,d.z);
    float outBox = max(d2.x,d2.z);
    domBox = min(-outBox,domBox);
    a.x = min(-domBox,a.x);

    //Floor
    p = po;
    b.x = box(p-vec3(0,-31,0),vec3(13,30,13));
    b.y = 3.0;
    a=(a.x<b.x)?a:b;

    //BOX RINGS
    t = ttt;
    float sz = 16.;
    p.y+=-t*14.0;
    p.y = pmod(p.y,8.0);
    b.x = box(p,vec3(sz,1,sz));
    b.x = max(-box(p,vec3(sz-1.,6,sz-1.)),b.x);
    b.x = max(po.y-3.,b.x);

    glow+=0.1/(0.1+b.x*b.x)*vec3(0.733,0.000,1.000)*0.2*smoothstep(4.0,-6.0,po.y);
    b.y = 5.0;
    a=(a.x<b.x)?a:b;

    return a;
}
vec3 norm(vec3 p){
    vec2 e= vec2(0.001,0);
    return normalize(map(p).x-vec3(
    map(p-e.xyy).x,
    map(p-e.yxy).x,
    map(p-e.yyx).x));
}

void render( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0);
    vec3 ro = vec3(0,12,-30);
    ro.zx*=rot(iTime*0.15*pi/3.*(6./5.));

    vec3 lk = vec3(0,2,0);
    vec3 f = normalize(lk-ro);
    vec3 r = normalize(cross(vec3(0,1,0),f));
    vec3 rd = f*0.7+uv.x*r+uv.y*cross(f,r);
    vec3 p = vec3(0);
    vec2 d = vec2(0);
    float dO = 0.;
    bool hit = false;
    for(float i = 0.; i<STEPS; i++){
        p = ro+rd*dO;
        d = map(p);

        if((d.x)<0.005){
            if(d.y == 5.0){
                d.x = 0.08;
            }
            else{
            hit = true;
            break;
            }
        }
        if(dO>MDIST){
            p = ro+rd*MDIST;
            dO = MDIST;
            break;
        }
        dO+=d.x*0.9;
    }
    vec3 al = vec3(0);
    if(hit){
        vec3 n = norm(p);
        vec3 r = reflect(rd, n);
        if(d.y==1.0) al = vec3(0.000,0.984,1.000)*1.4;
        if(d.y==2.0) al = vec3(0.004,0.137,0.537);
        if(d.y==3.0) {
        al = vec3(0.588,0.180,1.000);
        al+=smoothstep(0.95,1.0,sin(p.x*pi*0.75+pi/2.0))*1.6;
        al+=smoothstep(0.95,1.0,sin(p.y*pi*0.75+2.0))*1.6;
        al+=smoothstep(0.95,1.0,sin(p.z*pi*0.75+pi/2.0))*1.6;
        }
        //Lighting EQs from blackle
        float diff = length(sin(n*3.)*.5+.5)/sqrt(3.);
        float spec = length(sin(r*2.)*.4+.6)/sqrt(3.);
        col = al*diff+pow(spec,8.0)*0.4*vec3(1.000,0.722,0.910);
        #define AO(a,n,p) smoothstep(-a,a,map(p+n*a).x)
        float ao = AO(0.05,n,p)*AO(0.1,n,p);
        ao*=smoothstep(-.4,.4,map(p+n*.4).x*2.1);
        col*=pow(ao,0.7);
    }
    else{
        col = mix(vec3(0),vec3(0.055,0.000,0.161),length(pow(uv,vec2(3.0))));

    }
    //col = sqrt(col);
    col+=glow*0.07;
    fragColor = vec4(col,1.0);
}

#define AA 1.0
#define ZERO min(0.0,iTime)
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float px = 1.0/AA;
    vec4 col = vec4(0);

    if(AA==1.0) {render(col,fragCoord); fragColor = col; return;}

    for(float i = ZERO; i <AA; i++){
        for(float j = ZERO; j <AA; j++){
            vec4 col2;
            vec2 coord = vec2(fragCoord.x+px*i,fragCoord.y+px*j);
            render(col2,coord);
            col.rgb+=col2.rgb;
            glow = vec3(0);
        }
    }
    col/=AA*AA;
    fragColor = vec4(col);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float aspectRatio = iResolution.x/iResolution.y;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= aspectRatio;

    vec2 mouse = iMouse.xy/iResolution.xy;
    mouse.x *= aspectRatio;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    if(length(uv-mouse) < 0.1) {
        fragColor = vec4(col*0.9, 1.0);
    }
    else {
        // Output to screen
        fragColor = vec4(col,1.0);
    }
}

#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .01

mat2 Rot(float a){
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}

float sdSphere(vec3 p, float r){
    return length(p) - r;
}

float sdPentagon2d( in vec2 p, in float r )
{
    const vec3 k = vec3(0.809016994,0.587785252,0.726542528);
    p.x = abs(p.x);
    p -= 2.0*min(dot(vec2(-k.x,k.y),p),0.0)*vec2(-k.x,k.y);
    p -= 2.0*min(dot(vec2( k.x,k.y),p),0.0)*vec2( k.x,k.y);
    p -= vec2(clamp(p.x,-r*k.z,r*k.z),r);    
    return length(p)*sign(p.y);
}

float GetDist(vec3 p){
    vec2 torRad = vec2(1.3, 1.);
    vec2 cp = vec2(length(p.xz) - torRad.x, p.y);
    float a = atan(p.x, p.z); // polar angle between -pi and pi
    cp *= Rot(a * 3. + iTime * 3.);
    cp.y = abs(cp.y) - .3;

    float d = length(cp) - torRad.y;
    d = sdPentagon2d(cp, .1) - .1;

    float t = 1. * abs(sin(iTime * 3.));
    t = 3.;
    float displacement = sin(t * p.x) * cos(t * p.y) * sin(t * p.z) * .3;
    float disp = sin(t * p.x) * sin(t * p.y) * sin(t * p.z) * sin(t * 5.);

    return min(d + displacement, sdSphere(p, .5) + disp);
    //return sdSphere(p - vec3(0., 0., 0.), .1);
}

float RayMarch(vec3 ro, vec3 rd){
    float dO = 0.;

    for(int i=0; i < MAX_STEPS; i++){
        vec3 p = ro + rd * dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO > MAX_DIST || abs(dS) < SURF_DIST) break;
    }

    return dO;
}
vec3 GetNormal(vec3 p){
    float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p - e.xyy),
        GetDist(p - e.yxy),
        GetDist(p - e.yyx));

    return normalize(n);
}

float GetLight(vec3 p, vec3 lp){
    vec3 l = normalize(lp - p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p + n*SURF_DIST*2., l);

    if(d < length(lp - p)) dif *= .6;

    return dif;
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z){
    vec3 f = normalize(l - p),
        r = normalize(cross(vec3(0, 1, 0), f)),
        u = cross(f, r),
        c = f * z,
        i = c + uv.x * r + uv.y * u,
        d = normalize(i);

     return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;
    vec2 m = iMouse.xy/iResolution.xy;

    vec3 ro = vec3(0, 3, -3);
    ro.yz *= Rot(-m.y*3.14 + 1.);
    ro.xz *= Rot(-m.x*6.2831);

    vec3 rd = GetRayDir(uv, ro, vec3(0, 0, 0), 1.);
    vec3 col = vec3 (0.);

    float d = RayMarch(ro, rd);

    if(d < MAX_DIST){
        vec3 p = ro + rd * d;
        float dif = GetLight(p, vec3(3, 4, -3));
        dif += GetLight(p, vec3(-3, -4, 3));
        col = vec3(.2, .9, .2) * dif;
    }

    col *= pow(col, vec3(.4545));

    fragColor = vec4(col, 1.);
}

