#define MAX_STEPS 100
#define MAX_DIST 100.
#define EPSILON .01
#define PORTAL_TRANSLATE vec3(10, 0, 0)
#define PORTAL_WIDTH 1.

#define NUM_LIGHTS 3
vec3[] lightDirs = vec3[NUM_LIGHTS] (
    vec3(1, .2, -1),
    vec3(-1, .3, -1),
    vec3(0, .1, 1)
);

vec3[] lightCols = vec3[NUM_LIGHTS] (
    vec3(1.2, .7, .4),
    vec3(.8, .8, 1.),
    vec3(.5)
);


float sdSphere(vec3 p, float s) {
    return length(p)-s;
}

float sdBox( vec3 p, vec3 b ) {
    vec3 d = abs(p) - b;
    return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

float intersectPlane(
        vec3 rayOrigin,
        vec3 rayDirection,
        vec3 planeNormal,
        vec3 planeOrigin) {
    float denom = dot(planeNormal, rayDirection);
    vec3 p0l0 = planeOrigin - rayOrigin;
    return dot(p0l0, planeNormal) / denom;
}

float intersectPortal(
        vec3 rayOrigin,
        vec3 rayDirection,
        vec3 planeNormal,
        vec3 planeOrigin,
        float t) {

    if (t < 0.) {
      return 0.;
    }

    vec3 p = rayOrigin + rayDirection * t;
    p = mod(p + PORTAL_TRANSLATE * .5, PORTAL_TRANSLATE) - PORTAL_TRANSLATE * .5;

    if (abs(p.x) < PORTAL_WIDTH && abs(p.y) < 3.) {
      return sign(dot(rayDirection, planeNormal));
    } else {
      return 0.;
    }
}


// Note that the "other world" and the main world have the same geometry,
// otherwise one could use a different GetDist depending on which world they
// were starting in.
float GetDist(vec3 op) {
	vec4 s = vec4(0, 1, 0, 1);

    float d = MAX_DIST;
    { // Spheres
        vec3 p = mod(op + PORTAL_TRANSLATE, PORTAL_TRANSLATE * 2.) - PORTAL_TRANSLATE;
        d = min(d, sdSphere(p + vec3(0, -1, -2.1 + sin(iTime)), 1.));
    }
    { // Cubes
        vec3 p = mod(op, PORTAL_TRANSLATE * 2.) - PORTAL_TRANSLATE;
        d = min(d, sdBox(p + vec3(0, -1, -2.1 + cos(iTime)), vec3(1.)));
    }
    { // Frame
        vec3 p = mod(op + PORTAL_TRANSLATE*.5, PORTAL_TRANSLATE) - PORTAL_TRANSLATE*.5;
        p.x = -abs(p.x);
        d = min(d, sdBox(p + vec3(PORTAL_WIDTH, 0, 0), vec3(.1, 3, .1)));
        d = min(d, sdBox(p + vec3(0, -3, 0), vec3(PORTAL_WIDTH+.1, .1, .1)));
    }

    d = min(d, op.y);
    return d;
}

mat3 RayMarch(vec3 ro, vec3 rd) {
	float dO = 0.;
    float world = 0.;

    for(int i=0; i<MAX_STEPS; i++) {
        // Calculate step.
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);

        // Determine if we passed through a portal.
        float t = intersectPlane(p, rd, vec3(0, 0, 1), vec3(0));
        float portal = intersectPortal(p, rd, vec3(0, 0, 1), vec3(0), t);
        if (portal != 0. && t < dS) {
          ro += PORTAL_TRANSLATE;
          dS = t + EPSILON;
          world += sign(dot(rd, vec3(0, 0, 1)));
        }

        // Determine if we should stop.
        dO += dS;
        if (dO>MAX_DIST) {
            dO = MAX_DIST;
            break;
        } else if (dS<EPSILON) {
            break;
        }
    }

    mat3 result;
    result[0] = vec3(ro + rd * dO);
    result[1] = vec3(dO, 0, 0);
    result[2] = vec3(world, 0, 0);
    return result;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.01, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetSun(vec3 p, vec3 lightAngle, vec3 lightCol, float world) {
    vec3 l = normalize(lightAngle);
    vec3 n = GetNormal(p);

    // Shadow
    float dif = clamp(dot(n, l), 0., 1.);
    mat3 hit = RayMarch(p+n*EPSILON*2., l);
    float d = hit[1].x;
    world += hit[2].x;
    if(d < MAX_DIST) dif = 0.;

    // The other world gets other lighting.
    if (world != 0.) {
      lightCol.xyz = lightCol.zxy;
    }

    return lightCol * dif;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0);

    // Who doesn't like interaction? (even if minimal)
    float mouseX = iMouse.x/iResolution.x;
    float mouseY = iMouse.y/iResolution.x;
    vec3 focus = vec3(0, 1, 0);
    vec3 ro = vec3(sin(mouseX)*-8. + iTime, (-sin(mouseY)+.7)*4., -10.);
    vec3 at = ro - focus;

    // Point camera at origin (overkill for this example)
    vec3 camZ = -normalize(at);
    vec3 camX = cross(vec3(0, 1, 0), camZ);
    vec3 camY = -cross(camX, camZ);

    vec3 rd = normalize(vec3(uv.x, uv.y, 1));
    fragColor = vec4(normalize(uv.x*camX + uv.y*camY + camZ),1.);

    // Distance
    mat3 hit = RayMarch(ro, rd);
    vec3 p = hit[0];
    float d = hit[1].x;
    float world = hit[2].x;

    vec3 lights = vec3(0);
    for (int iLight = 0; iLight < NUM_LIGHTS; iLight++) {
      lights += GetSun(p, lightDirs[iLight], lightCols[iLight], world);
    }
    col = vec3(lights);


    // Sky color
    vec3 sky = vec3(0);
    for (int iLight = 0; iLight < NUM_LIGHTS; iLight++) {
      float amount = clamp(dot(rd, lightDirs[iLight]), 0., 1.);
      sky += amount * lightCols[iLight];
    }

    // Fade off
    col = mix(col, sky, d/MAX_DIST);

    // Gamma correction
    col = pow(col, vec3(.4545));

    fragColor = vec4(col,1.0);
}

#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(h)cos((h)*6.3+vec3(0,23,21))*.5+.5
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    vec3 p,r=iResolution,
    d=normalize(vec3((C-.5*r.xy)/r.y,1));
    float i=0.,g=0.,e,s;
    for(;i++<99.;)
    {
        p=g*d-vec3(.05*sin(iTime*.5),.1,1.8);
        p=R(p,normalize(vec3(1,-2,2)),iTime*.5);
        s=2.;
        vec4 q=vec4(p,sin(iTime*.4)*.5);
		for(int j=0;j++<8;)
        {
            q=abs(q);
            q=q.x<q.y?q.zwxy:q.zwyx;
            s*=e=2.3/clamp(dot(q,q),.2,1.5);
            q=q*e-vec4(1,4,2,1);
        }
        g+=e=min(
                length(q.w)/s,
                length(cross(q.xyw,vec3(.577)))/s-.3e-4
            )+.001;
        O.rgb+=mix(r/r,H(log(s)*2.),.3)*.015*exp(-.03*i*i*e);
    }
    O*=O*O*O;
 }

#define TAU 6.283185307179586

#define MAXSTEPS 8.0
#define CELLSIZE 128.0
#define EDGESIZE 0.05
#define SHRINK   0.93
#define ROTSIZE  (TAU/256.0)
#define ROTSPEED 0.85
#define MOUSEAMP 4.0
#define DRIFTSPEED vec2(1.2, 1.8)

#define hash21(p) fract(sin(dot(p, vec2(12.9898, 78.233))) * 43758.5453)

vec3 hsv2rgb(in vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec2 rotate(in vec2 point, in float rads)
{
    float cs = cos(rads);
    float sn = sin(rads);
    return point * mat2(cs, -sn, sn, cs);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 px = fragCoord.xy - (iResolution.xy / 2.0);
    vec2 position = px / CELLSIZE;
    vec3 color = vec3(1.0);

    vec2 drift = iTime * DRIFTSPEED;
    position += drift;

    vec2 mouse = ((iMouse.xy/iResolution.xy) * 2.0) - 1.0;
    vec2 smouse = vec2(0.0);
    if (mouse.x > -1.0) {
        smouse = mouse * (1.0/SHRINK) * MOUSEAMP;
    }

    vec2 rpos = position;
    for (float i = 0.0; i < 1.0; i += (1.0/MAXSTEPS)) {
        vec2 cellnum = floor(rpos);
        vec2 cellpos = fract(rpos);
        //cellpos = abs((cellpos * 2.0) - 1.0);

        float cellhash = hash21(cellnum);

        if ((min(cellpos.x, cellpos.y) < EDGESIZE) ||
            (max(cellpos.x, cellpos.y) > (1.0 - EDGESIZE))) {
            color = hsv2rgb(vec3(cellhash, (1.0-i) * 0.78, 0.95));
            break;
        }

        rpos -= drift;
        rpos -= smouse;
        rpos /= SHRINK;
        rpos = rotate(rpos, ROTSIZE * sin( iTime * ROTSPEED ));  
        rpos += smouse;
        rpos += drift;
    }

    fragColor = vec4(color, 1.0);
}

