#define PI acos(-1.)
#define TAU (2.*PI)
#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))

#define od(p,d) (dot(p,normalize(sign(p)))-d)
#define hash21(x) fract(sin(dot(x,vec2(13.4,32.7)))*134.5) 

vec2 edge (vec2 p)
{
    vec2 p2 = abs(p);
    return (p2.x>p2.y) ? vec2((p.x<0.) ? -1. : 1., 0.) : vec2(0., (p.y<0.) ? -1. : 1.);
}

float box (vec3 p, vec3 c)
{
    vec3 q = abs(p)-c;
    return min(0.,max(q.x,max(q.y,q.z)))+length(max(q,0.));
}

float sc (vec3 p, float d)
{
    p = max(abs(p),abs(p.yzx));
    return min(p.x, min(p.y,p.z))-d;
}

float g1=0.;
float prim1 (vec3 p, float size) 
{
    float odile = od(p,size*.75);
    g1 += 0.001/(0.001+odile*odile);
    return min(odile,
                max(-sc(p,size*.75),
                     box(p,vec3(size))
                   )
               );
}

vec2 center;
float SDF (vec3 p)
{
    p.yz *= rot(-atan(1./sqrt(2.)));
    p.xz *= rot(PI/4.);

    vec2 id = floor(p.yz)+.5;
    p.y += (mod(id.y,2.)<=0.5) ? iTime*0.2 : -iTime*0.2;
    center = floor(p.yz)+.5; 
    vec2 neighbour = center+edge(p.yz-center);    

    float size = clamp(hash21(center+0.5)/2.,0.1,0.36),
    me = prim1(p-vec3(0.,center),size),
    next = box(p-vec3(0.,neighbour),vec3(0.45));

    return min(me,next);
}


vec3 getnorm (vec3 p)
{
    vec2 eps = vec2(.001,0.);
    return normalize(SDF(p)-vec3(SDF(p-eps.xyy),SDF(p-eps.yxy),SDF(p-eps.yyx)));
}

float AO (float eps, vec3 p, vec3 n)
{return clamp(SDF(p+eps*n)/eps,0.,1.);}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
    float dither = hash21(uv);

    vec3 ro = vec3(uv*3.,-30.), rd=vec3(0.,0.,1.), p=ro,
    col = vec3(0.), l = normalize(vec3(1.5,-2.,-2.));

    bool hit = false;
    for (float i=0.;i<100.;i++)
    {
        float d = SDF(p);
        if (d<0.001)
        {hit = true; break;}
        d *= 0.95+dither*0.1;
        p += d*rd;
    }

    if (hit)
    {
        vec3 n = getnorm(p);
        float light = max(dot(n,l),0.), ao = AO(0.1,p,n)+AO(0.2,p,n)+AO(0.38,p,n);
        col = vec3(light)*ao/3.;
    }

    col += g1*0.2*vec3(0.45,.8,hash21(center));

    fragColor = vec4(sqrt(clamp(col,0.,1.)),1.0);
}

struct Ray {
    vec3 origin;
    vec3 direction;
};

struct Light {
    vec3 position;
    float strongth;
    vec3 color;
};

#define PI 3.1415926

mat4 euler(float x, float y, float z) {
    mat4 xmat = mat4(vec4(1.0,  0.0,    0.0,    0.0),
                     vec4(0.0,  cos(x), sin(x), 0.0),
                     vec4(0.0, -sin(x), cos(x), 0.0),
                     vec4(0.0,  0.0,    0.0,    1.0));
    mat4 ymat = mat4(vec4( cos(y), 0.0, sin(y), 0.0),
                     vec4( 0.0,    1.0, 0.0,    0.0),
                     vec4(-sin(y), 0.0, cos(y), 0.0),
                     vec4( 0.0,    0.0, 0.0,    1.0));
    mat4 zmat = mat4(vec4( cos(z),  sin(z), 0.0, 0.0),
                     vec4(-sin(z),  cos(z), 0.0, 0.0),
                     vec4( 0.0,     0.0,    1.0, 0.0),
                     vec4( 0.0,     0.0,    0.0, 1.0));

    return xmat*ymat*zmat;
}

mat4 transform(float x, float y, float z) {
    return mat4(vec4(1.0, 0.0, 0.0, 0.0),
                vec4(0.0, 1.0, 0.0, 0.0),
                vec4(0.0, 0.0, 1.0, 0.0),
                vec4(x,   y,   z,   1.0));
}

float sphereSDF(vec3 center, float radius, vec3 point) {
    return length(point - center) - radius;
}

float planeSDF(vec3 origin, vec3 normal, vec3 point) {
    return dot(point - origin, normal);
}

vec4 stereographicProj(vec3 point) {
    vec4 p4 = vec4(point, 2.0);
    float norm = length(p4);
    return 4.0*p4/(norm*norm) - vec4(vec3(0.0),1.0);
}

float torus4SDF(vec4 point, vec4 pl1, vec4 pl2, float radius) {
    float dp1 = dot(point,pl1);
    float dp2 = dot(point,pl2);
    vec4 pp = pl1*dp1 + pl2*dp2;
    vec4 ppp = point - pp;
    vec4 cp = pp/length(pp);
    vec4 ppmcp = pp-cp;
    return sqrt(dot(ppmcp,ppmcp) + dot(ppp,ppp)) - radius;
}

float sceneSDF(vec3 point) {
    float theta = iTime;//PI/2.0*sin(iTime)*sin(iTime);
    return torus4SDF(stereographicProj(point), 
                    vec4(0.0,1.0,0.0,0.0),
                    vec4(cos(theta),0.0,0.0,sin(theta)),
                    0.8);
    /*return min(
        min(
            min(
                sphereSDF(vec3(-0.7, 0.7, 0.0), 0.5, point),
                sphereSDF(vec3(0.7, 0.7, 0.0), 0.5, point)
            ),
            sphereSDF(vec3(0.0), 1.0, point)
        ),
        planeSDF(vec3(0.0), vec3(0.0, 1.0, 0.0), point)
      );*/
}

vec3 sceneSDFGradient(vec3 point, float epsilon) {
    vec3 xe = vec3(epsilon, 0.0, 0.0)/2.0;
    vec3 ye = vec3(0.0, epsilon, 0.0)/2.0;
    vec3 ze = vec3(0.0, 0.0, epsilon)/2.0;

    return vec3(
        (sceneSDF(point + xe) - sceneSDF(point - xe)) / epsilon,
        (sceneSDF(point + ye) - sceneSDF(point - ye)) / epsilon,
        (sceneSDF(point + ze) - sceneSDF(point - ze)) / epsilon
      );
}

vec3 sceneSDFNormal(vec3 point) {
    return normalize(sceneSDFGradient(point, 0.01));
}

vec3 rayPoint(Ray ray, float dist) {
    return ray.origin + dist * ray.direction;
}

vec3 screen(vec3 a, vec3 b) {
    return vec3(1.0) - (vec3(1.0) - a)*(vec3(1.0) - b);
}

vec3 lightPoint(Light light, vec3 point, vec3 normal, vec3 camera, vec3 diffuse, vec3 bounce, vec3 current) {
    vec3 lightchord = light.position - point;

    vec3 lightcolor = light.color * 1.0 / pow(length(lightchord)/light.strongth+1.0, 2.0);

    vec3 colour = diffuse * lightcolor * max(dot(normal, normalize(lightchord)), 0.0);
    colour = screen(colour, bounce * lightcolor * max(vec3(1.0) - 5.0*(vec3(1.0) - dot(normalize(lightchord), reflect(normalize(point - camera), normal))), 0.0));

    return screen(current, colour);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float lightangle = iTime;

    Light light1 = Light(vec3(20.0*cos(lightangle), 5.0, 20.0*sin(lightangle)), 1000.0, vec3(1.0, 0.0, 0.0));

    lightangle += PI*2./3.;

    Light light2 = Light(vec3(20.0*cos(lightangle), 5.0, 20.0*sin(lightangle)), 1000.0, vec3(0.0, 1.0, 0.0));

    lightangle += PI*2./3.;

    Light light3 = Light(vec3(20.0*cos(lightangle), 5.0, 20.0*sin(lightangle)), 1000.0, vec3(0.0, 0.0, 1.0));

    float disttoscreen = 0.5;

    vec2 uv = fragCoord/iResolution.xy - vec2(0.5);
    uv.y *= iResolution.y/iResolution.x;

    vec3 camorigin = vec3(5.0, 2.0*cos(iTime*2.0+PI) + 6.0, -7.0 + 3.0*cos(iTime*2.0+PI));

    mat4 camtoscene = transform(camorigin.x, camorigin.y, camorigin.z)*euler(0.7 + 0.4*cos(iTime*2.0+PI), 0.5, 0.0);

    Ray ray = Ray((camtoscene*vec4(vec3(0.0),1.0)).xyz,
                  normalize(camtoscene*vec4(uv.x, uv.y, disttoscreen, 0.0)).xyz);

    vec3 point = camorigin;

    float scenedist = sceneSDF(point);
    bool invert = scenedist < 0.0;
    if (invert) scenedist = -scenedist;
    float raydist = 0.0;

    float epsilon = 0.01;
    float end = 100.0;

    while (scenedist > epsilon) {
        if (raydist > end) {
            fragColor = vec4(0.0, 0.0, 0.0, 1.0);
            return;
        }

        point = rayPoint(ray, raydist);

        scenedist = invert ? -sceneSDF(point) : sceneSDF(point);

        raydist += scenedist;
    }

    vec3 normal = sceneSDFNormal(point);
    vec3 diffuse = vec3(1.0);
    vec3 bounce = vec3(1.0);

    vec3 colour = lightPoint(light1, point, normal, camorigin, diffuse, bounce, vec3(0.3));
    colour = lightPoint(light2, point, normal, camorigin, diffuse, bounce, colour);
    colour = lightPoint(light3, point, normal, camorigin, diffuse, bounce, colour);

    // Output to screen
    fragColor = vec4(colour,1.0);
}

//A tiling of the hyperbolic plane by ideal quadrilaterals determines a punctured torus
//by taking each pair of opposite sides, and gluing them with the hyperbolic isometry
// which identifies the closest pair of points on each to one another.
//To draw this tiling, instead of translating by these isometries,
//I generate it by reflecting in the sides




//------------------------------------------
//Define hyperbolic half spaces, reflections
//------------------------------------------

//vertical line to reflect in
//side=+1 means the half space contains points to the right, -1 to the left
struct Line{
float pos;
float side;
};


//semicircle centered on real line to reflect in
//side =+1 means the halfspace contains points below, -1 means above
struct Circle{
float center;
float radius;
float side;
};



//reflection in vertical line defining a half space
//this is done by conjugating reflection in the line x=0
//by the translation taking this line there
void invert(inout vec2 p, Line line){

    p.x -= line.pos;

    p.x *= -1.;

    p.x += line.pos;

}



//reflection in a circle defining a half space
//this is done by conjugating inversion in the unit circle
//with a similarity transformation taking the given circle to the unit circle
void invert(inout vec2 p, Circle circle){

    p.x -= circle.center;
    p /= circle.radius;

    p /= dot(p,p);

    p *= circle.radius;
    p.x += circle.center;

}

//check if inside a half space bounded by a line:
bool inside(vec2 p, Line line){
    if(line.side>0.){//check if we are on the right side
        return p.x>line.pos;
    }
    else{//check if we are on the left side
        return p.x<line.pos;
    }
}

//check if inside a half space bounded by a circle
bool inside(vec2 p, Circle circle){
    if(circle.side>0.){//check if we are inside the semicircle
        return length(p-vec2(circle.center,0))<circle.radius;
    }
    else{//check if we are outside the semicircle
        return length(p-vec2(circle.center,0))>circle.radius;
    }
}




//------------------------------------------
//set up the fundamental domain
//------------------------------------------


//Give names to the sides of the shape
Line side0;
Line side1;
Circle side2;
Circle side3;


//function that defines these sides in terms of two deformation parameters a,b
//this generates a quadrilateral with vertices at infinity, -a, 0 and b.
void setUpFD(float a, float b){

side0=Line(-1.*a,1.);
side1=Line(b,-1.);
side2=Circle(-a/2.,a/2.,-1.);
side3=Circle(b/2.,b/2.,-1.);

}



//------------------------------------------
//reflect points into fundamental domain
//------------------------------------------


//this function takes in a point p, and looks at each side of the fundamental domain
//if p is not within the half space defined by that side, we reflect it
//if p is already in that half space, we leave it alone
void reflectInFD(inout vec2 p,inout float invCount){


        if(!inside(p,side0)){
            invert(p,side0);
            invCount+=1.;
        }

        if(!inside(p,side1)){
            invert(p,side1);
            invCount+=1.;
        }

        if(!inside(p,side2)){
            invert(p,side2);
            invCount+=1.;
        }

        if(!inside(p,side3)){
            invert(p,side3);
            invCount+=1.;
        }

}



//this function checks if p is inside our fundamental domain or not
bool insideFD(vec2 p){
  return inside(p,side0)&&inside(p,side1)&&inside(p,side2)&&inside(p,side3);
}




//move into the fundamental domain
//this combines the two functions above: it iteratively reflects in the sides,
//then checks if you ended up inside the FD
//if you do, it stops.
void moveToFD(inout vec2 p,inout float invCount){


    for(int i=0;i<20;i++){

        reflectInFD(p,invCount);

    if(insideFD(p)){
            break;
         }

    }
}





