void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    //set up the input
    vec2 uv = normalizedFragCoord(fragCoord);

    //if you are outside the poincare disk, make black
    if(length(uv)>1.){
        fragColor=vec4(0,0,0,1);
        return;
      }

    //map to upper half plane for computation:
    vec2 p = toUHP(uv);

    //setup the fundamental domain we are working with
    setUpFD(osc(0.01,1.,iTime/3.),osc(0.5,2.5,iTime/1.));

    //reflect into the fundamental domain
    float invCount=0.;
    moveToFD(p,invCount);

    // color the pixel based on this
    //right now this is a very boring coloring: 
    //we just count if theres an even or odd number of reflections!
    float evenOdd=mod(invCount,2.);
    vec3 col=vec3(0.3,0.6-evenOdd,evenOdd);

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define TAU (6.283185307)
#define HEX(x) (vec3((x >> 16) & 255, (x >> 8) & 255, x & 255) / 255.)

float ease(float x) {
    x = clamp(x, 0., 1.);
    return smoothstep(0., 1., x);
}

float ripple(vec2 uv, vec2 lightDir, float scale, float time) {

    return sin(
        length(uv) * scale - time * TAU
    ) * dot(
        lightDir,
        normalize(uv) *
        ease(length(uv) * scale * 0.5)
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Make sure this loops
    float time = fract(iTime / 10.0);
    // Normalized pixel coordinates
    vec2 uv = ( 2.* fragCoord - iResolution.xy ) / length(iResolution.xy);
    vec2 lightDir = vec2(sin(time * TAU), cos(time * TAU));

    vec2 center0 = 0.5*vec2(sin(TAU*time), cos(TAU*time));
    vec2 center1 = 0.5*vec2(sin(TAU*(time+1./3.)), cos(TAU*(time+1./3.)));
    vec2 center2 = 0.5*vec2(sin(TAU*(time-1./3.)), cos(TAU*(time-1./3.)));

    const float sizeScale = 30.;
    const float timeScale = 10.;

    vec3 col = HEX(0x4080c0) + 0.15 * (
        ripple(
            uv - center0, lightDir,
            sizeScale, 0.2 + time * timeScale
        ) + ripple(
            uv - center1, lightDir,
            sizeScale, 0.4 + time * timeScale
        ) + ripple(
            uv - center2, lightDir,
            sizeScale, 0.8 + time * timeScale
        )
    );

    // Output to screen
    fragColor = vec4(
        col, 1.0
    );
}

const vec3 eye = vec3(0,0,10);
const vec3 light = vec3(0.0,5.0,10.0);
const int maxSteps = 100;
const float eps = 0.01;


float smin (float a, float b, float k)
{
    float res = exp(-k*a)+exp(-k*b);
    return -log(res)/k;
}

float dCone ( vec3 p, vec2 c )
{
    float q = length( p.yz );
    return dot ( c, vec2 ( q, p.x ) );
}

float dCyl ( vec3 p, vec3 c )
{
return length ( p.xz - c.xy ) - c.z+0.03* cos(5.0*p.y + iTime)+0.02 * sin(20.0*p.z + iTime) ;
}

float dSphere (vec3 p, vec3 c)
{
    return length(p - c) - 1.0 +0.03 * sin(20.0*p.y + iTime);
}

float dEllipsoid( vec3 p, vec3 r )
{
  float k0 = length(p/r);
  float k1 = length(p/(r*r));
  return k0*(k0-1.0)/k1;
}

float length8 (in vec2 p)
{
    return pow(pow(p.x,8.0) + pow (p.y,8.0),1.0/8.0);
}


float length8 ( in vec3 p )
{
    return pow ( pow ( p.x, 8.0 ) + pow ( p.y, 8.0 ) + pow ( p.z, 8.0 ), 1.0/ 8.0 );
}


float dTorus (vec3 p, vec3 t)
{
    vec2  q=vec2(length8 (p.xz) - t.x,p.y);
    return length8(q) - t.y;
}

mat3 rotateX(float theta)
{
    float c = cos(theta);
    float s = sin(theta);
    return mat3(vec3(1,0,0),
            vec3(0,c,-s),
            vec3(0,s,c));
}

mat3 rotateZ(float theta)
{
    float c = cos(theta);
    float s = sin(theta);
    return mat3(vec3(c,-s,0),
            vec3(s,c,0),
            vec3(0,0,1));
}

mat3 rotateY(float theta)
{
    float c = cos(theta);
    float s = sin(theta);
    return mat3(vec3(c,0,s),
            vec3(0,1,0),
            vec3(-s,0,c));
}

float sdf (in vec3 p,in mat3 m)
{
    vec3 q = m*p;
    vec3 o = q*rotateX(90.0);

    return smin(smin(dEllipsoid(q,vec3(1,5,1)),dCyl(rotateX(-90.0)*q,vec3(0,0,1)),7.0),smin(dSphere(q,vec3(0,5,0)),dSphere(q,vec3(0,-5,0)),8.0),9.0);
}  
vec3 generateNormal (vec3 z, float d,in mat3 m)
{
    float e  = max(d * 0.5,eps);
    float dx1 = sdf(z+ vec3(e,0,0),m);
    float dx2 = sdf(z- vec3(e,0,0),m);
    float dy1 = sdf(z+ vec3(0,e,0),m);
    float dy2 = sdf(z- vec3(0,e,0),m);
    float dz1 = sdf(z+ vec3(0,0,e),m);
    float dz2 = sdf(z- vec3(0,0,e),m);

    return normalize (vec3 (dx1 - dx2,dy1-dy2,dz1-dz2));
}



vec3 trace(in vec3 from, in vec3 dir, out bool hit, in mat3 m)
{
    vec3 p = from;
    float totalDist = 0.0;

    hit =false;

    for (int steps = 0; steps < maxSteps; steps++)
    {
        float dist = sdf(p,m);

        if(dist < 0.001)
        {
            hit = true;
        }
        totalDist+=dist;

        if(totalDist > 20.0)
            break;
           p+=dist*dir;
    }

    return p;
}
