bool quadratic(float a, float b, float c, out float t0, out float t1)
{
    float disc = b * b - 4. * a * c;

    if (disc < 0.)
    {
        return false;
    } 

    if (disc == 0.)
    {
        t0 = t1 = -b / (2. * a);
        return true;
    }

    t0 = (-b + sqrt(disc)) / (2. * a);
    t1 = (-b - sqrt(disc)) / (2. * a);
    return true;    
}

Material getMaterial(int type, int index)
{
    if (type == SPHERE)
    {
        return spheres[index].material;
    }

    if (type == PLANE)
    {
        return planes[index].material;
    }
}

bool intersectSphere(
    vec3 origin, 
    vec3 direction, 
    Sphere sphere, 
    out float dist, 
    out vec3 surfaceNormal, 
    out vec3 Hit)
{
    vec3 L = origin - sphere.position;

    float a = dot(direction, direction);
    float b = 2. * dot(direction, L);
    float c = dot(L, L) - pow(sphere.radius, 2.);

    float t0;
    float t1;

    if (quadratic(a, b, c, t0, t1))
    {        
        if (t0 > t1) 
        {
        	float temp = t0;
            t0 = t1;
            t1 = temp;
        } 

        if (t0 < 0.)
        { 
            t0 = t1; 
            if (t0 < 0.) return false; 
        }  

        dist = t0;

        Hit = origin + dist * direction;
        surfaceNormal = normalize(Hit - sphere.position);               

        return true;
    }  

    return false;
}

bool intersectPlane(in Plane plane, in vec3 origin, in vec3 rayDirection, out float t, out vec3 hit) 
{    
    float denom = dot(plane.normal, rayDirection); 
    if (denom < 1e-6) 
    { 
        vec3 p0l0 = plane.position - origin; 
        t = dot(p0l0, plane.normal) / denom; 

        if (t >= 0.)
        {
            hit = origin + rayDirection * t;
            return true;
        }             
    } 

    return false; 
} 

vec3 getLitColor(in vec3 viewDir, in vec3 surfacePointPosition, in vec3 objectColor, in Point_Light Point_Light, in vec3 surfaceNormal, in Material material)
{
    vec3 lightVector = surfacePointPosition - Point_Light.position;
    vec3 lightDir = normalize(lightVector);   

   	float lightIntensity = (pow(0.1, 2.) / pow(length(lightVector), 2.)) * Point_Light.intensity;

    float coeff = -dot(lightDir, surfaceNormal);     

    vec3 ambient = material.ambient * objectColor;

    vec3 diffuse = material.diffuse * max(coeff, 0.) * objectColor * lightIntensity;

    vec3 halfwayDir = normalize(lightDir + viewDir);  
    vec3 specular = pow(max(-dot(surfaceNormal, halfwayDir), 0.0), material.shininess) * material.specular * objectColor * lightIntensity;

    vec3 color = ambient + diffuse + specular;

    return color;
}

void shadow(vec3 hit, inout vec3 finalColor, float ambient, int type, int index)
{
    vec3 shadowSurfaceNormal;
    vec3 shadowRay = lights[0].position - hit;
    vec3 shadowRayDirection = normalize(shadowRay);
    float distanceToLight = sqrt(dot(shadowRay, shadowRay));
    vec3 shadowHit;

    float dist; 

    for(int i = 0; i < 2; ++i)
	{
        if (type == SPHERE && index == i)
        {
            continue;  
        }

        if (intersectSphere(hit, shadowRay, spheres[i], dist, shadowSurfaceNormal, shadowHit))
        {
            if (dist > 0. && distanceToLight > dist)
            {
            	finalColor *= 2. * ambient;
            }
        }
    }

    for(int i = 0; i < PLANES_COUNT; ++i)
	{
 		if (type == PLANE && index == i)
        {
            continue;
        }

        if (intersectPlane(planes[i], hit, shadowRay, dist, shadowHit))
        {    
            if (dist < distanceToLight)
            {                
 				finalColor *= 2. * ambient;        
            }
        }
    }     
}

vec3 rayTrace(in vec3 rayDirection, in vec3 rayOrigin)
{
    vec3 finalColor = vec3(0.);

    int BOUNCES = 2;

    int prevType = -1;
    int prevIndex = -1;  

	vec3 hit = rayOrigin; 
    vec3 passHit;

    for (int bounce = 0; bounce < BOUNCES; bounce++)
    {    
        float dist = 1. / 0.;
        float objectHitDistance = dist;

        vec3 surfaceNormal;

        int type = -1;
    	int index = -1;   

        vec3 passColor = vec3(0.);

        for (int i = 0; i < SPHERES_COUNT; ++i)
        {          
            if (prevType == SPHERE && prevIndex == i)
            {
                continue;
            }

            if (intersectSphere(rayOrigin, rayDirection, spheres[i], objectHitDistance, surfaceNormal, hit))
            {                
                if (objectHitDistance < dist)
                {
                    dist = objectHitDistance;
                    passColor = getLitColor(rayDirection, hit, spheres[i].color, lights[0], surfaceNormal, spheres[i].material);
                    shadow(hit, passColor, spheres[i].material.ambient, SPHERE, i);

                    type = SPHERE;
                    index = i;
                    passHit = hit;
                }
            }
        }

        for(int i = 0; i < PLANES_COUNT; ++i)
        {
            if (prevType == PLANE && prevIndex == i)
            {
                continue;
            }

            if (intersectPlane(planes[i], rayOrigin, rayDirection, objectHitDistance, hit))
            {
                if (objectHitDistance <= dist)
                {    
					dist = objectHitDistance;
                    passColor = getLitColor(rayDirection, hit, planes[i].color, lights[0], planes[i].normal, planes[i].material);        

                    surfaceNormal = planes[i].normal;

                    shadow(hit, passColor, planes[i].material.ambient, PLANE, i);

                	type = PLANE;
                    index = i;                    
                    passHit = hit;                    
                }
            }
        } 

        if (bounce == 0)
        {
            finalColor += passColor;
        }
        else
        {
        	finalColor += getMaterial(type, index).specular * passColor;
        }

        if (type < 0) break;

        rayOrigin = passHit;
        rayDirection = reflect(rayDirection, surfaceNormal);

        prevType = type;
        prevIndex = index;
    }

    return finalColor / float(BOUNCES);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    setupScene();
    vec2 uv = fragCoord/iResolution.xy - 0.5;
    uv.x *= (iResolution.x / iResolution.y); 

    vec3 clipPlanePosition = vec3(uv.x, uv.y, camera.position.z + camera.cameraDistance);
    vec3 rayDirection = normalize(clipPlanePosition - camera.position);

    vec3 finalColor = rayTrace(rayDirection, camera.position);
    fragColor = vec4(finalColor, 1.);
}

const float pi = 3.1415926535897932384626433832795f;

float make_arrow(
    in vec2 uv, 
    in float tailW, in float tailL, 
    in float headW, in float headL, 
    in float bottom, in float left
) {
    if(uv.y > bottom && uv.y < bottom + tailL) {
        float hTOff = (headW - tailW) / 2.f;
        return float(uv.x > left + hTOff && uv.x < left + tailW + hTOff);
    } else if(uv.y >= bottom + tailL && uv.y < bottom + tailL + headL) {
        float slope = headL / headW * 2.f;
        if(uv.x > left && uv.x < left + headW / 2.f) {
            return float(slope * (uv.x - left) > uv.y - bottom - tailL);
        } else if(uv.x >= left + headW / 2.f && uv.x < left + headW) {
            return float(slope * (uv.x - left - headW / 2.f) < bottom + tailL + headL - uv.y);
        }
    }
    return 0.f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float aspect = iResolution.x / iResolution.y;
    float angle = pi / 6.f * iTime; //varying angle with time, 1 degree = pi/180 radian
    uv -= 0.5f; // move arrow to centre
    uv = vec2(uv.x * aspect * cos(angle) - uv.y * sin(angle), uv.x * aspect * sin(angle) + uv.y * cos(angle)); //rotate
    float mask = make_arrow(uv, 0.01f, 0.4f, 0.05f, 0.1f, 0.f, 0.f); // generate arrow at origin
    vec3 bgcol = vec3(0);
    vec3 fgcol = vec3(0.75);

    // Output to screen
    fragColor = vec4(mask * fgcol + bgcol,1.0);
}

