vec2 castRay(vec3 ro, vec3 rd, int octaves) {
	const float p = 0.001;
	float t = 0.0;
	float h = p * 2.0;
	float m = -1.0;
	for (int i=0; i<36; i++) {
		if (abs(h)<=p || t>=28.0) break;
        t += h;
        vec2 res = map(ro + rd*t, octaves);
        h = res.x;
        m = res.y;
	}
	if (t>28.0) m = -1.0;
	return vec2(t, m);
}

vec3 calcNormal(vec3 p, int octaves) {
	const vec3 eps = vec3(0.002, 0.0, 0.0);
	return normalize( vec3(map(p+eps.xyy, octaves).x - map(p-eps.xyy, octaves).x,
			       map(p+eps.yxy, octaves).x - map(p-eps.yxy, octaves).x,
			       map(p+eps.yyx, octaves).x - map(p-eps.yyx, octaves).x) );
}

float shadows(vec3 ro, vec3 rd, float tMax, float k, int octaves) {
    float res = 1.0;
	float t = 0.1;
	for(int i=0; i<22; i++) {
        if (t>=tMax) break;
        float h = map(ro + rd*t, octaves).x;
        res = min( res, k*h/t );
        t += h;
    }
    return clamp(res, 0.2, 1.0);
}

vec3 render(vec3 ro, vec3 rd) {
	const int geoLOD = 4;

	vec2 res = castRay(ro, rd, geoLOD);

	vec3 lPos = normalize(vec3(1.0, 0.5, 0.0));
	vec3 lCol = vec3(1.0, 0.9, 0.8);

	vec3 pos = ro + rd*res.x;

	// mat -1 = Background / sky
    vec3 color = vec3(0.45,0.5,0.6);
    float sun = clamp(dot(rd,lPos),0.0,1.0);
    color += 0.6 * lCol * sun*sun;
    if (res.y < -0.5) {
		return color;
	}

    vec3 skyColor = color;

	int norLOD = int(max(2.0, 12.0-11.0*res.x/28.0));
	vec3 nor = calcNormal(pos, norLOD);

	// mat 0 = Rock / mountain
	if (res.y>-0.5 && res.y<0.5) {
		// Base rock
		color = mix( vec3(0.4, 0.1, 0.0), vec3(0.7, 0.6, 0.3), step(0.9, nor.y) );

		// Layer noise
		float n = 0.5*(snoise(pos.xy*vec2(2.0, 15.0))+1.0);
		color = mix( vec3(0.6, 0.5, 0.4), color, n*smoothstep(0.0, 0.7, 1.0-nor.y) );

        // Sand on top
        color = mix(color, vec3(0.7, 0.6, 0.3), smoothstep(0.0, 0.2, nor.y-0.8));
	}
	// mat 1 = Sand
	if (res.y>0.5) {
		// Base sand and rock color
		color = mix( vec3(0.3, 0.2, 0.0), vec3(0.7, 0.6, 0.3), nor.y );
	}

    // Lighting and shadows
    float lAmb = clamp( 0.5 + 0.5 * nor.y, 0.0, 1.0);
    float lDif = clamp( dot( nor, lPos ), 0.0, 2.0);

    if (lDif>0.05) lDif *= shadows(pos, lPos, 8.0, 12.0, geoLOD);

    color += (0.4*lAmb) * lCol;
    color *= (1.8*lDif) * lCol;	

	// Fog
	float fog = exp(-0.003 *res.x*res.x);
	color = mix(skyColor, color, fog);

	return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 pos = 2.0 * ( fragCoord.xy / iResolution.xy ) - 1.0;
	pos.x *= iResolution.x / iResolution.y;
    float t1 = iTime;
	// Camera
	float x = 0.0 + (0.5*t1);
	float y = 0.0;
	float z = 0.0 + sin(0.1*t1);
	vec3 cPos = vec3(x, y, z);
	cPos.y = terrain(cPos.xz, 1) + 2.5;

	const vec3 cUp = vec3(0., 1., 0.);
	vec3 cLook = vec3(cPos.x + 1.0, cPos.y*0.85, 0.0);

	// Camera matrix
	vec3 ww = normalize( cLook-cPos );
	vec3 uu = normalize( cross(ww, cUp) );
	vec3 vv = normalize( cross(uu, ww) );

	vec3 rd = normalize( pos.x*uu + pos.y*vv + 2.0*ww );

	// Render
	vec3 color = render(cPos, rd);

	fragColor = vec4( color, 1.0 );
}

#define S(a, b, t) smoothstep(a, b, t)

float noise(float t)
{
    return fract(sin(t*3456.)*6547.);
}

vec4 noise14(float t)
{
    return fract(sin(t*vec4(123., 1024., 3567., 8745.))*vec4(4568., 345, 9821., 8632.));
}

struct ray
{
    vec3 origin, direction;
};

ray getRay(vec2 uv, vec3 camPos, vec3 lookAt, float zoom)
{
    ray a;
    a.origin = camPos;

    // Basis vectors for the camera.
    vec3 forward = normalize(lookAt - camPos);
    vec3 right = cross(vec3(0., 1., 0.), forward);
    vec3 up = cross(forward, right);
    vec3 centerScreen = a.origin + forward*zoom;
    vec3 intersectionPoint = centerScreen + uv.x*right + uv.y*up;

    a.direction = normalize(intersectionPoint - a.origin);

    return a;
}

vec3 closestPoint(ray r, vec3 point)
{
    return r.origin + max(0., dot(point - r.origin, r.direction))*r.direction;
}

float distToRay(ray r, vec3 point)
{
    return length(point - closestPoint(r, point));
}

float bokeh(ray r, vec3 point, float size, float blur)
{
    float dist = distToRay(r, point);
    size *= length(point);
    float s = S(size, size*(1.-blur), dist);
    s *= mix(.6, 1., S(size*.8, size, dist));
    return s;
}

vec3 streetLights(ray r, float time)
{
    float side = step(r.direction.x, 0.);
    r.direction.x = abs(r.direction.x);

    float s = 1./10.; // 0.1
    float mask = 0.;
    for(float i = 0.; i < 1.; i += s)
    {
        float ti = fract(time + i + side*s*.5);
        vec3 point = vec3(2., 2., 100. -ti*100.);

    	mask += bokeh(r, point, .05, .1)*ti*ti*ti;
    }

    return vec3(1., .5, 0.)*mask;
}

vec3 envLights(ray r, float time)
{
    float side = step(r.direction.x, 0.);
    r.direction.x = abs(r.direction.x);

    float s = 1./10.; // 0.1
    vec3 col_1 = vec3(0.);
    for(float i = 0.; i < 1.; i += s)
    {
        float ti = fract(time + i + side*s*.5);

        vec4 noise = noise14(i+side*100.);
        float fade = ti*ti*ti;

        float occlusion = sin(ti*6.28*10.*noise.x)*.5+.5;

        fade = occlusion; 
        float x = mix(2.5, 10., noise.x);
        float y = mix(.1, 1.5, noise.y);
        vec3 point = vec3(x, y, 50. -ti*50.);

        vec3 col_2 = noise.wzy;

    	col_1 += bokeh(r, point, .05, .1)*fade*col_2*.5;
    }

    return col_1;
}

vec3 headLights(ray r, float time)
{
    time *= 2.;
    float w1 = .25;
    float w2 = w1*1.2;
    float s = 1./30.; // 0.1
    float mask = 0.;

    for(float i = 0.; i < 1.; i += s)
    {
        float noise = noise(i);

        if(noise > .1)
        {
            continue;
        }

        float ti = fract(time + i);
        float z = 100. -ti*100.;
        float fade = ti*ti*ti*ti*ti;
        float focus = S(.8, 1., ti);

        float size = mix(.05, .03, focus);
    	mask += bokeh(r, vec3(-1.-w1, .15, z), size, .1)*fade;
        mask += bokeh(r, vec3(-1.+w1, .15, z), size, .1)*fade;

        mask += bokeh(r, vec3(-1.-w2, .15, z), size, .1)*fade;
        mask += bokeh(r, vec3(-1.+w2, .15, z), size, .1)*fade;

        float reflection = 0.;
        reflection += bokeh(r, vec3(-1.-w2, -.15, z), size*3., 1.)*fade;
        reflection += bokeh(r, vec3(-1.+w2, -.15, z), size*3., 1.)*fade;

        mask += reflection*focus;

    }

    return vec3(.9, .9, 1.)*mask;
}

vec3 tailLights(ray r, float time)
{
    time *= .25;
    float w1 = .25;
    float w2 = w1*1.2;
    float s = 1./15.;
    float mask = 0.;

    for(float i = 0.; i < 1.; i += s)
    {
        float noise = noise(i);		// At this point noise is between 0 - 1.

        if(noise > .5)
        {
            continue;
        }

        // If we get this far we know that noise value is between 0 - 0.5

        // This says if noise is < 0.25 then lane = 0
        // If noise is > 0.25 then lane = 1
        float lane = step(.25, noise);

        float ti = fract(time + i);
        float z = 100. -ti*100.;
        float fade = ti*ti*ti*ti*ti;
        float focus = S(.9, 1., ti);

        float size = mix(.05, .03, focus);
        float laneShift = S(1., .96, ti);
        float x = 1.5 - lane * laneShift;
        float indicator = step(0., sin(time*500.))*7.*lane*step(.96, ti);

        // Left bokeh
    	mask += bokeh(r, vec3(x-w1, .15, z), size, .1)*fade;
        mask += bokeh(r, vec3(x+w1, .15, z), size, .1)*fade;

        // Right bokeh
        mask += bokeh(r, vec3(x-w2, .15, z), size, .1)*fade;
        mask += bokeh(r, vec3(x+w2, .15, z), size, .1)*fade*(1.+ indicator);

        float reflection = 0.;
        reflection += bokeh(r, vec3(x-w2, -.15, z), size*3., 1.)*fade;
        reflection += bokeh(r, vec3(x+w2, -.15, z), size*3., 1.)*(1.+ indicator*.1);

        mask += reflection*focus;

    }

    return vec3(1., .1, .03)*mask;
}

vec2 rain(vec2 uv, float time)
{
    time *= 40.;
    vec2 a = vec2(3., 1.);
    vec2 st = uv*a;
    vec2 id = floor(st);
    st.y += time*.22;
    float noise = fract(sin(id.x*76.34)*758.58);
    st.y += noise;
    uv.y += noise;
    uv.x += noise*.08;
    id = floor(st);
    st = fract(st)-.5;

    time += fract(sin(id.x*76.34 +id.y*135.97)*758.58)*6.28;

    float y = -sin(time+sin(time+sin(time)*.5))*.43;
    vec2 pos_1 = vec2(0., y);
    vec2 offset_1 = (st-pos_1)/a;
    float dist = length((st - pos_1)/a);

    float mask_1 = S(.07, .0, dist);
    vec2 offset_2 = (fract(uv*a.x*vec2(1., 2.))-.5)/vec2(1., 2.);

    dist = length(offset_2);

    float mask_2 = S(.3*(.5-st.y), .0, dist)*S(-.1, .1, st.y-pos_1.y);
    //if(st.x > .46 || st.y > .49)
    //{
    //    mask_1 = 1.;
    //}

    return vec2(mask_1*offset_1*30.+mask_2*offset_2*10.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{ 
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv.x -= .75;
    uv.y -= .35;
    uv.x *= iResolution.x/iResolution.y;

    vec2 m = iMouse.xy/iResolution.xy;
    vec3 camPos = vec3(.5, .2, 0.);
    vec3 lookAt = vec3(.5, .2, 1.);
    float time = iTime * .04+m.x;

    //uv.x += sin(uv.y*40.)*.1;

    vec2 rainDistort = rain(uv*5., time)*.1;
    rainDistort += rain(uv*9., time)*.45;

    uv.x += sin(uv.y*20.)*.009;
    uv.y += sin(uv.y*90.)*.004;
    ray r = getRay(uv-rainDistort*.5, camPos, lookAt, 2.0);

    vec3 col = streetLights(r, time);

    col += headLights(r, time);
    col += tailLights(r, time);
    col += envLights(r, time);
    col += (r.direction.y+.25)*vec3(.2, .1, .5);

    //col = vec3(rainDistort, 0.);
    // Output to screen
    fragColor = vec4(col, 1.);
}

#define LIGHT_BRIGHTNESS 2.
#define SPHERE_RADIUS 3. 
// Radiuses 2. and 4. are also worth a look 

const vec3  eye      = vec3 ( 0, 0, 0.5);
 vec3  light    = vec3  ( 0.0, 0.1, 5 );
const int   maxSteps = 700;
const float eps      = 0.01;

// Rotation matrix around the X axis.
mat3 rotateX(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(1, 0, 0),
        vec3(0, c, -s),
        vec3(0, s, c)
    );
}

// Rotation matrix around the Y axis.
mat3 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, 0, s),
        vec3(0, 1, 0),
        vec3(-s, 0, c)
    );
}

float dSphere ( vec3 p, in vec3 c, float r )
{
	return length ( p - c ) - r;
}

float dBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}


float dTorus ( vec3 p, vec2 t )
{
	vec2 q = vec2 ( length ( p.xz ) - t.x, p.y );

	return length ( q ) - t.y;
}

float smin ( float a, float b, float k )
{
	float res = exp ( -k*a ) + exp ( -k*b );
	return -log ( res ) / k;
}

float menger ( in vec3 p )
{
   float d = dBox(p,vec3(1.0));

   float s = 1.0;
   for( int m=0; m<4; m++ )
   {
      vec3 a = mod( p*s, 2.0 )-1.0;
      s *= 3.0;
      vec3 r = abs(1.0 - 3.0*abs(a));

      float da = max(r.x,r.y);
      float db = max(r.y,r.z);
      float dc = max(r.z,r.x);
      float c = (min(da,min(db,dc))-1.0)/s;

      d = max(d,c);
   }

   return d;
}

float sdf ( in vec3 p, in mat3 m )
{
    vec3 q = m * p + vec3(0,0,-5)*iTime;
    q.xyz -= 2.5;
    q = mod(q, 5.0) - 2.5;

    //return dSphere ( p, vec3 ( 2, 0, 0 ) );
    //return dBox ( q, vec3 ( 0.5, 0.2, 0.7 ) );
	return max (menger(q/2.5), -dSphere ( q, vec3 ( 0, 0, 0 ), SPHERE_RADIUS ));
}

vec3 trace ( in vec3 from, in vec3 dir, out bool hit, in mat3 m, in float maxDist, out float softShadow)
{
	vec3	p         = from;
	float	totalDist = 0.0;

	hit = false;
    softShadow = 1.0;
    float prevDist = 1e20;

	for ( int steps = 0; steps < maxSteps; steps++ )
	{
		float dist = sdf ( p, m );

		if ( dist < 0.001 )
		{
			hit = true;
			break;
		}

        float y = dist*dist/(2.0*prevDist);
        float d = sqrt(dist*dist-y*y);
		softShadow = min(softShadow, 200. * d / max(0.0, max(totalDist, 0.) - y));
        prevDist = dist;

		totalDist += dist;

		if ( totalDist > maxDist )
			break;

		p += dist * dir;
	}

    if (hit) {
        softShadow = 0.;
    }

	return p;
}

vec3 trace ( in vec3 from, in vec3 dir, out bool hit, in mat3 m) {
    const float defaultMaxDist = 100.;
    float softShadow = 0.;
    return trace ( from, dir, hit, m, defaultMaxDist, softShadow);
}

float traceLightBulb ( in vec3 from, in vec3 dir, out bool hit, in vec3 lightPos, in mat3 m)
{
	vec3	p         = from;
	float	totalDist = 0.0;

	hit = false;

	for ( int steps = 0; steps < maxSteps; steps++ )
	{
		float dist = dSphere ( m * p, lightPos, 0.1 );

		if ( dist < 0.001 )
		{
			hit = true;
			break;
		}

		totalDist += dist;

		if ( totalDist > 20. )
			break;

		p += dist * dir;
	}

	return totalDist;
}

vec3 generateNormal ( vec3 z, float d, in mat3 m )
{
    float e   = max (d * 0.5, eps );
    float dx1 = sdf(z + vec3(e, 0, 0), m);
    float dx2 = sdf(z - vec3(e, 0, 0), m);
    float dy1 = sdf(z + vec3(0, e, 0), m);
    float dy2 = sdf(z - vec3(0, e, 0), m);
    float dz1 = sdf(z + vec3(0, 0, e), m);
    float dz2 = sdf(z - vec3(0, 0, e), m);

    return normalize ( vec3 ( dx1 - dx2, dy1 - dy2, dz1 - dz2 ) );
}

vec3 encodeSRGB(vec3 linearRGB)
{
    vec3 a = 12.92 * linearRGB;
    vec3 b = 1.055 * pow(linearRGB, vec3(1.0 / 2.4)) - 0.055;
    vec3 c = step(vec3(0.0031308), linearRGB);
    return mix(a, b, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

        // Normalized pixel coordinates (from 0 to 1)
    bool hit;
	vec3 mouse = vec3(iMouse.xy/iResolution.xy - 0.5,iMouse.z-.5);
    mat3 m     = rotateY ( 6.0*mouse.x) *  rotateX ( -6.0*mouse.y );
    vec2 scale = 3.0 * iResolution.xy / max ( iResolution.x, iResolution.y ) ;
    vec2 uv    = scale * ( fragCoord/iResolution.xy - vec2 ( 0.5 ) );
	vec3 dir   = normalize ( vec3 ( uv, 0 ) - eye );
    vec3 color = vec3 ( 0 );
    vec3 p     = trace ( eye, dir, hit, m );
    vec3 solidColor = vec3(228, 155, 15) / 255.;
    vec3 lightColor = vec3(255, (sin(iTime)+0.5)/2.*255., 255) / 255.;

    light = vec3(5. * -cos(iTime / 2.), 1.5 * sin(iTime + 1.) + 0.1, 5. * sin(iTime / 2.));
    float hitDist = length( eye - p );

	if ( hit )
	{
		vec3  l  = normalize        ( light*m - p );
        vec3  v  = normalize        ( eye - p );
		vec3  n  = generateNormal   ( p, 0.001, m );
		float nl = max ( 0.0, dot ( n, l ) );
        vec3  r  = reflect(l, n);
        float hn = max ( 0.0, dot ( r, v ) );
        float sp = pow ( hn, 4.0 );

        if (dot(l, n) <= 0.) {
            sp = 0.;
        }

		color = 1. * solidColor * nl  + solidColor * sp * 0.2;
        color *= lightColor ;

        float dist = length(light*m - p);
        float shadow = 1.;

        trace(p + l * 0.1, l, hit, m, dist, shadow);

        color *= LIGHT_BRIGHTNESS / pow(dist, 2.) * shadow;
        color += 0.001 * solidColor * lightColor / pow(dist, 2.);
	}

    float bulbDist = traceLightBulb(eye, dir, hit, light, m);

    if (hit && bulbDist < hitDist) {
        float alpha = clamp(0., 1., pow(abs(bulbDist - length(light*m - eye))*10., 2.));
        color = (1.-alpha)*color + alpha * (lightColor * 0.5 + vec3(1) * alpha*alpha);
    }

    // Output to screen
    fragColor = vec4(encodeSRGB(color), 1);
}

