// GOLF COURSE

void mainImage( out vec4 O, in vec2 I ) {

    vec2 UV = (I / R.xy - .5);
    UV.x /= R.y / R.x;

    vec3 ro = vec3( 100.0 * sin( M.x * .00777 ), ( 100.0 * cos( M.x * .00777 )), (( M.y / R.y ) - .5) * -100. );
    vec3 rs = ro; // ray stepped
    vec3 rl = vec3( 0.0, 0., (( M.y / R.y ) - .5) * 50. ); // ray look
    vec3 n, trs, ww = normalize( rl - ro ), uu = normalize( cross( ww, vec3( 0.0, 0.0, 1.0 ))); // Z will always be up in 3D space!
    vec3 rd = normalize( UV.x * uu + UV.y * cross( uu, ww ) + ww ); // ray direction

    bool B; // global break bool
    float t, d;

    // 2-step ray marcher (big volume, then voxel)
    for( int j = 0; j < 7; j++ ) {

        B = false;
        trs = rs;
        t = 0.;

        // big sdf volume jumps
        for( int i = 0; i < LOOP_N; i++) {
            rs = trs + rd * t;
            d = map( rs );
            if( d <= -CUBEH2 ) { break; } else if( d > DMAX ) { B = true; break; }
            t += d;
        }
        if( B ) break;

        trs = rs;
        vec3 c = floor(rs);
        vec3 ts = ( c + max( vec3( 0. ), sign( rd )) - rs) / rd;

        // higher precision for voxels
        for( int i = 0; i < LOOP_N; i++) {
            n = step( ts, ts.yzx ) * step( ts, ts.zxy );
            c += sign( rd ) * n;
            float d = map( c );
            if( d <= -CUBEH ) {
                rs = trs + rd * min( ts.x, min( ts.y, ts.z ));
                break;
            }
            vec3 dd = sign( rd ) / rd * n;
            ts += dd;
        }
        rs = trs + rd * min( ts.x, min( ts.y, ts.z ));
    }
    if( B ) { O = vec4(0.); return; }

    float D = distance( rs, ro );
    if( D > DMAX ) O = vec4( 0. ); else {
        //calculate diffuse with light direction / scaled distance
        vec3 C = vec3( 5. + dot( n * -sign( rd ), vec3( .5, 1., .5 ))) / ( D * .5 );
        // sRGB encoding + contrast mapping
        C = mix( 12.92 * C, 1.055 * pow( C, vec3( 1.0 / 2.4 )) - 0.055, step( vec3( 0.0031308 ), C ));
        O = vec4( clamp( C, vec3( 0. ), vec3( 1. )), 1.0f );
    }
}

float getCol(vec2 uv)
{
    vec2 pos = vec2(cos(uv.x * 10.0) + pow(sin(uv.y * 10.0), 3.0), uv.x / uv.y + cos(iMouse.x / (iMouse.y + 0.001))) * uv.x;
    float z = tan(pos.x/tan(pos.y + 0.001) - sin(iTime)) * sin(cos(pos.y / (pos.x + 0.001) / cos(pos.x)) - iTime);

    return 0.5 * sin(z) + 0.5 + 0.1/tan(uv.x * uv.y);
}

float getBlurredCol(vec2 uv)
{
    float result = 0.0;
    float divBy = 0.0;
    float BLUR_SIZE = 0.03 * (1.0 + sin(uv.x * uv.y * 0.8 - 0.3));
    float BLUR_STEP = 0.015 * (0.7 + 0.5 * sin(uv.x / uv.y));

    for (float x = -BLUR_SIZE; x < BLUR_SIZE; x+=BLUR_STEP) 
    {
        for (float y = -BLUR_SIZE; y < BLUR_SIZE; y+=BLUR_STEP)
        {
            result += getCol(uv + vec2(x, y));
            divBy++;
        }
    }

    return result / divBy;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = 0.9 + tan(iTime * 0.2);
    float scaleFactor = 1.0 / iResolution.x;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = scaleFactor * (fragCoord - iMouse.xy) * zoom * vec2(1.0, -1.0);
    float z = getBlurredCol(uv);
    vec3 col = vec3(sin(1.0 / z) / 4.0 + 0.1, 0.5 * z, z);

    // Output to screen
    fragColor = vec4(col,1.0);
}

const float SceneDurationSeconds = 4.;
const float Complexity = 7.;

vec4 hash42(vec2 p)
{
	vec4 p4 = fract(vec4(p.xyxy) * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

void mainImage(out vec4 o, vec2 C)
{
    vec2 uv = C/max(iResolution.x, iResolution.y);
    uv.x --;
    uv /= 4.;

    float scene = floor((iTime+98.)/SceneDurationSeconds);
    vec4 hscene = hash42(vec2(scene));
    uv += scene;

    float t = iTime*mix(.01,.06,hscene.y);
    uv -= t*.5;

    vec2 uvn = C/iResolution.xy-.5;
    float ds = max(abs(uvn.x), abs(uvn.y))*2.;

    for (float i = 1.; i < Complexity; ++ i) {
        vec2 c = floor(uv);
        vec4 h = hash42(c);
        vec2 p = fract(uv);
        vec2 a = abs(p-.5);
        float sd = .5-max(a.x, a.y);
        o = h*sqrt(sd)*4.;
        uv *= 2.;
        float sc = 1./i;
        uv += fract(abs(fract(uv.yx/sc+t)-.5)*sc-1.) * sign(hscene.x - .5);
    }
    o = clamp(o * hscene,0.,1.);
    o.rgb = mix(vec3(o.r+o.g+o.b)/3., o.rgb, 1.-ds*ds);
    o += (hash42(C+iTime).r-.5)*.15;
    o *= 1.-dot(uvn,uvn*2.);
}

