vec2 rot(vec2 p, float a)
{   return vec2( p.x * cos(a) - p.y * sin(a),
                 p.x * sin(a) + p.y * cos(a) );
}

float getAngle(vec2 pos)
{   vec2 p = normalize(pos);

    if(p.y > 0.0) return        acos(p.x);
    else          return PIx2 - acos(p.x);
}

vec4 colorBorder(vec2 pos)
{   if(pos.y < 0.05) return tonsin(0., 0.05, pos.y);
    if(pos.y > 0.95) return tonsin(0.95, 1., pos.y);
    if(pos.x < 0.05) return tonsin(0., 0.05, pos.x);
    if(pos.x > 0.95) return tonsin(0.95, 1., pos.x);
    return vec4(0.);
}

vec4 colorSectors(vec2 pos)
{
    pos = rot(pos - 0.5, Speed);

    if(RADIUS * RADIUS < pos.x * pos.x + pos.y * pos.y) return BGROUND;

    float asector = PIx2 / NSEC  ;
    float a       = getAngle(pos);

    for(float i = asector, j = 0.; i < PIx2; i += asector, j += 1.)
    {
        if(a < i)
        {   
            if(mod(j, 2.) == 0.)
            {
                vec4 color = colorArrayCircles(rot(pos, -i), false);
                if(color.a > 0.)
                {   return color + vec4( 0., 0., fract(a)/3., 0.);
                }
            }
            else
            {   float t =  i - asector - a - a;
                vec4 color = colorArrayCircles2(rot(pos, t), true );
                if(color.a > 0.)
                {   return color + vec4( fract(t)/3., fract(t)/3., fract(t)/3., 0.);
                }
            }

            return vec4(0.3*sin(i)+0.4, 0.0, 0.0, 1.0);
        }
    }

    return RED;
}

