    vec2 id=floor(uv);
    for(int y=-1;y<=1;y++){
        for(int x=-1;x<=1;x++){
            vec2 offs=vec2(x,y);
            float n=hash21(id+offs);// random [0,1]
            float size=fract(n*354.32);
            float st=star(gv-offs-vec2(n,fract(n*34.))+.5,smoothstep(.9,1.,size)*.6);
            vec3 color=sin(vec3(1.3392,.2882,.0988))*.8+.5;
            st*=sin(iTime*3.+n*6.2831)*.5+1.;
            col+=st*size*color;
        }
    }
    return col;
}


