#define TAU 6.28318530718
#define MINSCALE 0.001
#define SHAPECOUNT 8u
#define TILE true

float fold(float x) {
    return abs(2. * fract(x) - 1.);
}

// returns vec2(mask, distanceFromCenter)
vec2 tshape(vec2 uv, float angle) {
    if (TILE) {
    uv = mod(uv + 6.0, 12.0) - 6.0;
    }
    vec2 uvRot = vec2(
        uv.x * cos(angle * TAU) + uv.y * sin(angle * TAU),
        uv.x * sin(angle * TAU) - uv.y * cos(angle * TAU)
    );

    float rectHriz = max(0., max(abs(uvRot.x) - 2., abs(uvRot.y)));
    float rectVert = max(0., max(abs(uvRot.x), abs(uvRot.y) - 2.));
    if (uvRot.y < 0.) {rectVert = rectHriz;}
    float value = 1. - min(rectHriz, rectVert);
    float mask = step(0.0, value);
    return vec2(mask, mask * (value));
}

float timeAdj(float time){
    return 0.25 * (smoothstep(0.0, 0.2, fract(4. * time)) + floor(4. * time));
}
vec3 hex(int hexcode){
    return vec3(
        (hexcode >> 16) & 255,
        (hexcode >> 8) & 255,
        hexcode & 255
    ) / 255.;
}
vec3 tcolor(vec2 md, float layer, float time) {
    vec3 color0Base  = hex(0x4a089a);
    vec3 color0Light = hex(0x7d3ecd);
    vec3 color1Base  = hex(0x9a2cC3);
    vec3 color1Light = hex(0xfc69f8);

    float mixFactor = step(fract(layer * -5.6), 0.3);

    vec3 colorBase  = mix(color0Base , color1Base , mixFactor);
    vec3 colorLight = mix(color0Light, color1Light, mixFactor);

    return mix(
        colorBase, colorLight, 0.5 + 0.5 * sin(TAU * fract(md.y + 2. * time))
    );
}

float grid(vec2 uv, float scale) {
    return max(fold(uv.x * scale), fold(uv.y * scale));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = fract(iTime / 4.);
    // Scales coords so that the diagonals are all dist 1 from center
    float scale = length(iResolution.xy);
    vec2 uv = (fragCoord / scale
	- (iResolution.xy / scale / 2.)) * 2.;

    float dist = length(uv);

    vec2 t = vec2(0);
    uint i = 0u;

    for (; i < SHAPECOUNT; i++) {
        float prog = (float(i) + time);
        t = tshape(
            uv / (exp(prog) * MINSCALE),
            timeAdj(time + float(i) * -0.01)
        );
        if (t.x > 0.) {
            break;
        }
    }
    // Time varying pixel color
    vec3 col = tcolor(t, float(i) + time, time);
    float gridV = step(0.9, grid(uv, 12.0 * exp2(fract(time))));
    gridV = mix(gridV, step(0.9, grid(uv, 6.0 * exp2(fract(time)))), fract(time));
    col -= 0.05 * gridV;

    // Output to screen
    fragColor = vec4(col, 1.0);
}

vec3 nobles[240];
const float PI = 3.14159265359;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 dir;
    for(int i = 0; i < nobles.length(); i++){
    	nobles[i] = vec3(
        	fract(pow(float(i),1.2)*1.0 + sin(iTime*1.0/20.0*PI*2.0+float(i%60))*0.25),
            fract(pow(float(i),1.3)*1.0 + cos(iTime*1.0/20.0*PI*2.0+float(i%60))*0.25),
            fract(float(i) * 2123.2345)
        );

        dir += vec2(cos(nobles[i].z), sin(nobles[i].z)) / pow(distance(uv, nobles[i].xy + vec2(0.0, 0.0)),2.0);
        dir += vec2(cos(nobles[i].z), sin(nobles[i].z)) / pow(distance(uv, nobles[i].xy + vec2(1.0, 0.0)),2.0);
        dir += vec2(cos(nobles[i].z), sin(nobles[i].z)) / pow(distance(uv, nobles[i].xy + vec2(-1.0, 0.0)),2.0);

        dir += vec2(cos(nobles[i].z), sin(nobles[i].z)) / pow(distance(uv, nobles[i].xy + vec2(0.0, 1.0)),2.0);
        dir += vec2(cos(nobles[i].z), sin(nobles[i].z)) / pow(distance(uv, nobles[i].xy + vec2(1.0, 1.0)),2.0);
        dir += vec2(cos(nobles[i].z), sin(nobles[i].z)) / pow(distance(uv, nobles[i].xy + vec2(-1.0, 1.0)),2.0);

        dir += vec2(cos(nobles[i].z), sin(nobles[i].z)) / pow(distance(uv, nobles[i].xy + vec2(0.0, -1.0)),2.0);
        dir += vec2(cos(nobles[i].z), sin(nobles[i].z)) / pow(distance(uv, nobles[i].xy + vec2(1.0, -1.0)),2.0);
        dir += vec2(cos(nobles[i].z), sin(nobles[i].z)) / pow(distance(uv, nobles[i].xy + vec2(-1.0, -1.0)),2.0);

    }

    vec3 col;

    if(abs(atan(dir.y, dir.x) - 0.5) < 0.03) col = vec3(0.8, 0.8, 0.8);
    else if(abs(atan(dir.y, dir.x) - 0.5) < 0.1)  col = vec3(0.2, 0.2, 0.2);
    else col = vec3(0.65, 0.25, 0.6);

    // Output to screen
    fragColor = vec4(col,1.0);
}

const vec3  eye      = vec3 ( 0, 0, 3 );
const vec3  light    = vec3  ( 0.0, 3.0, 5.0 );
const int   maxSteps = 70;
const float eps      = 0.01;

// Rotation matrix around the X axis.
mat3 rotateX(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(1, 0, 0),
        vec3(0, c, -s),
        vec3(0, s, c)
    );
}

// Rotation matrix around the Y axis.
mat3 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, 0, s),
        vec3(0, 1, 0),
        vec3(-s, 0, c)
    );
}

float dSphere ( vec3 p, in vec3 c )
{
	return length ( p - c ) - 1.0 + 0.03 * sin(20.0*p.x + iTime);
}

float length8 ( in vec2 p )
{
    return pow ( pow ( p.x, 8.0 ) + pow ( p.y, 8.0 ), 1.0/ 8.0 );
}

float length8 ( in vec3 p )
{
    return pow ( pow ( p.x, 8.0 ) + pow ( p.y, 8.0 ) + pow ( p.z, 8.0 ), 1.0/ 8.0 );
}


float dTorus ( vec3 p, vec2 t )
{
	vec2	q = vec2 ( length8 ( p.xz ) - t.x, p.y );

	return length8 ( q ) - t.y;
}

float smin ( float a, float b, float k )
{
	float res = exp ( -k*a ) + exp ( -k*b );
	return -log ( res ) / k;
}

float sdf ( in vec3 p )
{
    //return dSphere ( p, vec3 ( 0, 0, 0 ) );
    //return dBox ( p, vec3 ( 0.5, 0.2, 0.7 ) );
	return dTorus ( p, vec2 ( 0.73, 0.5 ) );
}

float sdf ( in vec3 p, in mat3 m )
{
   vec3 q = m * p;

    //return dSphere ( p, vec3 ( 2, 0, 0 ) );
    //return dBox ( q, vec3 ( 0.5, 0.2, 0.7 ) );
	return smin ( dTorus ( q, vec2 ( 0.73, 0.4 ) ),  dSphere ( q, vec3 ( 1, 0, 0 ) ), 7.0 );
}

vec3 trace ( in vec3 from, in vec3 dir, out bool hit, in mat3 m )
{
	vec3	p         = from;
	float	totalDist = 0.0;

	hit = false;

	for ( int steps = 0; steps < maxSteps; steps++ )
	{
		float	dist = sdf ( p, m );

		if ( dist < 0.01 )
		{
			hit = true;
			break;
		}

		totalDist += dist;

		if ( totalDist > 10.0 )
			break;

		p += dist * dir;
	}

	return p;
}

vec3 generateNormal ( vec3 z, float d, in mat3 m )
{
    float e   = max (d * 0.5, eps );
    float dx1 = sdf(z + vec3(e, 0, 0), m);
    float dx2 = sdf(z - vec3(e, 0, 0), m);
    float dy1 = sdf(z + vec3(0, e, 0), m);
    float dy2 = sdf(z - vec3(0, e, 0), m);
    float dz1 = sdf(z + vec3(0, 0, e), m);
    float dz2 = sdf(z - vec3(0, 0, e), m);

    return normalize ( vec3 ( dx1 - dx2, dy1 - dy2, dz1 - dz2 ) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

        // Normalized pixel coordinates (from 0 to 1)
    bool hit;
	vec3 mouse = vec3(iMouse.xy/iResolution.xy - 0.5,iMouse.z-.5);
    mat3 m     = rotateX ( 6.0*mouse.y ) * rotateY ( 6.0*mouse.x);
    vec2 scale = 9.0 * iResolution.xy / max ( iResolution.x, iResolution.y ) ;
    vec2 uv    = scale * ( fragCoord/iResolution.xy - vec2 ( 0.5 ) );
	vec3 dir   = normalize ( vec3 ( uv, 0 ) - eye );
    vec4 color = vec4 ( 0, 0, 0, 1 );
    vec3 p     = trace ( eye, dir, hit, m );


	if ( hit )
	{
		vec3  l  = normalize        ( light - p );
        vec3  v  = normalize        ( eye - p );
		vec3  n  = generateNormal   ( p, 0.001, m );
		float nl = max ( 0.0, dot ( n, l ) );
        vec3  h  = normalize ( l + v );
        float hn = max ( 0.0, dot ( h, n ) );
        float sp = pow ( hn, 150.0 );

		color = 0.5*vec4 ( nl ) + 0.5 * sp * vec4 ( 0, 1, 0, 1 );
	} 

    // Output to screen
    fragColor = color;
}

const float PI = 3.14159265;

float mag2(vec3 v) { return dot(v, v); }
float mag(vec3 v) { return sqrt(dot(v, v)); }


float line(vec3 p, vec3 q, vec3 d, float width) {
    return mag(p - (q + d*dot(p-q, d)/dot(d,d))) - width;
}

vec3 twist(vec3 p, vec3 center, float dist) {
    vec3 v = p - center;

    vec2 polar = vec2(sqrt(dot(v.xy, v.xy)), atan(v.y, v.x));
    float rad = polar.x;
    float theta = polar.y;


    float r = 2. * PI * polar.x / dist;

    float angle = theta + r;

    v = vec3(rad * cos(angle), rad * sin(angle), v.z);

    return v + center;
}

float lerps(float a, float b, float s) { return s * b + (1.0 - s) * a; }
vec3 lerpv(vec3 a, vec3 b, float s) { return s * b + (1.0 - s) * a; }

float scene(vec3 p) {

    p.xy += vec2(sin(p.y*10. + iTime)*0.05, 0.);

    float swirl1 = line(
            twist(p, vec3(sin(iTime)/5.0, cos(iTime)/5.0, 0.), 0.08), 
            vec3(0., 0., 0.), 
            vec3(-0.4, 1., 0.), 
            lerps(0.03, 0.3, abs(sin((iTime + 7.)*2.3))));

    float swirl2 = line(
        twist(p, vec3(-0.25, -0.25, 0.), lerps(0.01, 0.1, sin(iTime))), 
        vec3(0., 0., 0.), 
        vec3(0.4, 1., 0.), 
        lerps(0.03, 0.3, abs(sin((iTime + 7.)*2.3))));


    return max(swirl1, swirl2);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - iResolution.xy/2.)/iResolution.xx;

    vec3 eye = vec3(uv.x, uv.y, -10.);

    float sd = 1000.;
    vec3 p = eye;
    vec3 dir = vec3(0., 0., 1.);
    for (int i = 0; i < 100; i++) {
       float d = scene(p);
       if (d > sd) break;

       sd = d;
       if (d <= 0.001) break;
       p = p + sd * dir;
    }

    float anxiety = max(0., (sd > 0.001 ? lerps(1.0, 0.0, sd*lerps(1.0, 10.0, abs(sin(iTime*2.7)))) : 1.0));

    vec3 col = lerpv(vec3(1.0, 0.5, 0.), vec3(1.0, 0., 0.), anxiety);

    // Output to screen
    fragColor = vec4(col,1.0);
}

