#define EPS 0.07
#define PI 3.1415926535

float distLine(vec2 point, vec2 a, vec2 b) {
    vec2 pa = point - a;
    vec2 pb = b - a;
    float t = dot(pa, pb) / dot(pb, pb);
    t = clamp(t, 0.0, 1.0);
    return distance(pa, t * pb);
}

vec3 line(vec2 point, vec2 a, vec2 b, float thickness, vec3 color) {
    float dist = distLine(point, a, b);
    return (1.0 - step(thickness, dist)) * color;
}

vec3 circle(vec2 point, vec2 center, float r, float thickness, vec3 fillColor, vec3 strokeColor) {
	float d = length(point - center);
    float inner = step(r - thickness, d);
    float outter = step(d, r);
	vec3 color = (1.0 - inner) * fillColor + inner * outter * strokeColor;
	return color;
}

vec3 rectangle(vec2 point, vec2 center, vec2 size, float thickness, vec3 fillColor, vec3 strokeColor) {
    vec2 po = abs(point - center);
    vec2 mo = min(max(po, vec2(0.0)), size);
    vec2 ed = vec2(1.0) - step(size, mo) * step(size - vec2(thickness), mo);
    vec2 st = vec2(1.0) - step(size - vec2(thickness), mo);
    return st.x * st.y * fillColor + ed.x * ed.y * strokeColor;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    uv *= mat2(cos(iTime), sin(iTime), -sin(iTime), cos(iTime));

    float dist = length(uv);

    float ang = atan(uv.y,uv.x);
    float angMapVal = floor(ang / (2.*PI) * 100.);
    float m1 = mod(angMapVal, 5.0);
    float m2 = sin(ang * 40.0) * 4.0;
    float m = mix(m1, m2, mod(iTime, 12.));
    float modif = EPS + m * 0.010;

    float r = modif + 0.25 + sin(iTime) * 0.25;
    float big = smoothstep(r, r - EPS, dist); // * step(r - EPS, dist);

    // generate final color
    vec3 col = vec3(1.0, 0.1, 0.7) * big;
    col += line(uv, vec2(-0.7,  0.3), vec2(0.7, 0.0), 0.0025, vec3(0.9, 0.0, 0.0));
    col += line(uv, vec2(-0.7,  0.0), vec2(0.7, 0.3), 0.0025, vec3(0.9, 0.0, 0.0));
    col += line(uv, vec2( 0.0, -0.3), vec2(0.0, 0.4), 0.0025, vec3(0.9, 0.0, 0.0));
    col += line(uv, vec2(-0.7,  0.1), vec2(0.7, 0.1), 0.0025, vec3(0.9, 0.0, 0.0));
    col += line(uv, vec2(-0.6, -0.4), vec2(0.2, 0.4), 0.0025, vec3(0.9, 0.0, 0.0));
    col += line(uv, vec2( 0.4, -0.4), vec2(0.2, 0.4), 0.0025, vec3(0.9, 0.0, 0.0));
    col += circle(uv, vec2(0.), 0.3, 0.07, vec3(0.0, 0.8, 0.0), vec3(0.0, 0.0, 0.7));
    col += rectangle(uv, vec2(0.5, 0.3), vec2(0.15, 0.15), 0.05, vec3(0.9, 0.0, 0.0), vec3(0.9, 0.4, 0.2));
    col += rectangle(uv, vec2(-0.5, -0.4), vec2(0.1, 0.1), 0.03, vec3(1.0, 0.0, 0.4), vec3(0.5, 0.4, 0.2));

    // Output to screen
    fragColor = vec4(col,1.0);
}

