// Based on Morgan McGuire @morgan3d
// https://www.shadertoy.com/view/4dS3Wd
float noise (in vec2 _st) {
    vec2 i = floor(_st);
    vec2 f = fract(_st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    vec2 u = f * f * (3.0 - 2.0 * f);

    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

#define NUM_OCTAVES 5

float fbm ( in vec2 _st) {
    float v = 0.0;
    float a = 0.5;
    vec2 shift = vec2(100.0);
    // Rotate to reduce axial bias
    mat2 rot = mat2(cos(0.5), sin(0.5),
                    -sin(0.5), cos(0.50));
    for (int i = 0; i < NUM_OCTAVES; ++i) {
        v += a * noise(_st);
        _st = rot * _st * 2.0 + shift;
        a *= 0.5;
    }
    return v;
}

float pattern( in vec2 p )
{
    vec2 q = vec2( fbm( p + vec2(0.0,0.0) ),
                   fbm( p + vec2(5.2,1.3) ) );

    vec2 r = vec2( fbm( p + 4.0*q + vec2(1.7,9.2) ),
                   fbm( p + 4.0*q + vec2(8.3,2.8) ) );

    return fbm( p + 4.0*r );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = gl_FragCoord.xy/iResolution.xy*3.;
    vec2 oSt = st;
    st += fbm(st + 0.2*iTime - distance(st, vec2(0.5)));
    // st += st * abs(sin(u_time*0.1)*3.0);
    vec3 color = vec3(0.0);


    float a = fbm(st + 3.*fbm(oSt + vec2(0.2*iTime, 0.)));
    float b = fbm(st + 3.*fbm(oSt + vec2(a, 0.) ));
    float c = fbm(st + 3.*fbm(oSt + vec2(b, 0.) ));




    color = mix(vec3(a*a*a*a, b, c),
                vec3(196./255., 116./255., 174./255.),
                a);

    color = mix(color,
                vec3(156./255., 116./255., 196./255.),
                b*b);
     color = mix(color,
                vec3(1.),
                c*c);

    fragColor = vec4(color,1.);
}

// A very simple duck that's walking.
// Shoutout to rimina! :)

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

void rot(inout vec2 p, float a) {
    p = cos(a)*p + sin(a)*vec2(p.y, -p.x);
}

vec2 opU(vec2 d1, vec2 d2)
{
    return (d1.x < d2.x) ? d1 : d2;
}

float llength = .4;
float lspread = .4;
float wspeedmod = 4.;
float wstridemod = .2;

struct MarchResult
{
    vec3 p;
    float id;
};


vec2 sdf(vec3 p){
   vec3 pp = p;

   pp = pp - vec3(1.+sin(iTime*wspeedmod*2.)*.02,.7,.0);
   float head = sdBox(pp,vec3(.5,.4,.5));
   vec3 ppp = pp - vec3(.7,-.1,.0);
   float beak = sdBox(ppp,vec3(.2,.08,.2));
   pp.z = abs(pp.z);
   pp = pp - vec3(.5,.2,.3);
   float eyes = sdBox(pp,vec3(.1,.1,.1));

   vec2 h = opU(vec2(eyes,1.0),opU(vec2(beak,2.0),vec2(head,3.0)));

   pp = p;
   rot(pp.xz,-sin(iTime*wspeedmod)*.1);
   float body = sdBox(pp,vec3(1.,.6,.8));
   pp = pp - vec3(-1.1+sin(iTime*wspeedmod*2.)*.02,.7,.0);
   rot(pp.xz,sin(iTime*wspeedmod)*.1);
   float tail = sdBox(pp,vec3(.4,.1,.6));
   body = min(tail,body); 
   vec2 b = opU(vec2(body,3.0),h);

   pp = p;
   rot(pp.xy,sin(iTime*wspeedmod)*wstridemod);
   pp = pp - vec3(0.,-1.,lspread);
   float lleg = sdBox(pp,vec3(.1,llength,.2));
   lleg = min(lleg,sdBox(pp-vec3(.2,-llength,.0),vec3(.4,.1,.2)));

   pp = p;
   rot(pp.xy,-sin(iTime*wspeedmod)*wstridemod);
   pp = pp - vec3(0.,-1.,-lspread);
   float rleg = sdBox(pp,vec3(.1,llength,.2));
   rleg = min(rleg,sdBox(pp-vec3(.2,-llength,.0),vec3(.4,.1,.2)));
   float legs = min(lleg,rleg);

   return opU(vec2(legs,2.0),b);

}

MarchResult march(in vec3 ro, in vec3 rd, inout float t){

    MarchResult m;
    m.p = ro+rd;
    for(int i = 0; i < 40; ++i){
        vec2 d = sdf(m.p);
        t += d.x;
        m.p += rd*d.x;
        m.id = d.y;

        if(d.x < 0.01 || t > 100.){
            break;
        }

    }

    return m;
}

vec3 color(in float id)
{
    if (id == 1.0)
        return vec3(0.1);
    else if (id == 2.0)
        return vec3(.8,.3,.2);
    else if (id == 3.0)
        return vec3(.8,.7,.4);
    else
        return vec3(0);
}
vec3 calcNormal( in vec3 pos) 
{
    vec2 e = vec2(0.00001, 0.0);
    return normalize( vec3(sdf(pos+e.xyy).x-sdf(pos-e.xyy).x,
                           sdf(pos+e.yxy).x-sdf(pos-e.yxy).x,
                           sdf(pos+e.yyx).x-sdf(pos-e.yyx).x ) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec3 cp = vec3(3.,2.0,3.);
    vec3 ct = vec3(0,0,0);
    vec3 ld = vec3(-2.,0.5,2.);

    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 q = -1.0+2.0*uv;
    q.x *= iResolution.x/iResolution.y;

    vec3 cf = normalize(ct-cp);
    vec3 cr = normalize(cross(vec3(0.0,1.0,0.0),cf));
    vec3 cu = normalize(cross(cf,cr));

    vec3 rd = normalize(mat3(cr,cu,cf)*vec3(q,radians(90.0)));

    vec3 p = vec3(0.0);

    float t;
    MarchResult m;
    m.p = vec3(0.0);
    m.id = 0.0;
    m = march(cp,rd,t);

    vec3 col = vec3(0.0);
    if(t < 100.){
        col = color(m.id) + (clamp(dot(calcNormal(m.p), ld), 0.0, 1.0)*0.1);

    }

    fragColor = vec4(col,1.0);
}

vec2 baryCentric(vec2 A, vec2 B, vec2 C, vec2 P){

    //no needto normalize because equalateral triangles
    vec2 AB = B-A;
    vec2 BC = C-B;
    vec2 CA = A-C;

    float areaA = length(cross(vec3(AB,0.),vec3(P-A,0.)));
    float areaB = length(cross(vec3(BC,0.),vec3(P-B,0.)));
    float areaC = length(cross(vec3(CA,0.),vec3(P-C,0.)));

    float ret = 0.;


    float abSplit = abs(areaA-areaB);
    float bcSplit = abs(areaC-areaB);
    float caSplit = abs(areaA-areaC);
    float thick = 0.03;


    if(areaA < areaB && areaA < areaC) return vec2(1.,
                                        smoothstep(thick,thick+0.01,caSplit) *
                                        smoothstep(thick,thick+0.01,abSplit) );
    if(areaB < areaA && areaB < areaC) return vec2(2.,
                                        smoothstep(thick,thick+0.01,bcSplit) *
                                        smoothstep(thick,thick+0.01,abSplit) );

    if(areaC < areaA && areaC < areaB) return vec2(3.,
                                        smoothstep(thick,thick+0.01,caSplit) *
                                        smoothstep(thick,thick+0.01,bcSplit) );





    //float linesAB = ret != 1. ? smoothstep(0.1,0.2,abSplit)
       //                       : 0.;
