}
float badHash(vec2 x){
    return fract(sin(dot(vec2(23.,72.),x)*134.)*43143.);
}
mat2 skew = mat2(1.1547, 0., 1.1547/2., 1.);
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-iResolution.xy*0.5)/iResolution.y;
    uv.x += iTime/4.;
    uv *= skew;

    uv *= 6.;

    float f = 1.-smoothstep(0.47,0.49, abs(fract(uv.x)-0.5) );
    float g = 1.-smoothstep(0.47,0.49, abs(fract(uv.y)-0.5) );
    float h = smoothstep(0.02,0.025,abs(fract(uv.y)-fract(uv.x)) );

    vec2 ff = fract(uv);
    vec2 id = floor(uv);

    float side = sign(ff.x-ff.y);
    float ii = badHash(id + side);


    vec2 tri = side < 0. ? baryCentric(vec2(0.),vec2(1.),vec2(0.,1.),ff)
                          : baryCentric(vec2(0.),vec2(1.,0.),vec2(1.,1.),ff);
    ff = ff*2.-1.;
    //float ac = abs(length(abs(ff*mat2(0.707,-0.707,0.707,0.707))-0.7)-0.1);
    //ac = smoothstep(0.05,0.1,ac);
    // Time varying pixel color
    vec3 col = vec3( ii - 0.1*(length(ff-vec2(side*0.35,-side*0.35))-0.05) );


    //col *= ac;
    if(side < 0.){
        col = mix(col, vec3(.3,0.1,0.7), step(.5,tri.x));
        col = mix(col, vec3(.4,0.7,.9), step(1.5,tri.x));
        col = mix(col, vec3(0.5,0.6,.3), step(2.5,tri.x));
        col *= tri.y;//= mix(col, vec3(0.), tri);
        }

    if(side > 0.){
        col = mix(col, vec3(.1,0.,0.).xzy, step(.5,tri.x));
        col = mix(col, vec3(.4,0.,.1).xzy, step(1.5,tri.x));
        col = mix(col, vec3(0.8,0.,.3).xzy, step(2.5,tri.x));
        col *= tri.y;//= mix(col, vec3(0.), tri);
        }
      col *=g*f*h; 
      col *= 1.2;
    //col *= ac;
    // Output to screen
    fragColor = vec4(col,1.0);
}

