#define N normalize

void mainImage(out vec4 c, vec2 q)
{
    vec2 r = iResolution.xy;
    q = (q + q - r)/r.y; // viewport
	float t = iTime, k = .0015, x;
    vec3 o = vec3(2);
    o.xz *= R(.1 * t);    
    vec3 w = N(-o) // camera
    //, u = N(cross(w, vec3(0,1,0)))
    , u = N(vec3(-w.z,0,w.x)) // thx dean_the_coder!
    , v = N(cross(u, w))
    , d = N(q.x*u + q.y*v + 4.*w)
    , a = vec3(0), p;
    o.x -= .2*t;
    t = 3.5; // now trace len, skip empty space at start
    for (int i = 99; i-- > 0; k *= 1.025) {      
        p = t * d + o;
        float s = 2., z = 1., e, f;	// Yonatan combined field
        for (e = f = p.y; s < 1e2; s *= 1.6)
            p.xz *= R(s),
            e S(, vec3(.4)), // fog
            f S(.xz*.5, vec2(1)); // hill
        z += f > .001 ? e : -exp(-f*f);
        x = max(z, 0.);
        t += 2.5 * k * x * x;
        a = .95 * a + .07 * x * vec3(x * x, x, 1);
    }    
    c = vec4(.5 * log(1. + a), 1); // tonemap
}
