    float t = iTime;
    for(o=c=1.;i++<1e2;g+=min(f,max(.03,e))*.7) {
        s=2.;vec3 p=vec3((Q.xy-r/s)/r.y*g,g-s);
        p.yz*=R(-.8);
        p.z+=t;
        for(e=f=p.y;s<2e2;s/=.6)
            p.xz*=R(s),
            e+=abs(dot(sin(p*s)/s,p-p+.3)),
            f+=abs(dot(sin(p.xz*s*.6)/s,r/r));
        o+=(f>k*k?e:-exp(-f*f))*o*k;
    }
    c+=e;    
    C = vec4(o);
    C.rg -= c*.04;    
}
