#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(h)(cos((h)*6.3+vec3(0,23,21))*.5+.5)
void mainImage(out vec4 O, vec2 C)
{
    vec3 p,r=iResolution,c=vec3(0),
    d=normalize(vec3((C-.5*r.xy)/r.y,1));
    float i=0.,s,e,g=0.;
    for(;++i<99.;)
    {
        p=g*d;
        p.z+=iTime*.8;
        p=R(p,vec3(.577),.3);
        s=2.;
        p=cos(p);
        for(int i=0;i++<7;)
        {
            p=1.8-abs(p-1.2);
            p=p.x<p.y?p.zxy:p.zyx;
            s*=e=4.5/min(dot(p,p),1.5);
            p=p*e-vec3(.2,3,4);
        }
        g+=e=length(p.xz)/s;
        c+=mix(vec3(1),H(log(s*5.)),.3)*.01*exp(-9./i/i/e);
    }
    c*=c;
    O=vec4(c,1);
}

vec3 plot(float dist, vec3 color, float thickness) {
    return color * (smoothstep(-thickness, 0.0, dist) - smoothstep(0.0, thickness, dist));
} 

vec3 y_axis(vec2 uv) {
    float dist = (uv.x - 0.5);
    return plot(dist, vec3(1.0, 1.0, 0.0), 0.002);
}

vec3 x_axis(vec2 uv) {
    float dist = (uv.y - 0.5);
    return plot(dist, vec3(1.0, 1.0, 0.0), 0.003);
}

vec3 yx_line(vec2 uv) {
    vec2 point = uv - 0.5;
    float dist = abs(point.x - point.y);
    return plot(dist, vec3(0.0, 1.0, 0.0), 0.003);
}

vec3 sin_line(vec2 uv) {
    vec2 point = uv - 0.5;
    float val = 0.4 * sin(iTime) * sin(point.x * 3.14 * 5. + iTime*2.);
    float dist = abs(val - point.y);
    return plot(dist, vec3(1.0, 0.0, 0.0), 0.02);
}

vec3 cos_line(vec2 uv) {
    vec2 point = uv - 0.5;
    float val = 0.3 * cos(iTime) * cos(point.x * 3.14 * 5. + iTime*5.);
    float dist = abs(val - point.y);
    return plot(dist, vec3(1.0, 0.0, 1.0), 0.02);
}

vec3 tan_line(vec2 uv) {
    vec2 point = uv - 0.5;
    float val = 0.3 * cos(iTime) * tan(point.x * 3.14 * 5. + iTime*4.);
    float dist = abs(val - point.y);
    return plot(dist, vec3(1.0, 1.0, 0.0), 0.02);
}


