const float CLOSE_ENOUGH = 0.001;
const float RENDER_DISTANCE = 30.0;

const vec3 BG_COLOR = vec3(90., 10., 90.)/255.;
const vec3 SHADOW_COLOR = vec3(30., 10., 80.)/255.;
const vec3 COLOR = vec3(30., 150., 250.)/255.;

struct sphere {
	vec3 c;
    float r;
};

float sphereDist(vec3 p, sphere s) {
	return distance(p, s.c) - s.r;
}

//from iq : https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float opSmoothUnion( float d1, float d2, float k )  {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); 
}

float sdf(vec3 p) {
    float returnVal = .0;

    vec3 rep = vec3(1., 0., 2.);
	vec3 pr = mod(p, rep) - .5 * rep; 

    sphere s1 = sphere(vec3(sin(iTime*4.)*.0, sin(iTime*3.)*0.25-0.5, 0.5), 0.1-0.3*sin(iTime));
    sphere s2 = sphere(vec3(0., sin(iTime*2.)*.8-0.8, 0.5), 0.1);
    sphere s3 = sphere(vec3(0., sin(iTime*1.)*1.-0.8, 0.5), 0.3*sin(iTime));

    vec3 b = vec3(1.5, 0.01, 3.);
    vec3 q = abs(pr + vec3(.5,1.,-2.)) - b;
    float b1 = length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);

    float smoothness = .2;
    returnVal = opSmoothUnion(b1, sphereDist(pr,s1), smoothness); 
    returnVal = opSmoothUnion(returnVal, sphereDist(pr,s2), smoothness); 
    returnVal = opSmoothUnion(returnVal, sphereDist(pr,s3), smoothness);    
	return returnVal; 
}

// from iq https://www.iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 calcNormal( in vec3 p ) {
    const vec2 k = vec2(1,-1);
    return normalize( k.xyy*sdf( p + k.xyy*CLOSE_ENOUGH ) + 
                      k.yyx*sdf( p + k.yyx*CLOSE_ENOUGH ) + 
                      k.yxy*sdf( p + k.yxy*CLOSE_ENOUGH ) + 
                      k.xxx*sdf( p + k.xxx*CLOSE_ENOUGH ) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Normalized pixel coordinates (from 0 to 1)
	vec2 uv    = fragCoord.xy / iResolution.xy - vec2(0.5);
         uv.x *= iResolution.x / iResolution.y;
	vec3 cameraPos = vec3(.0,0.0,-iTime*0.1);

    // Time varying pixel color
    vec3 ro = vec3(cameraPos.x + uv.x, cameraPos.y + uv.y, cameraPos.z);
	vec3 rd = normalize(vec3(uv.x, uv.y, 1.0));
    vec3 col = BG_COLOR * (1. - uv.y);
    float dist = .0;

    vec3 lightDir = normalize(vec3(sin(iTime*0.125), sin(iTime*0.25), -1.0));

    while(dist < RENDER_DISTANCE)
    {
        float d = sdf(ro + rd * dist);
        if(d < CLOSE_ENOUGH)
        {
            vec3 hit = ro + rd * dist;
            col = COLOR; 
            col *= mix(SHADOW_COLOR, col, max(dot(calcNormal(hit), lightDir), .0));
            col = col;
            break;
        }
        dist += d;
    }

    // Output to screen
    fragColor = vec4(col, 1.0);

}

