// operations on complex numbers
vec2 cmul(vec2 a, vec2 b)
{
    return vec2(a.x * b.x - a.y * b.y, a.x * b.y + a.y * b.x);
}

vec2 conjugate(vec2 a)
{
    return vec2(a.x, -a.y);
}

vec2 cdiv(vec2 a, vec2 b)
{
    return cmul(a, conjugate(b)) / (b.x * b.x + b.y * b.y);
}

vec2 cpow(vec2 a, int e)
{
    vec2 res = a;
    for (int i = 1; i < e; i++)
    {
        res = cmul(res, a);
    }
    return res;
}

// 4th degree polynomial with no constant term
vec2 f(vec2 a, vec2 r[4])
{
    vec2 res = vec2(0.);
    for (int i = 0; i < 4; i++)
    {
        res += cpow(cmul(r[i], a), 4-i);
    }
    return res;
}

// derivative
vec2 df(vec2 a, vec2 r[4])
{
    vec2 res = vec2(0.);
    for (int i = 0; i < 4; i++)
    {
        res += float(4-i) * cpow(cmul(r[i], a), 4-i-1);
    }
    return res;
}

// newton-raphson
vec2 newton(vec2 guess, vec2 r[4])
{
    for (int i = 0; i < 30; i++)
    {
        guess = guess - cdiv(f(guess, r), df(guess, r));
    }
    return guess;
}

vec3 hs(vec3 Color, float Shift)
{
    vec3 P = vec3(0.55735, 0.55735, 0.55735)*dot(vec3(0.55735, 0.55735, 0.55735),Color);
    vec3 U = Color-P;
    vec3 V = cross(vec3(0.55735, 0.55735, 0.55735),U);
    Color = U*cos(Shift*6.2832) + V*sin(Shift*6.2832) + P;
    return Color;
}

