{

    result.rgb = c2 * 0.5;

    float tile = 18.0;
    float sharp = 2.0 / iResolution.x * tile;


    // iterate neighbors
    for (int i=-2; i<=2; i++) {
    for (int j=-2; j<=2; j++) {
        vec2 uv = fragCoord/iResolution.xy * vec2(1.0, iResolution.y / iResolution.x);

        // get grid id of current object
        ivec2 id = ivec2(floor(uv*tile) + vec2(i,j));
        vec2 idf = vec2(id) / tile;

        uv = fract(uv * tile);
        // account for coord in neighborhood
        uv -= vec2(i,j);


        vec2 tri = vec2(0.6, 0.8 + 0.5*sin(iTime));
        float rndg = 0.1234;

        // TODO: rotation matrix!

        // offset even rows
        float off = id.y % 2 == 0 ? 1.0/3.0 : 0.0;
        // tile
        vec2 ctr = 0.5 + vec2(off, 0.0);
        float r = 0.125;

        uv = rotation(PI / 4.0 + sin(idf.x + iTime)) * (uv - 0.5);

        //float dist = length(uv-ctr) - r;
        float dist = sdTriangleIsosceles(uv + vec2(off, 0.0), tri) - rndg;



        float a = sinc(dist * 22.0, 2.0);
        float a_ = 1.0 - a;

        vec3 col = id.x % 2 == 0 ? c0 : c1;
        vec3 other = mix(hash(uvec3(id.xyx)), c2, 0.7);
        col = mix(col, other, 1.0 - 2.0*idf.y);

        // source-over
        result.rgb = result.rgb * a_ + col.rgb * a;
        result.a   = result.a * a_ + a;
    }}

    // Output to screen
}

// Radius of the first circle, scales the whole image.
float R = 40.;
// Ratio of the radius of the first circle to the second
float k = 1.;
// Ratio of the radius of the first circle to the third
float k2 = 2.01;
// Distance to the drawing point
float h = 1.;
// Ratio of the paths
float p = 0.5;

float coolX(float R, float r, float r2, float h, float p, float t) {
    return (R + r) * cos(t) 
            + (r + r2) * cos(t + (R*t)/r - R*t/(p*r))
            + h * cos(t + R*t/r - R*t/(p*r) - R*t/(p*r2));
}

float coolY(float R, float r, float r2, float h, float p, float t) {
    return (R + r) * sin(t)
            + (r + r2) * sin(t + (R*t)/r - R*t/(p*r))
            + h * sin(t + R*t/r - R*t/(p*r) - R*t/(p*r2));
}

