#define pi 3.1415926535
#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))
#define pmod(p,x) (mod(p,x)-0.5*(x))


vec3 hsv(vec3 c){
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}
//My poor mans version of Javad Taba's helix function
vec3 spiral(vec3 p, float R){

    p.xz*=rot(p.y/R);
    vec2 s = sign(p.xz);
    p.xz=abs(p.xz)-R*0.5;

    p.xz*=rot(iTime*pi/3.);

    float poy = p.y;
    p.y=0.;
    //This is supposed to correct for the distortion that happens
    //when you twist a shape by rotating it over an axis.
    //In my head it should work a lot better than it does, but
    //it definitely helps a little bit so I guess it's better than nothing
    p.yz*=rot(mix(0.,pi/4.,1./(R*0.5+1.5)))*-sign(s.x*s.y);
    p.y=poy;
    return p;
}


vec2 map(vec3 p){
    float t = iTime*0.5;
    //p.y+=sin(-p.z*0.1)*2.;
    p.y-=p.z*p.z*0.008;

    p.zy*=rot(pi/2.);
    vec3 po = p;

    p.y-=t*pi*4.339;

    vec2 a = vec2(1);
    vec2 b = vec2(2);
    p.xz*=rot(-0.05*(iTime/3.));
    p = spiral(p,6.6);
    p = spiral(p,2.);
    p = spiral(p,1.);
    p = spiral(p,0.4);
    //there are some small artifacts but you dont notice them ;) 


    //p = spiral(p,0.1);
    //vec2 d = abs(p.xz);
    //a.x = max(d.x,d.y)-1.0;
    a.x = length(p.xz)-0.1;
    //a.x = max((abs(po.y)-7.),a.x);
    a.x*=0.6;
    return vec2(a);
}
vec3 norm(vec3 p){
    vec2 e = vec2(0.005,0);
    return normalize(map(p).x-vec3(
    map(p-e.xyy).x,
    map(p-e.yxy).x,
    map(p-e.yyx).x));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
