    vec3 lk = vec3(0,0,0);
    vec3 f = normalize(lk-ro);
    vec3 r = normalize(cross(vec3(0,1,0),f));
    vec3 rd = normalize(f*(0.9)+uv.x*r+uv.y*cross(f,r));  
    vec3 p = ro;
    float dO = 0.;
    bool hit = false;
    vec2 d= vec2(0);
    for(float i = 0.; i<STEPS; i++){
        p = ro+rd*dO;
        d = map(p);
        dO+=d.x;

        if(abs(d.x)<0.005||i>STEPS-1.5){
            hit = true;
            break;
        }
        if(dO>MDIST){
            dO = MDIST;
            break;
        }
    }
    if(hit)
    {
        vec3 ld = normalize(-vec3(p.x,p.y,p.z-5.));

        //sss from nusan
        float sss=0.01;
        for(float i=1.; i<20.; ++i){
            float dist = i*0.35;
            sss += smoothstep(0.,1.,map(p+ld*dist).x/dist)*0.18*1.25;
        }
        for(float i=1.; i<5.; ++i){
            float dist = i*0.7;
            sss += smoothstep(0.,1.,map(p-ld*dist).x/dist)*0.25;
        }
        vec3 al = vec3(0.204,0.267,0.373);
        vec3 n = norm(p);
        vec3 r = reflect(rd,n);
        float diff = max(0.,dot(n,ld));
        float amb = dot(n,ld)*0.45+0.55;
        float spec = pow(max(0.,dot(r,ld)),40.0);
        #define AO(a,n,p) smoothstep(-a,a,map(p+n*a).x)
        float ao = AO(.3,n,p)*AO(.5,n,p)*AO(.9,n,p);

        col = al*
        mix(vec3(0.169,0.000,0.169),vec3(0.984,0.996,0.804),mix(amb,diff,0.75))
        +spec*0.3;
        col+=sss*hsv(vec3(0.76,0.9,1.35));
        col*=mix(ao,1.,0.5);
        col = pow(col,vec3(0.7));
    }
    vec3 bg = mix(vec3(0.094,0.000,0.200),vec3(0.600,0.000,0.600),length(rd.xy)-0.65);

    col = mix(col,bg,pow(dO/MDIST,2.5));
    fragColor = vec4(col,1.0);
}

#define HEIGHT      0.05   // Height of the Bar
#define BACKLIGHT   0.6    // Backlight
#define BRIGHTNESS  0.33   // overall Brightness
#define GLOW        1.25   // Glow intensity of the Bar

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy - 0.5;

	float c = BACKLIGHT;
	float a = abs(uv.y);
	float s = 1.0 - smoothstep(0.0, HEIGHT, a);
	c *= 1.33 - smoothstep(0.0, 0.5, a);
    c*=c*c;

    if(abs(uv.y) < HEIGHT) { c += s; }

	fragColor = vec4(cos(6.283 * (uv.x + iTime + vec3(0.0,.33,0.66))) + GLOW, 1.0) * c * BRIGHTNESS;
}

#define enable_color_correction 1

float max_d = 100000.;
float min_d = 0.0015;
int max_i = 220;
float rotation_speed = .2;

vec4 s0 = vec4(-4.5, 0., -3., 4.);
vec4 s1 = vec4(3., -1.8, -2., 2.2);
vec4 c0 = vec4(1., -1.5, 4., 2.5);
float p1 = -4.03;
vec4 c2 = vec4(.5, 2.02, 3.5, 1.);
vec4 s2 = vec4(.5, 4.5, 3.5, 1.5);
vec4 s3 = vec4(-7., 4., 5., 1.);

#if enable_color_correction
float gamma = 2.2; // For gamma correction.
float exposure = 0.6;
float tone_pow = 0.35; // Represents how strong the tone mapping is.

vec3 correct_color(vec3 c) {
    c *= exposure;
    c = pow(c, vec3(1. / tone_pow));
    c = pow(c / (c + 1.), vec3(tone_pow));
    c = pow(c, vec3(1. / gamma));
    return c;
}
#endif

float sum(vec3 v) { return v.x + v.y + v.z; }
float max_abs(vec3 v) { return max(max(abs(v.x), abs(v.y)), abs(v.z)); }

float sphere(vec3 p, vec3 s, float r) { return sqrt(sum((p - s) * (p - s))) - r; }

float cube(vec3 p, vec3 s, float r) { return max_abs(p - s) - r; }

vec3 cube_m(vec3 p, vec4 c, vec3 ray) {
    if(abs(p.x - c.x) > max(abs(p.z - c.z), abs(p.y - c.y))) { ray.x *= -1.; }
    if(abs(p.y - c.y) > max(abs(p.z - c.z), abs(p.x - c.x))) { ray.y *= -1.; }
    if(abs(p.z - c.z) > max(abs(p.y - c.y), abs(p.x - c.x))) { ray.z *= -1.; }
    return ray;
}

vec3 col(vec3 cam, vec3 ray) {
    vec3 p = cam;
    vec3 col = vec3(1., 1., 1.);
    vec3 col_1 = col;
    vec3 pcol = vec3(0., 0., 0.);
    float min_s3d = 2.;
    bool t;
    for(int i = 0; i < max_i; i ++) {
        t = true;
        float d = max_d;
        d = min(d, sphere(p, s0.xyz, s0.a));
        if(d < min_d && t) {
            ray -= normalize(s0.xyz - p) * dot(s0.xyz - p, ray) / s0.a * 2.;
            col *= vec3(1., .5, .5);
            t = false;
        }
        d = min(d, sphere(p, s1.xyz, s1.a));
        if(d < min_d && t) {
            ray -= normalize(s1.xyz - p) * dot(s1.xyz - p, ray) / s1.a * 2.;
            col *= vec3(.5, 1., .5) * (float(sin(atan((s1.x - p.x) / (s1.z - p.z)) * 10. + sin(iTime) * 30.) > 0.) * .7 + .3);
            t = false;
        }
        d = min(d, sphere(p, s2.xyz, s2.a));
        if(d < min_d && t) {
            ray -= normalize(s2.xyz - p) * dot(s2.xyz - p, ray) / s2.a * 2.;
            col *= vec3(.2, 1., 1.) * (float(sin(atan(length(s2.xz - p.xz) / (s2.y - p.y)) * 12.) > 0.) * .7 + .3);
            t = false;
        }
        float s3d = sphere(p, s3.xyz, s2.a);
        if(s3d < min_s3d && s3d < 2.) { min_s3d = s3d; }
        d = min(d, s3d * .4);
        if(d < min_d && t) {
            pcol += vec3(2., 2., 4.) * col;
            t = false;
            break;
        }
        d = min(d, cube(p, c0.xyz, c0.a));
        if(d < min_d && t) {
            ray = cube_m(p, c0, ray);
            col *= vec3(.4, .4, 1.);
            t = false;
        }
        if(ray.y < 0.) {
            d = min(d, (p1 - p.y) / ray.y);
            if(d < min_d && t) {
                ray.y *= -1.;
                col *= vec3(.3, .3, .3);
                t = false;
            }
        }
        d = min(d, cube(p, c2.xyz, c2.a));
        if(d < min_d && t) {
            ray = cube_m(p, c2, ray);
            col *= vec3(1., 1., .1);
            t = false;
        }
        if(!t) {
            p += ray * min_d * 2.;
            pcol += col_1 * vec3(1., 1., 2.) * (2. - min_s3d) * (2. - min_s3d) * .5;
            min_s3d = 2.;
            col_1 = col;
        }
        p += ray * d;
        if(length(p) > max_d) { break; }
    }
    pcol += col * vec3(1., 1., 2.) * (2. - min_s3d) * (2. - min_s3d) * .5;
    if(length(p) <= max_d && t) { return pcol; }
    col = col * (ray.yxz * vec3(.65, .45, .45) + vec3(.35, .55, .55)).rgb * (ray.y * 2.5 + .4) *
    (0.9 + .2 * float(sin(atan(ray.x / ray.z) * 30.) > 0.6)) *
    (0.9 + .2 * float(sin(atan(ray.y / ray.z) * 30.) > 0.6)) *
    (0.9 + .2 * float(sin(atan(ray.y / ray.x) * 30.) > 0.6)) + pcol;
    return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - iResolution.xy * .5) / (iResolution.x + iResolution.y) * 4.;

    float x = iTime * 1.5 - float(int(iTime * 1.5)) - .5;
    s2.y = 4.52 + (1. - x * x * 4.) * 2.;

    float t = iTime * rotation_speed;

    vec3 color = col(
        vec3(sin(t) * 25., 4., - cos(t) * 25.),
        normalize(vec3(- sin(t) * 1.3 + cos(t) * uv.x,
            uv.y - .3, cos(t) * 1.3 + sin(t) * uv.x))
    );

    #if enable_color_correction
    fragColor.rgb = correct_color(color);
    #else
    fragColor.rgb = color;
    #endif
    fragColor.a = 1.;
}

//Using code from IQ
//https://iquilezles.org/www/articles/distfunctions/distfunctions.htm

const int MAX_MARCHING_STEPS = 255;
const float MIN_DIST = 0.0;
const float MAX_DIST = 20.0;
const float PRECISION = 0.001;
const float PI = 3.14159265359;

vec3 white = vec3(1.);
vec3 black = vec3(.0);
vec3 grey = vec3(.6);
vec3 weights_col = vec3(.2, .2, .2);
vec3 pink = vec3(1., .71, .76);
vec3 blush_col = vec3(.99, .5, .65);
vec3 shoes_col = vec3(.87, .02, .35);
vec3 blue = vec3(.0, .0, 1.);

struct Surface {
    float sd;
    vec3 col;
};

mat3 rotateX(float a) {
    float c = cos(a);
    float s = sin(a);
    return mat3(
        vec3(1., 0., 0.),
        vec3(0., c, -s),
        vec3(0., s, c)
    );
}

mat3 rotateY(float a) {
    float c = cos(a);
    float s = sin(a);
    return mat3(
        vec3(c, 0., -s),
        vec3(0., 1., 0.),
        vec3(s, 0., c)
    );
}

mat3 rotateZ(float a) {
    float c = cos(a);
    float s = sin(a);
    return mat3(
        vec3(c, -s, 0.),
        vec3(s, c, 0.),
        vec3(0., 0., 1.)
    );
}

//operator
Surface unionOp(Surface s1, Surface s2) {
    if(s1.sd < s2.sd) {
        return s1;
    } else {
        return s2;
    }
}

Surface intersection(Surface s1, Surface s2) {
    if(s1.sd < s2.sd) {
        return s2;
    } else {
        return s1;
    }
}

Surface smoothUnion(Surface a, Surface b, float k ) {
    float h = clamp( 0.5 + 0.5 * (b.sd - a.sd) / k, 0.0, 1.0 );
    vec3 col = mix( b.col, a.col, h ) - k * h * (1.0 - h);
    return Surface(mix( b.sd, a.sd, h ) - k * h * (1.0 - h), col);
}

Surface ssubstract(Surface a, Surface b, float k ) {
    float h = clamp( 0.5 - 0.5 * (b.sd + a.sd) / k, 0.0, 1.0 );
    vec3 col = mix( b.col, a.col, h ) - k * h * (1.0 - h);
    return Surface(mix( b.sd, -a.sd, h ) + k * h * (1.0 - h), col);
}

//sdf

Surface sdSphere(vec3 p, float r, vec3 col) {
    float d = length(p) - r;
    return Surface(d, col);
}

Surface sdEllipsoid( vec3 p, vec3 r, vec3 col) {
  float k0 = length(p / r);
  float k1 = length(p / (r * r));
  return Surface(k0 * (k0 - 1.0) / k1, col);
}

Surface sdFloor(vec3 p, vec3 col) {
    return Surface(p.y, col);
}

Surface sdVerticalCapsule( vec3 p, float h, float r, vec3 col)
{
  p.y -= clamp( p.y, 0.0, h );
  return Surface(length( p ) - r, col);
}

Surface sdRoundCone( vec3 p, float r1, float r2, float h, vec3 col) {
  vec2 q = vec2( length(p.xz), p.y );

  float b = (r1-r2)/h;
  float a = sqrt(1.0-b*b);
  float k = dot(q,vec2(-b,a));

  if( k < 0.0 )return Surface(length(q) - r1, col);
  if( k > a*h ) return Surface(length(q-vec2(0.0,h)) - r2, col);

  return Surface(dot(q, vec2(a,b) ) - r1, col);
}

Surface sdBox( vec3 p, vec3 b , vec3 col) {
  vec3 q = abs(p) - b;
  return Surface(length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0), col);
}

Surface sdCylinder( vec3 p, float h, float r, vec3 col) {
  vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(h, r);
  return Surface(min(max(d.x,d.y), 0.0) + length(max(d,0.0)), col);
}

Surface bar(vec3 p, vec3 repulsor, float k) {
    //repulsor to bend the bar
    p = p - normalize(p - repulsor) * k;
    Surface bar = sdCylinder((p - vec3(0., -2.7, .3)) * rotateZ(1.5708), .08, 4., white);
    return bar;
}


Surface arms(vec3 p, Surface modele, bool flex) {
    vec3 pbiceps = p;
    float wave = .5 +.35 * sin(iTime * 3.);
    pbiceps.y += 1.;
    pbiceps.y *= 1.+.1 * wave;
    pbiceps.y -= 1.;
    pbiceps.xz *= 1.-.1 * wave;

    vec3 pforearm = p;
    pforearm.x += 1.4;
    pforearm.z += 1.1;
    pforearm.y += 1.25;
    pforearm *= 1.+.1 * -wave;
    pforearm -= 1.2;

    //arms
    Surface shoulderL = sdSphere((pbiceps - vec3(.5, .5, -.25)), .6, pink);
    Surface armL = sdVerticalCapsule((pbiceps - vec3(.5, 0.5, -.35)) * rotateZ(90.), 1.5, .45, pink);
    Surface biceps = sdEllipsoid((pbiceps - vec3(1.5, .35, -.25)) * rotateZ(10.), vec3(.6, .45, .45), pink);
    Surface triceps = sdEllipsoid((pbiceps - vec3(1.25, -.35, -.25)) * rotateY(6.) * rotateZ(10.), vec3(.5, .25, .25), pink);
    Surface arm = smoothUnion(shoulderL, armL, .25);
    arm = smoothUnion(biceps, arm, .1);
    arm = smoothUnion(arm, triceps, .5);

    Surface elbow = sdEllipsoid((p - vec3(2.0, -.35, -.25)) * rotateY(6.) * rotateZ(10.), vec3(.65, .5, .5), pink);
    Surface elbow2 = sdEllipsoid((pforearm - vec3(2.35, -.65, -.25)) * rotateY(6.) * rotateZ(10.), vec3(.45, .3, .3), pink);
    arm = smoothUnion(arm, elbow, .2);
    arm = smoothUnion(arm, elbow2, .2);

    Surface forearmL = sdEllipsoid((pforearm - vec3(2.5, 0.2, -.05)) * rotateX(-60.) /* rotateZ(-wave)*/, vec3(.5, 1.2, .45), pink);
    Surface hand;
    if(flex)
        hand = sdRoundCone((pforearm - vec3(2.5, 1., .25)) * rotateX(50.) * rotateZ(87.), .25, .5, .5, pink);
    else {
         hand = sdRoundCone((pforearm - vec3(2.5, 1., .25)) * rotateX(50.) * rotateZ(0./*87.*/), .25, .5, .5, pink);
         Surface thumb = sdEllipsoid((pforearm  - vec3(2.1, 1.4, .8)) * rotateY(-1.3), vec3(.3, .15, .15), pink);
         hand = smoothUnion(hand, thumb, .2);
    }
    forearmL = smoothUnion(hand, forearmL, .1);
    arm = smoothUnion(arm, forearmL, .1);
    modele = smoothUnion(arm, modele, .1);

    return modele;
}

Surface weights(vec3 p, Surface modele) {
    float wave = .5 +.35 * sin(iTime * 3.);
    p.x += 1.4;
    p.z += 1.1;
    p.y += 1.25;
    p.xzy *= 1.+.1 * -wave;
    p.xzy -= 1.2;

    Surface bar = bar(p, vec3(0., -5.5, .0), 4.5);

    vec3 op = p;
    op.x = abs(op.x);
    Surface weight1 = sdCylinder((op - vec3(5.45, .6, .65)) * rotateZ(1.8), 1.9, .4, weights_col);
    Surface weight_sub = sdCylinder((op - vec3(5., .7, .65)) * rotateZ(1.8), 1., .2, grey);
    Surface security1 = sdCylinder((op - vec3(5., .8, .65)) * rotateZ(1.8), .3, .2, grey);
    weight1 = ssubstract(weight_sub, weight1, .1);
    weight1 = unionOp(weight1, security1);
    Surface weight2 = sdCylinder((op - vec3(6., .4, .65)) * rotateZ(1.8), 1.7, .2, weights_col);
    Surface weight3 = sdCylinder((op - vec3(6.35, .3, .65)) * rotateZ(1.8), 1.3, .15, weights_col);
    Surface weight_sub2 = sdCylinder((op - vec3(6.5, .25, .65)) * rotateZ(1.8), .5, .15, grey);
    Surface security = sdCylinder((op - vec3(6.5, .25, .65)) * rotateZ(1.8), .25, .15, grey);
    weight3 = ssubstract(weight_sub2, weight3, .1);
    Surface weights = unionOp(weight1, weight2);
    weights = unionOp(weights, weight3);
    weights = unionOp(weights, security);
    bar = unionOp(bar, weights);

    modele = ssubstract(bar, modele, .2);
    modele = unionOp(bar, modele);
    return modele;
}

Surface legs(vec3 p, Surface modele) {
    //thigh
    Surface thigh = sdRoundCone((p - vec3(0.5, -2.1, .0)) * rotateZ(6.2), .5, .7, .9, pink);
    Surface leg = smoothUnion(thigh, modele, .4);
    modele = unionOp(leg, modele);
    Surface quadri = sdEllipsoid((p - vec3(.75, -1.5, .2)) * rotateZ(50.3) * rotateX(50.05), vec3(.5, .9, .6), pink);
    Surface isq = sdEllipsoid((p - vec3(.35, -1.5, .35)) * rotateZ(50.2) * rotateX(50.05), vec3(.45, .9, .4), pink);
    Surface m = unionOp(quadri, isq);
    modele = smoothUnion(m, modele, .1);
    //Knee
    Surface knee = sdRoundCone((p - vec3(0.65, -2.6, .05)) * rotateZ(6.2), .4, .4, .2, pink);
    modele = smoothUnion(knee, modele, .1);
    Surface knee_cap = sdRoundCone((p - vec3(0.7, -2.7, .35)) * rotateZ(6.2) * rotateX(50.5), .15, .3, .3, pink);
    modele = smoothUnion(knee_cap, modele, .05);
    //calf
    Surface calf = sdRoundCone((p - vec3(0.65, -3.9, .0)) * rotateZ(6.2), .3, .5, .8, pink);
    modele = smoothUnion(calf, modele, .2);

    return modele;
}

Surface eyes(vec3 p, Surface modele) {
    p.y *= 1./ smoothstep(.0,.15,abs(sin(iTime)));
    Surface eyeL = sdEllipsoid((p - vec3(-.3, 0.5, 1.3)) * rotateX(6.6), vec3(.13, .43, .1), black);
    Surface pupL = sdEllipsoid((p - vec3(-.3, 0.7, 1.3)) * rotateX(6.7), vec3(.08, .14, .05), white);
    eyeL = ssubstract(pupL, eyeL, .1);
    eyeL = unionOp(eyeL, pupL);
    Surface blueL = sdEllipsoid((p - vec3(-.3, 0.3, 1.42)) * rotateX(6.5), vec3(.08, .17, .05), blue);
    Surface subblue = sdEllipsoid((p - vec3(-.3, 0.4, 1.4)) * rotateX(6.5), vec3(.08, .17, .1), blue);
    blueL = unionOp(blueL, subblue);
    blueL = ssubstract(subblue, blueL, .01);
    eyeL = unionOp(eyeL, blueL);
    modele = ssubstract(eyeL, modele, .1);
    modele = unionOp(eyeL, modele);
    return modele;
}

Surface kirby(vec3 p, bool flex) {
    vec3 op = p;
    vec3 pbody = p;
    float wave = .5 + .35 * sin(iTime * 3.);
    pbody.y += 1.;
    pbody.y *= 1. + .1 * wave;
    pbody.y -= 1.;
    pbody *= 1.-.1 * wave;

    //body
    Surface body = sdSphere(pbody, 1.5, pink);

    //butt
    op = pbody;
    op.x = abs(p.x) -.9;
    Surface butt = sdSphere(op - vec3(-.45, -1.35, -.45), .6, pink);
    Surface modele = smoothUnion(body, butt, .3);

    //eyes
    op = pbody;
    op.x = abs(op.x) -.6;
    modele = eyes(op, modele);

    //blush
    op = pbody;
    op.x = abs(op.x) - 1.5;
    Surface blush = sdEllipsoid((op - vec3(-.75, -.0, 1.25)) * rotateY(.55), vec3(.15, .05, .05), blush_col);
    modele = ssubstract(blush, modele, .1);
    modele = smoothUnion(modele, blush, .1);
    //mouth
    Surface mouth = sdEllipsoid((pbody - vec3(.0, .75, 1.3)), vec3(.6, 1.2, .5), pink);
    Surface box_mouth= sdBox((pbody - vec3(.0, 0.95, 1.3)), vec3(.8, 1., .65), shoes_col);
    mouth = ssubstract(box_mouth, mouth, .5);
    modele = ssubstract(mouth, modele, .1);
    Surface mouthes = sdRoundCone((pbody - vec3(0.0, -.5, 1.5)) * rotateX(50.5), .15, .3, .3, black);
    mouth = unionOp(box_mouth, mouth);    
    //arms
    op = p;
    op.x = abs(op.x) - .8;
    modele = arms(op - vec3(0., -.1, 0.2), modele, flex);
    //legs
    op = p;
    op.x = abs(op.x) - .2;
    modele = legs(op - vec3(0., -.2, 0.), modele);
    //shoes
    op = p;
    op.x = abs(op.x) - 2.15;
    Surface shoeL = sdEllipsoid((op - vec3(-1.1, -5.1, .3)) * rotateY(10.), vec3(.6, 1., 1.), shoes_col);
    Surface box= sdBox((op - vec3(-1.1, -5.5, .3)) * rotateY(10.), vec3(.5, .6, 1.), shoes_col);
    shoeL = ssubstract(box, shoeL, .3);
    modele = smoothUnion(shoeL, modele, .1);
    //weights
    if(!flex)
        modele = weights(p - vec3(.0, .0, .1), modele);

    return modele;
}

Surface sdScene(vec3 p) {
    //change false by true if you want to see kirby flex without the weights
    Surface kirby = kirby(p, false);
    Surface floor = sdFloor(p - vec3(.0, -5., .0), grey * 1.3);
    return unionOp(kirby, floor);
}

vec3 computeNormal(vec3 p) {
    float e = 0.0005;
    return normalize(vec3(
        sdScene(vec3(p.x + e, p.y, p.z)).sd - sdScene(vec3(p.x - e, p.y, p.z)).sd,
        sdScene(vec3(p.x, p.y + e, p.z)).sd - sdScene(vec3(p.x, p.y - e, p.z)).sd,
        sdScene(vec3(p.x, p.y, p.z + e)).sd - sdScene(vec3(p.x, p.y, p.z - e)).sd
    ));
}

Surface rayMarching(vec3 ro, vec3 rd, float start, float end) {
    float depth = start;
    Surface co;
    const int step = 255;
    for(int i = 0; i < MAX_MARCHING_STEPS; ++i) {
        vec3 p = ro + depth * rd;
        co = sdScene(p);
        depth += co.sd;
        if(co.sd < PRECISION || depth > end)
            break;
    }
    co.sd = depth;
    return co;
}

vec3 blinn_phong(vec3 lightDirection, vec3 normal, vec3 rd) {
    vec3 lightColor = vec3 (1.,1.,1.);
    //ambient
    float ka = .4;
    vec3 ia = lightColor;
    vec3 ambient = ia * ka;

    //diffuse
    float kd = .5;
    vec3 id = lightColor;
    float diff = clamp(dot(lightDirection, normal), 0., 1.);
    vec3 diffuse = kd * diff * id;

    //specular
    float ks = .5;
    vec3 is = lightColor;
    float alpha = 10.;
    float dotRV = clamp(dot(reflect(-lightDirection, normal), -rd), 0., 1.);
    vec3 specular = ks * pow(dotRV, alpha) * is;

    return ambient + diffuse + specular;
}

mat2 rotate2d(float theta) {
  float s = sin(theta), c = cos(theta);
  return mat2(c, -s, s, c);
}

mat3 camera(vec3 cameraPos, vec3 lookAtPoint) {
	vec3 cd = normalize(lookAtPoint - cameraPos);
	vec3 cr = normalize(cross(vec3(0, 1, 0), cd));
	vec3 cu = normalize(cross(cd, cr));

	return mat3(-cr, cu, -cd);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv =  (fragCoord - .5 * iResolution.xy) / iResolution.y;
    vec3 backgroundColor = vec3(0.6);
    vec2 mouseUV = iMouse.xy / iResolution.xy;

    if (mouseUV == vec2(0.0)) 
        mouseUV = vec2(0.5);

    vec3 col = vec3(0.);
    vec3 ro = vec3(0., -1., 6.5);
    vec3 lp = vec3(0);

    //camera
    float cameraRadius = 2.;
    ro.yz = ro.yz * cameraRadius * rotate2d(mix(-PI/2., PI/20., mouseUV.y));
    ro.xz = ro.xz * rotate2d(mix(-PI, PI, mouseUV.x)) + vec2(lp.x, lp.z);
    vec3 rd = camera(ro, lp) * normalize(vec3(uv, -1));

    Surface co = rayMarching(ro, rd, MIN_DIST, MAX_DIST);
    if(co.sd > MAX_DIST) {
        col = backgroundColor;
    } else {
        vec3 p = ro + co.sd * rd;
        vec3 normal = computeNormal(p);

        //light
        vec3 lightPosition = vec3(0., 2., 3.);
        vec3 lightDirection = normalize(lightPosition - p);
        col = co.col * blinn_phong(lightDirection, normal, rd) + backgroundColor * .2;
    }
    fragColor = vec4(col, 1.);
}

// Public domain.

#define S(a, b, x) smoothstep(a, b, x)

vec4 Band(vec4 col, float dist, float start, float end, float blur)
{
    float step1 = S(start - blur, start + blur, dist);
    float step2 = S(end   + blur, end   - blur, dist);

    col.a = step1 * step2;  // intersection

    return col;
}

vec4 Rect(vec4 col, vec2 uv, float left, float right, float bot, float top,
          float blur)
{
    vec4 band1 = Band(col, uv.x, left, right, blur);
    vec4 band2 = Band(col, uv.y, bot,  top,   blur);

    col.a = band1.a * band2.a;  // intersection

    return col;
}

vec4 Carpet(vec4 col, vec4 figCol, vec2 uv, float blur)
{
    vec4 square  = Rect(figCol, uv, -.2, .2, -.2, .2, blur);

    vec4 hLineM  = Band(figCol, uv.y, -.073, .073, blur);

    vec4 vLineTR = Rect(figCol, uv, .35,   .5,  .2,   .5, blur);
    vec4 vLineTL = Rect(figCol, uv, -.5, -.35,  .2,   .5, blur);
    vec4 vLineBL = Rect(figCol, uv, -.5, -.35, -.5,  -.2, blur); 
    vec4 vLineBR = Rect(figCol, uv, .35,   .5, -.5,  -.2, blur); 
    vec4 hLineT  = Rect(figCol, uv, -.4,   .4, .35,   .5, blur);
    vec4 hLineB  = Rect(figCol, uv, -.4,   .4, -.5, -.35, blur);

    vec4 hLineTR = Rect(figCol, uv, .4,   1.5,   .2, .35, blur);
    vec4 hLineBR = Rect(figCol, uv, .4,   1.5, -.35, -.2, blur);
    vec4 hLineTL = Rect(figCol, uv, -1.5, -.4,   .2, .35, blur);
    vec4 hLineBL = Rect(figCol, uv, -1.5, -.4, -.35, -.2, blur);

    col = mix(col, square,  square.a);
    col = mix(col, hLineM,  hLineM.a);
    col = mix(col, vLineTR, vLineTR.a);
    col = mix(col, vLineTL, vLineTL.a);
    col = mix(col, vLineBL, vLineBL.a);
    col = mix(col, vLineBR, vLineBR.a);
    col = mix(col, hLineT,  hLineT.a);
    col = mix(col, hLineB,  hLineB.a);
    col = mix(col, hLineTR, hLineTR.a);
    col = mix(col, hLineBR, hLineBR.a);
    col = mix(col, hLineTL, hLineTL.a);
    col = mix(col, hLineBL, hLineBL.a);

    return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Move (0, 0) to the center of the screen.
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x / iResolution.y;  

    // Draw the image.
    vec4 col = vec4(.95, .87, .77, 1.);

    vec4 figCol = vec4(.37, .33, .3, 1.);
    float blur  = .001;

    vec2 pq = uv * 4.;  // scaled coords

    // Middle.
    for (int i = -10; i < 10; i += 2) {
        float fi = float(i) * 1. + mod(iTime * 2., 2.);

        vec4 c = Carpet(col, figCol, vec2(pq.x + fi, pq.y), blur);

        col = c;    
    }

    // Top.
    for (int i = -10; i < 10; i += 2) {
        float fi = float(i) * 1. - mod(iTime * 2., 2.);

        vec4 ct = Carpet(col, figCol, vec2(pq.x + fi + 3., pq.y + 1.3), blur);

        col = ct;
    }

    // Bottom.
    for (int i = -10; i < 10; i += 2) {
        float fi = float(i) * 1. - mod(iTime * 2., 2.);

        vec4 cb = Carpet(col, figCol, vec2(pq.x + fi + 3., pq.y - 1.3), blur);

        col = cb;
    }    

    // Return the color of this pixel.
    fragColor = col;
}

// Common


// Hashing function
// Returns a random number in [-1,1]
float Hash(float seed)
{
  return fract(sin(seed)*43758.5453);
}

// Cosine direction
vec3 Cosine(in float seed,in vec3 nor)
{
  float u=Hash(78.233+seed);
  float v=Hash(10.873+seed);

  // method 3 by fizzer: http://www.amietia.com/lambertnotangent.html
  float a=6.2831853*v;
  u=2.*u-1.;
  return normalize(nor+vec3(sqrt(1.-u*u)*vec2(cos(a),sin(a)),u));
}

// Rotation matrix around z axis
// a : Angle
mat3 rotate_z(float a)
{
  float sa=sin(a);float ca=cos(a);
  return mat3(ca,sa,0.,-sa,ca,0.,0.,0.,1.);
}

// Compute the ray
// m : Mouse position
// p : Pixel
// ro, rd : Ray origin and direction
void Ray(in vec2 m,in vec2 p,out vec3 ro,out vec3 rd)
{
  float a=3.*3.14*m.x;
  float le=3.8;

  ro=vec3(40.,0.,0.);
  ro*=rotate_z(3.*3.14*m.x);

  vec3 ta=vec3(0.,0.,1.);
  vec3 ww=normalize(ta-ro);
  vec3 uu=normalize(cross(ww,vec3(0.,0.,1.)));
  vec3 vv=normalize(cross(uu,ww));
  rd=normalize(p.x*uu+p.y*vv+le*ww);
}

// Main

const int Steps=1000;
const float Epsilon=.05;// Marching epsilon
const float T=.5;

const float rA=10.;// Maximum and minimum ray marching or sphere tracing distance from origin
const float rB=40.;

// Transforms
vec3 rotateX(vec3 p,float a)
{
  float sa=sin(a);
  float ca=cos(a);
  return vec3(p.x,ca*p.y-sa*p.z,sa*p.y+ca*p.z);
}

vec3 rotateY(vec3 p,float a)
{
  float sa=sin(a);
  float ca=cos(a);
  return vec3(ca*p.x+sa*p.z,p.y,-sa*p.x+ca*p.z);
}

vec3 rotateZ(vec3 p,float a)
{
  float sa=sin(a);
  float ca=cos(a);
  return vec3(ca*p.x+sa*p.y,-sa*p.x+ca*p.y,p.z);
}

vec3 translateX(vec3 p, float a)
{
  return vec3(p.x+a, p.y, p.z);
}

vec3 translateY(vec3 p, float a)
{
  return vec3(p.x, p.y+a, p.z);
}

vec3 translateZ(vec3 p, float a)
{
  return vec3(p.x, p.y, p.z+a);
}

// Smooth cubic falloff function
// x : distance
// R : radius
float falloff(float x,float R)
{
  float u=clamp(x/R,0.,1.);
  float v=(1.-u*u);
  return v*v*v;
}

// Primitives

// Point skeleton
// p : point
// c : center of skeleton
// e : energy associated to skeleton
// R : radius
float point(vec3 p,vec3 c,float e,float R)
{
  return e*falloff(length(p-c),R);
}

vec4 Segment(vec3 a, vec3 b, vec3 p, vec3 color)
{
    vec3 c = b-a;
    vec3 u = c/sqrt(pow(c.x,2.)+pow(c.y,2.)+pow(c.z,2.));
    float l = dot(p-a, u);
    float v;

    if(l < 0.)
      v = sqrt(pow(p.x-a.x,2.)+pow(p.y-a.y,2.)+pow(p.z-a.z,2.));
    else if(l > 0. && l < sqrt(pow(c.x,2.)+pow(c.y,2.)+pow(c.z,2.)))
      v = sqrt(pow(p.x-a.x,2.)+pow(p.y-a.y,2.)+pow(p.z-a.z,2.)-pow(l,2.));
    else
      v = sqrt(pow(p.x-b.x,2.)+pow(p.y-b.y,2.)+pow(p.z-b.z,2.));

    return vec4(falloff(v,0.5), color);
}

vec4 Circle(vec3 p, vec3 c, vec3 u, float R, vec3 color)
{
  vec3 n = p-c;
  float h = dot(n, u);
  float d = dot(n, n);
  float y = d - dot(h,h);
  y = R - sqrt(y);
  //float v=pow(p.x-c.x,2.)+pow(p.y-c.y,2.)+pow(p.z-c.z,2.) - R;

  return vec4(falloff(sqrt(y*y+h*h), 1.), color);
}

vec4 Sphere(vec3 p, vec3 c, float R, vec3 color)
{
  p.z=-p.z;
  float v=point(p,c,1.,R);

  return vec4(v, color);
}

vec4 Box(vec3 a, vec3 b, vec3 p, vec3 color)
{
  float dx;
  float dy;
  float dz;

  if(p.x > b.x)
      dx = p.x - b.x;
  else if(p.x < a.x)
      dx = -p.x + a.x;
  else
      dx = 0.;   
  if(p.y > b.y)
      dy = p.y - b.y;
  else if(p.y < a.y)
      dy = -p.y + a.y;
  else
      dy = 0.;   
  if(p.z > b.z)
      dz = p.z - b.z;
  else if(p.z < a.z)
      dz = -p.z + a.z;
  else
      dz = 0.;   

  float v = sqrt(pow(dx,2.) + pow(dy,2.) + pow(dz,2.));

  return vec4(falloff(v, 0.5), color);
}

// Operators

// Blending
// a,b : field function of the sub-trees
float Blend(float a,float b)
{
  return a+b;
}

vec4 ColorBlend(vec4 a,vec4 b)
{
  return vec4(a.x+b.x, ((a.yzw*a.x+b.yzw*b.x)/(a.x+b.x)));
}

// Union
// a : field function of left sub-tree
// b : field function of right sub-tree
float Union(float a,float b)
{
  return max(a,b);
}

vec4 ColorUnion(vec4 a,vec4 b)
{
  vec3 color;
  if(a.x>b.x)
    color = a.yzw;
  else
    color = b.yzw;
  return vec4(max(a.x,b.x), color);
}

float Intersection(float a,float b)
{
  return min(a,b);
}


// Potential field of the object
// p : point
vec4 Object(vec3 p)
{
  vec3 a = vec3(0., 0., -7.);
  vec3 b = vec3(4., 4., -6.);
  vec3 c = vec3(0., 0., 6.);
  vec3 d = vec3(4., 4., 7.);
  vec3 socleCenter1 = vec3(2.,2.,6.);
  vec3 socleCenter2 = vec3(2.,2.,-6);
  vec3 circleCenter1 = vec3(2.,2.,5.);
  vec3 circleCenter2 = vec3(2.,2.,-5.);
  vec3 ballCenter1 = vec3(2.,2.,2.);
  vec3 ballCenter2 = vec3(2.,2.,-2.);
  vec3 segment1 = vec3(2.,2.,5.);
  vec3 segment2 = vec3(2.,2.,-5.);
  vec3 n = vec3(0,0,1.);
  float R = 3.5;
  float RCircles = 2.;
  vec3 colorBall1 = vec3(0.6,0.1,0.);
  vec3 colorBall2 = vec3(0.3,0.,0.5);
  vec3 colorCircle1 = vec3(0.6,0.1,0.);
  vec3 colorCircle2 = vec3(0.3,0.,0.5);
  vec3 grey = vec3(0.8,0.8,0.8);

  circleCenter1.z += sin(0.7*iTime)*4.-5.;
  circleCenter2.z += cos(0.7*iTime)*4.+5.;

  ballCenter1.z += sin(0.7*iTime)*3.-3.;
  ballCenter2.z += cos(0.7*iTime)*3.+3.;
  ballCenter1.x += sin(5.*iTime)*2.;
  ballCenter1.y += cos(5.*iTime)*2.;
  ballCenter2.x -= cos(5.*iTime)*2.;
  ballCenter2.y -= sin(5.*iTime)*2.;

  RCircles = RCircles + sin(0.5*iTime)-0.5;

  vec4 socle1 = ColorUnion(Box(a,b,p,grey), Sphere(p,socleCenter1,R,grey));
  vec4 socle2 = ColorUnion(Box(c,d,p,grey), Sphere(p,socleCenter2,R,grey));
  vec4 frame = ColorUnion(socle1, socle2);
  vec4 circles = ColorBlend(Circle(p,circleCenter1,n,RCircles,colorCircle1),Circle(p,circleCenter2,n,RCircles,colorCircle2));
  vec4 balls = ColorBlend(Sphere(p,ballCenter1,2.,colorBall1),Sphere(p,ballCenter2,2.,colorBall2));
  vec4 w = ColorBlend(circles, balls);
  w = ColorBlend(w, Segment(segment1,segment2,p,grey));
  vec4 v = ColorUnion(frame, w);

  v.x = v.x-T;
  return v;
}

// Calculate object normal
// p : point
vec3 ObjectNormal(in vec3 p,out vec3 obj_color)
{
  float eps=.0001;
  vec3 n;
  vec4 V=Object(p);
  float v = V.x;
  obj_color = V.yzw;
  n.x=Object(vec3(p.x+eps,p.y,p.z)).x-v;
  n.y=Object(vec3(p.x,p.y+eps,p.z)).x-v;
  n.z=Object(vec3(p.x,p.y,p.z+eps)).x-v;
  return normalize(n);
}

// Trace ray using ray marching
// o : ray origin
// u : ray direction
// h : hit
// s : Number of steps
float Trace(vec3 o,vec3 u,float rB,out bool h,out int s)
{
  h=false;

  // Don't start at the origin, instead move a little bit forward
  float t=rA;

  for(int i=0;i<Steps;i++)
  {
    s=i;
    vec3 p=o+t*u;
    float v=Object(p).x;
    // Hit object
    if(v>0.)
    {
      s=i;
      h=true;
      break;
    }
    // Move along ray
    t+=Epsilon;
    // Escape marched far away
    if(t>rB)
    {
      break;
    }
  }
  return t;
}

// Trace ray using ray marching
// o : ray origin
// u : ray direction
// h : hit
// s : Number of steps
float SphereTrace(vec3 o,vec3 u,float rB,out bool h,out int s)
{
  h=false;

  // Don't start at the origin, instead move a little bit forward
  float t=rA;

  for(int i=0;i<Steps;i++)
  {
    s=i;
    vec3 p=o+t*u;
    float v=Object(p).x;
    // Hit object
    if(v>0.)
    {
      s=i;
      h=true;
      break;
    }
    // Move along ray
    t+=max(Epsilon,abs(v)/4.);
    // Escape marched far away
    if(t>rB)
    {
      break;
    }
  }
  return t;
}

// Ambient occlusion
// p : Point
// n : Normal
// a : Number of smaples
float AmbientOcclusion(vec3 p,vec3 n,int a)
{
  if(a==0){return 1.;}

  float ao=0.;

  for(int i=0;i<a;i++)
  {
    vec3 d=Cosine(581.123*float(i),n);

    int s;
    bool h;
    float t=SphereTrace(p,d,10.,h,s);
    if(!h){ao+=1.;}
    else if(t>5.)
    {
      ao+=1.;
    }
  }

  ao/=float(a);
  return ao;
}

// Background color
vec3 background(vec3 rd)
{
  return mix(vec3(.652,.451,.995),vec3(.552,.897,.995),rd.z*.5+.5);
}

float Light(vec3 p,vec3 n)
{
  // point light
  const vec3 lp=vec3(5.,10.,25.);

  vec3 l=normalize(lp-p);

  // Not even Phong shading, use weighted cosine instead for smooth transitions
  float diff=pow(.5*(1.+dot(n,l)),2.);

  bool h;
  int s;
  float t=SphereTrace(p+.1*n,l,100.,h,s);
  if(!h)
  {
    return diff;
  }
  return 0.;
}

float SmoothLight(vec3 p,vec3 n,int a)
{
  if(a==0)
  return 1.;

  // point light
  const vec3 lp=vec3(5.,10.,25.);

  vec3 l=normalize(lp-p);

  float lo=0.;

  for(int i=0;i<a;i++)
  {
    vec3 d=Cosine(581.123*float(i),n);
    d=normalize(l+d*.15);
    int s;
    bool h;
    float t=SphereTrace(p,d,10.,h,s);
    if(!h){lo+=1.;}
    else if(t>100.)
    {
      lo+=1.;
    }
  }

  lo/=float(a);
  return lo;

}

// Shading and lighting
// p : point,
// n : normal at point
vec3 Shade(vec3 p,vec3 n)
{
  vec3 c=.25+.25*background(n);
  c+=.15*AmbientOcclusion(p+.1*n,n,0)*vec3(1.,1.,1.);
  c+=.35*Light(p,n);
  return c;
}

// Shading with number of steps
vec3 ShadeSteps(int n)
{
  float t=float(n)/(float(Steps-1));
  return .5+mix(vec3(.05,.05,.5),vec3(.65,.39,.65),t);
}

// Picture in picture
// pixel : Pixel
// pip : Boolean, true if pixel was in sub-picture zone
vec2 Pip(in vec2 pixel,out bool pip)
{
  // Pixel coordinates
  vec2 p=(-iResolution.xy+2.*pixel)/iResolution.y;
  if(pip==true)
  {
    const float fraction=1./4.;
    // Recompute pixel coordinates in sub-picture
    if((pixel.x<iResolution.x*fraction)&&(pixel.y<iResolution.y*fraction))
    {
      p=(-iResolution.xy*fraction+2.*pixel)/(iResolution.y*fraction);
      pip=true;
    }
    else
    {
      pip=false;
    }
  }
  return p;
}

// Image
void mainImage(out vec4 color,in vec2 pxy)
{
  // Picture in picture on
  bool pip=true;

  // Pixel
  vec2 pixel=Pip(pxy,pip);

  // Mouse
  vec2 m=iMouse.xy/iResolution.xy;

  // Camera
  vec3 ro,rd;
  Ray(m,pixel,ro,rd);

  // Trace ray

  // Hit and number of steps
  bool hit;
  int s;

  float t=SphereTrace(ro,rd,100.,hit,s);

  // Position
  vec3 pt=ro+t*rd;

  // Shade background
  vec3 rgb=background(rd);

  if(hit)
  {
    // Compute normal
    vec3 obj_color;
    vec3 n=ObjectNormal(pt, obj_color);

    // Shade object with light
    rgb=Shade(pt,n) * obj_color;
  }

  // Uncomment this line to shade image with false colors representing the number of steps
  if(pip==true)
  {
    rgb=ShadeSteps(s);
  }

  color=vec4(rgb,1.);
}

float wavedx(vec2 position, vec2 direction, float time, float freq){
    float x = dot(direction, position) * freq + time;
    return exp(sin(x) - 1.0);
}

float getwaves(vec2 position){
    float iter = 0.0,phase = 6.0,speed = 2.0;
    float weight = 1.0,w = 0.0,ws = 0.0;   
    for(int i=0;i<5;i++){
        vec2 p = vec2(sin(iter), cos(iter));
        float res = wavedx(position,p,speed*iTime,phase);        
        w += res * weight; ws += weight;
        iter += 12.0; weight *=0.75; phase *= 1.18; speed *= 1.08;
    }
    return w / ws;
}
float sea_octave(vec2 uv,float choppy){
return getwaves(uv*choppy)+getwaves(uv); }

float noise3D(vec3 p){ 
   vec3 s = vec3(7, 157, 113);
   vec3 ip = floor(p); // Unique unit cell ID. 
   vec4 h = vec4(0.0, s.yz, s.y + s.z) + dot(ip, s);
    p -= ip; // Cell's fractional component. 
    p = p*p*(3.0 - 2.0*p); 
    h = mix(fract(sin(h)*43758.5453),fract(sin(h + s.x)*43758.5453),p.x); 
    h.xy = mix(h.xz, h.yw, p.y); 
    return mix(h.x, h.y, p.z);	
}
//borrowed from
//https://www.shadertoy.com/view/Xs33Df
float smaxP(float a, float b, float s){
    float h = clamp(0.5 + 0.5*(a - b)/s,0.0,1.0);
    return mix(b, a, h) + h*(1.0 - h)*s;
}
vec3 Freq=vec3(0.125,0.31,0.128),Amp= vec3(1.0,1.5,2.5);
vec2 path(float z){ return vec2(Amp.x*sin(z * Freq.x),
     Amp.y*cos(z * Freq.y) + Amp.z*(sin(z*Freq.z) - 1.0)); }
float map(vec3 p){
    float n=noise3D(p);
    float tx =n;
    vec3 q = p*0.35;//rock
    float h = dot(sin(q)*cos(q.yzx),vec3(0.222)) 
       + dot(sin(q*1.5)*cos(q.yzx*1.5),vec3(0.111));    
    float d = p.y+h*3.9;//some hills.   
    q = sin(p*0.5 + h);
    h = q.x*q.y*q.z; //tunnel walls.
    p.xy -= path(p.z);//detail wall
    float tnl = 1.5 - length(p.xy*vec2(.33, .66)) + (0.25 - tx*0.35);	
    return smaxP(d, tnl, 2.) - tx*.25 + tnl*.8;
}

#define STEP 36
#define FAR 35.0

float logBisectTrace(vec3 ro, vec3 rd){
    float t = 0., told = 0., mid, dn;
    float d = map(ro);
    float sgn = sign(d);
    for (int i=0; i<STEP; i++){
       if (sign(d) != sgn || d < 0.001 || t > FAR) break; 
        told = t;
        t += step(d,1.0)*(log(abs(d) + 1.1) - d) + d;        
        d = map(rd*t + ro); }
    // If a threshold was crossed without a solution, use the bisection method.
    if (sign(d) != sgn){    
        dn = sign(map(rd*told + ro));        
        vec2 iv = vec2(told, t); // Near, Far
        for (int ii=0; ii<5; ii++){ 
            mid = dot(iv,vec2(0.5));
            float d = map(rd*mid + ro);
            if (abs(d) < 0.001)break;
            iv = mix(vec2(iv.x, mid), vec2(mid, iv.y), step(0.0, d*dn));
        }
        t = mid;       
    }
    return min(t,FAR);
}

vec3 normal(vec3 p,float t){ vec2 e = vec2(-t, t);   
	return normalize(e.yxx*map(p + e.yxx) + e.xxy*map(p + e.xxy) + 
		 e.xyx*map(p + e.xyx) + e.y*map(p + e.y));   
} 
vec3 rotY(vec3 v, float a){float c = cos(a),s = sin(a);
    return vec3(c*v.x - s*v.z,v.y,s*v.x + c*v.z);}
//borrowed from 
//https://www.shadertoy.com/view/4ls3zM 
//
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy-0.5;
    uv.x*=iResolution.x/iResolution.y;

    float time = iTime*0.2;

    vec3 pos = (sin(time*0.14)*2.0+4.5)*vec3(sin(time*0.5), 0.0, cos(time*0.5));
    pos.z -= time;
    pos.y += 0.7 * sin(time * 0.2);
    float rot = -time*0.5;
    vec3 dir = normalize(vec3(uv, -0.6));
    dir = rotY(dir, rot);

    vec3 sun = vec3(-0.6, 0.5,-0.3); 
    float i = max(0.0, 1.2/(length(sun-dir)+1.0));
    vec3 col = vec3(pow(i, 1.9), pow(i, 1.0), pow(i, .8)) * 1.25;
    col = mix(col, vec3(0.0,0.39,0.62),(1.0-dir.y)*0.9);   

     if (dir.y > 0.0){//water suf
        float d = (pos.y-3.0)/dir.y;	
        vec2 wat = (dir * d).xz-pos.xz;
        d += sin(wat.x + time);
        wat = (dir * d).xz-pos.xz;     
        wat = wat * 0.1 + 0.2* texture(iChannel0,wat*0.01).xz;      
        col += sea_octave(wat,0.5)*0.6 * max(2.0/-d, 0.0);
    }
    else{//rock        
       vec3 ro=pos;ro.y+=12.0;

       float t = logBisectTrace(ro,dir);
       vec3 rock=vec3(0.0);
       if (t < FAR){   
        pos=ro+dir*t;
        t/=FAR;
        vec3 sn = normal(pos,0.1/(1.0 + t));
        float fre = clamp(1.0+dot(sun, sn),0.0,1.0); // Fresnel reflection.
        float Schlick = pow(1.0- max(dot(dir, normalize(dir + sun)),0.0), 5.0);
        fre *= mix(0.2, 1.0, Schlick);//Hard clay.
        float dif=dot(sn,sun)*0.2;    
        rock=(dif*texture(iChannel0,pos.xz*0.05).xyz+fre*fre*0.35)*col;
        float y=smoothstep(0.9,1.0,(1.0+dir.y)); 
        if(y>0.0) rock=mix(rock,col, y*t);     
        col=mix(rock,col,t);
      }	
      float f =(-dir.y-0.3+sin(time*0.05)*0.2)*0.3185;
	  f = clamp(f, 0.0, 1.0);		
	  col = mix(col,rock,f);
    }

    fragColor = vec4(col,1.0);
}

#define N 4
#define ITER 5
#define ITERNORM 4

vec2 conj(in vec2 a) {
    return vec2(a.x, -a.y);
}

vec2 cmul(in vec2 a, in vec2 b) {
    return vec2(a.x*b.x - a.y*b.y, a.x*b.y + a.y*b.x);
}

vec2 cdiv(in vec2 a, in vec2 b) {
    return cmul(a, conj(b))/(b.x*b.x + b.y*b.y);
}

vec2 p(in vec2 z, in vec2[N] points) {
    vec2 res = vec2(1., 0.);
    for (int i=0; i<N; i++) {
        res = cmul(res, z - points[i]);
    }
    return res;
}

vec2 pd(in vec2 z, in vec2[N] points) {
    vec2 res = vec2(0., 0.);
    for (int i=0; i<N; i++) {
        vec2 tmp = vec2(1., 0.);
        for (int j=0; j<N; j++) {
            if (i!=j) {
                tmp = cmul(tmp, z - points[j]);
            }
        }
        res += tmp;
    }
    return res;
}


vec2 newton(in vec2 z, in vec2[N] points) {
    return z - cdiv(p(z, points), pd(z, points));
}

vec2 s(in vec2 x) {
    vec2 z = 2.*(x/iResolution.xy - 0.5);
    z.x *= iResolution.x/iResolution.y;
    z *= mix(0.5, 1., 0.5*(sin(iTime) + 1.));
    return z;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 z = s(fragCoord);

    // points and colors
    vec2 points[N];
    points[0] = vec2(sin(2.02*iTime), 0.62*cos(1.2*iTime));
    points[1] = vec2(-1.76*sin(0.3*iTime), 1.3*cos(1.01*iTime));
    points[2] = vec2(1.33*sin(1.02*iTime), -0.3*cos(1.7*iTime));
    if (iMouse.z > 0.) {
        points[3] = s(iMouse.xy);
    } else {
        points[3] = vec2(0.11*sin(1.31*iTime), 0.05*cos(0.72*iTime));
    }
    vec3 color[N];
    color[0] = vec3(0.863,0.208,0.133);
    color[1] = vec3(0.851,0.797,0.620);
    color[2] = vec3(0.216,0.255,0.251);
    color[3] = vec3(0.741,0.765,0.780);
    int ind = 0;

    // iterations
    float dis = 2.;
    float scale = 1.;
    for (int i=0; i<ITER; i++) {
        z = newton(z, points);
        // calculate distance to nearest point
        for (int i=0; i<N; i++) {
            float cur = length(z - points[i]);
            if (cur < dis) {
                ind = i;
                dis = cur;
            }
        }
        if (dis < 0.05) {
            scale = mix(0.1, 1., float(clamp(i, 0, ITERNORM-1))/float(ITERNORM-1));
            break;
        }
    }

    // set color
    vec3 col = color[ind] * scale;
    fragColor = vec4(col, 1.);
}

#define cmul(a,b) vec2((a).x * (b).x - (a).y * (b).y, (a).x * (b).y + (a).y * (b).x)

#define pi 3.14159

#define r1 vec2(0.        ,  3.)
#define r2 vec2(2.85316955,  0.92705098)
#define r4 vec2(1.76335576, -2.42705098)
#define r5 vec2(-1.76335576, -2.42705098)


vec2 r3 = vec2(0.0,0.9);

#define c1 0x264653
#define c2 0x2a9d8f
#define c3 0xe9c46a
#define c4 0xf4a261
#define c5 0xe76f51


#define sqLength(z) z.x*z.x + z.y*z.y

#define rotSpeed 0.03


#define inv(z) vec2(z.x,-z.y)/(z.x*z.x + z.y * z.y)

#define f(z) cmul(cmul(cmul(cmul(z-r1,z-r2), z-r3), z-r4), z-r5);

int iterationNb;
//(x−a)(x−b)(x−c)(x−d)+(x−e)(x−b)(x−c)(x−d)
//(x−e)(x−a)(x−c)(x−d)+(x−e)(x−a)(x−b)(x−d)
//+(x−e)(x−a)(x−b)(x−c)
vec2 fp(vec2 z){
    vec2 zr1 = z-r1;
    vec2 zr2 = z-r2;
    vec2 zr3 = z-r3;
    vec2 zr4 = z-r4;
    vec2 zr5 = z-r5;
    return cmul(cmul(cmul(zr1,zr2),zr3),zr4) + 
           cmul(cmul(cmul(zr5,zr2),zr4),zr3) +
           cmul(cmul(cmul(zr4,zr1),zr3),zr4) + 
           cmul(cmul(cmul(zr4,zr1),zr2),zr4) + 
           cmul(cmul(cmul(zr5,zr1),zr2),zr3);
}


vec2 newtonReg(vec2 z){

    for (int i = 0; i < iterationNb; i +=1){
            vec2 fz = f(z);
            z -= cmul(fz, (inv(fp(z))));
    }
    return z;
}

/*
	h : given in radians.
	s : from 0 to 1.
	v : from 0 to 1.
*/
vec3 hsvToRgb(vec3 hsv){/* not understood but from wikipedia :
						https://fr.wikipedia.org/wiki/Teinte_Saturation_Valeur */
    float s = hsv.y;
    float v = hsv.z;

    vec3 rgb;
    hsv.x *= 180.f/pi;
    hsv.x = mod(hsv.x,360.f);
    int state = int(hsv.x/60.f)%6;
    float f = hsv.x/60.f - float(state);

    float l = (1.f - s);
    float m = (1.f - s * f);
    float n = (1.f - (1.f-f) * s);

    return v * vec3[](vec3(1,n,l),
                 vec3(m,1,l),
                 vec3(l,1,n),
                 vec3(l,m,1),
                 vec3(n,l,1),
                 vec3(1,l,m))[state];
}

vec3 hexToCol(int hex){
    return vec3(float((hex & 0xff0000) >> 16),
                float((hex & 0x00ff00) >> 8),
                float((hex & 0x0000ff)))/255.0;
}


vec3 coordToColor(vec2 z){    
    float r1Closeness = sqLength((z-r1));
    float r2Closeness = sqLength((z-r2));
    float r3Closeness = sqLength((z-r3));
    float r4Closeness = sqLength((z-r4));
    float r5Closeness = sqLength((z-r5));
    float closeness[5];
    if (r1Closeness < r2Closeness &&
        r1Closeness < r3Closeness &&
        r1Closeness < r4Closeness &&
        r1Closeness < r5Closeness){
        return hexToCol(c1);
    }
    if (r2Closeness < r3Closeness &&
        r2Closeness < r4Closeness &&
        r2Closeness < r5Closeness){
        return hexToCol(c2);
    }
    if (r3Closeness < r4Closeness &&
        r3Closeness < r5Closeness){
        return hexToCol(c3);
    }
    if (r4Closeness < r5Closeness){
        return hexToCol(c4);
    }
    return hexToCol(c5);
}

