const int MARCHSTEPS = 128;
const float MAX_DIST = 64.;
const float EPSILON = 0.01;
const vec3 pos = vec3(0.,0.0,-3.);
const vec3 posTime = vec3(0.,0.0,0.);
const vec3 lightDir = vec3(0.6,0.6,-0.6);
const float rotRate = 1.;
const float pi = 3.1415926535;

//https://www.iquilezles.org/www/articles/smin/smin.htm
vec2 sminCubic( float a, float b, float k )
{
    float h = max( k-abs(a-b), 0.0 )/k;
    float m = h*h*h*0.5;
    float s = m*k*(1.0/3.0); 
    return (a<b) ? vec2(a-s,m) : vec2(b-s,1.0-m);
}

//https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdCappedTorus(in vec3 p, in vec2 sc, in float ra, in float rb)
{
    p.x = abs(p.x);
    float k = (sc.y*p.x>sc.x*p.y) ? dot(p.xy,sc) : length(p.xy);
    return sqrt( dot(p,p) + ra*ra - 2.0*ra*k ) - rb;
}

//https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdVerticalCapsule( vec3 p, float h, float r )
{
  p.y -= clamp( p.y, 0.0, h );
  return length( p ) - r;
}

//https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdTriPrism( vec3 p, vec2 h )
{
  vec3 q = abs(p);
  return max(q.z-h.y,max(q.x*0.866025+p.y*0.5,-p.y)-h.x*0.5);
}

float sdSphere(vec3 p, float r)
{
    return length(p)-r;
}

float map(vec3 p)
{
    //Body
    vec3 pa = p-pos;
    p.y *= 1.1;
    float angle = atan(p.x, p.z);
    float centreDist = length(p.xz);
    float radMod = sin(angle*16.)*0.03;
    radMod += sin(angle*120.)*0.001;
    radMod *= (centreDist*centreDist);
    radMod -= (smoothstep(1.2, -.8, centreDist))*0.5;
    float d = sdSphere(p, 1. + radMod);

    //Stem
    //Domain bending from:
    float radMod2 = sin(angle*9.)*0.03;
    radMod2 += sin(angle*23.)*0.005;
    radMod2 *= smoothstep(0.02, 0.1, centreDist*centreDist);
    radMod2 += sin(-p.x*12.)*0.05+sin(p.y*5.-1.)*0.03+sin(p.z*25.)*0.01;
    d = sminCubic(d, sdVerticalCapsule(p, 1.1, 0.15+radMod2), 0.1).x;//sminCubic();

    //Eyes
    d = max(d, -sdTriPrism(vec3(abs(p.x),p.y,p.z)-vec3(0.3,0.2,-1.), vec2(0.15, 1.5)));

    //Mouth
    vec2 c = vec2(sin(0.5), cos(0.5));
    d = min(d, sdCappedTorus(vec3(p.x,-p.y,p.z)-vec3(0.,-0.2,-0.97), c, 0.35, 0.08));

    return d;
}

float trace(vec3 o, vec3 r) {
    float t = 0.0;

    for( int i=0;i<MARCHSTEPS;i++ ) {
        vec3 p = o + r * t;

        float d = map(p);

        if(t > MAX_DIST) {
            t = -1.0;
            break;
        }

        t += d;
    }
    return t;
}

vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        map(vec3(p.x + EPSILON, p.y, p.z)) - map(vec3(p.x - EPSILON, p.y, p.z)),
        map(vec3(p.x, p.y + EPSILON, p.z)) - map(vec3(p.x, p.y - EPSILON, p.z)),
        map(vec3(p.x, p.y, p.z  + EPSILON)) - map(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

mat3 rotY(float x)
{
    return mat3(cos(x), 0., sin(x),
                0., 1., 0.,
                -sin(x), 0., cos(x));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    vec3 r = normalize(vec3(uv, 2.0))*rotY(iTime*rotRate);
    vec3 o = pos*rotY(iTime*rotRate) + (iTime*posTime);
    vec3 t = vec3(trace(o, r));
    vec3 p = (o+r*t);
    vec3 nrm = estimateNormal(p);

    vec3 fog = vec3(0.);
    if(t != vec3(-1.))
    {
        fog = 1.0 / (1.0 + t * t * 0.05);
        fog *= clamp(dot(lightDir, nrm), 0., 1.) + vec3(0.1,0.1,0.15);
        fog *= vec3(1., .5, .02);
    }

    // Output to screen
    fragColor = vec4(fog,1.0);
}

