    float h = pow(sin(iTime/3.), 2.) * 0.1 + 0.002;
    float v = rod_light(uv, 0.1, h, 0.001);
    v = lin_to_srgb(v);

 	fragColor=vec4(v);
}

#define N 12.0
#define FADESPEED 2.0

#define EPSILON (0.001)
#define EQUAL1(a,b) (((a - EPSILON) < b) && ((a + EPSILON) > b))
#define EQUAL3(a,b) (EQUAL1(a.x, b.x) && EQUAL1(a.y, b.y) && EQUAL1(a.z, b.z))

vec2 rec2hex(vec2 rec)
{
	float temp = floor(rec.x + sqrt(3.0) * rec.y + 1.0);
	float q = floor((floor(2.0 * rec.x + 1.0) + temp) / 3.0);
	float r = floor((temp + floor(-rec.x + sqrt(3.0) * rec.y + 1.0)) / 3.0);
	return vec2(q,r);
}

vec3 axial_to_cube(vec2 hex)
{
	return vec3(hex.x, hex.y, -hex.x-hex.y);
}

float hex_length(vec3 hex) {
    return floor((abs(hex.x) + abs(hex.y) + abs(hex.z)) / 2.0);
}

float hex_distance(vec3 a, vec3 b) {
    return hex_length(a - b);
}

vec3 hex_round(vec3 h) {
    float q = floor(h.x);
    float r = floor(h.y);
    float s = floor(h.z);
    float q_diff = abs(q - h.x);
    float r_diff = abs(r - h.y);
    float s_diff = abs(s - h.z);
    if ((q_diff > r_diff) && (q_diff > s_diff)) {
        q = -r - s;
    } else if (r_diff > s_diff) {
        r = -q - s;
    } else {
        s = -q - r;
    }
    return vec3 (q, r, s);
}

vec2 rotate(in vec2 point, in float rads)
{
	float cs = cos(rads);
	float sn = sin(rads);
	return point * mat2(cs, -sn, sn, cs);
}

vec3 mod289(vec3 x) {
  return x - floor(x * (1.0 / 289.0)) * 289.0;
}

vec2 mod289(vec2 x) {
  return x - floor(x * (1.0 / 289.0)) * 289.0;
}

vec3 permute(vec3 x) {
  return mod289(((x*34.0)+1.0)*x);
}

float snoise(vec2 v)
  {
  const vec4 C = vec4(0.211324865405187,  // (3.0-sqrt(3.0))/6.0
                      0.366025403784439,  // 0.5*(sqrt(3.0)-1.0)
                     -0.577350269189626,  // -1.0 + 2.0 * C.x
                      0.024390243902439); // 1.0 / 41.0
// First corner
  vec2 i  = floor(v + dot(v, C.yy) );
  vec2 x0 = v -   i + dot(i, C.xx);

// Other corners
  vec2 i1;
  //i1.x = step( x0.y, x0.x ); // x0.x > x0.y ? 1.0 : 0.0
  //i1.y = 1.0 - i1.x;
  i1 = (x0.x > x0.y) ? vec2(1.0, 0.0) : vec2(0.0, 1.0);
  // x0 = x0 - 0.0 + 0.0 * C.xx ;
  // x1 = x0 - i1 + 1.0 * C.xx ;
  // x2 = x0 - 1.0 + 2.0 * C.xx ;
  vec4 x12 = x0.xyxy + C.xxzz;
  x12.xy -= i1;

// Permutations
  i = mod289(i); // Avoid truncation effects in permutation
  vec3 p = permute( permute( i.y + vec3(0.0, i1.y, 1.0 ))
		+ i.x + vec3(0.0, i1.x, 1.0 ));

  vec3 m = max(0.5 - vec3(dot(x0,x0), dot(x12.xy,x12.xy), dot(x12.zw,x12.zw)), 0.0);
  m = m*m ;
  m = m*m ;

// Gradients: 41 points uniformly over a line, mapped onto a diamond.
// The ring size 17*17 = 289 is close to a multiple of 41 (41*7 = 287)

  vec3 x = 2.0 * fract(p * C.www) - 1.0;
  vec3 h = abs(x) - 0.5;
  vec3 ox = floor(x + 0.5);
  vec3 a0 = x - ox;

// Normalise gradients implicitly by scaling m
// Approximation of: m *= inversesqrt( a0*a0 + h*h );
  m *= 1.79284291400159 - 0.85373472095314 * ( a0*a0 + h*h );

// Compute final noise value at P
  vec3 g;
  g.x  = a0.x  * x0.x  + h.x  * x0.y;
  g.yz = a0.yz * x12.xz + h.yz * x12.yw;
  return 130.0 * dot(m, g);
}

vec2 rand_loc(float i, float t)
{
	float offset = i * 11.0;
	float seed = t + (offset * 200.0);

	return vec2(snoise(vec2(seed, 11.0)),
		    snoise(vec2(seed, 88.765433)));

}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 position = ((fragCoord.xy / iResolution.xy) * 2.0) - 1.0;
	position.y *= iResolution.y/iResolution.x;

	float n = 7.0;
	vec2 pos = position * n;

	vec2 hpos = rec2hex(pos);
	vec3 orig_cube = axial_to_cube(hpos);
	vec3 orig_icube = hex_round(orig_cube);

	vec2 rhpos = rotate(hpos, iTime);
	vec3 cube = axial_to_cube(rhpos);
	vec3 h = abs(cube);
	float d = length(cube);

	vec3 color = vec3(0.0);

	color.rb = 1.0 - h.xz/n;
	float gfade = (cos(iTime) + 1.0) / 2.0;
	color.g = gfade * (1.0 - d/n);

	float bright = 0.0;

	for (float i = 0.0; i < 1.0; i += (1.0/N)) {
		float t = iTime + i;
		float fadeiTime = t * FADESPEED;
		float stepiTime = floor(fadeiTime);
		float stepfract = fract(fadeiTime);
		float stepprog = sin(stepfract * 3.14159265);
		stepprog = stepprog * stepprog;


		vec2 loc = rand_loc(i, stepiTime);
		vec2 loc_axial = rec2hex(loc * n);
		vec3 loc_hex = axial_to_cube(loc_axial);
		vec3 loc_ihex = hex_round(loc_hex);
		if (EQUAL3(loc_ihex, orig_icube)) {
			bright += stepprog;
		}
	}
	float cbright = clamp(bright, 0.0, 1.0);

#define R_SIZE  3.7
#define R_MOD   1.0
#define R_SPEED 1.666
#define R_WIDE  1.3
#define R_MARGIN (R_WIDE / 2.0)

	float r = R_SIZE + (R_MOD * sin(iTime * R_SPEED));
	if ((d > (r - R_MARGIN)) && (d < (r + R_MARGIN))) {
		vec3 grey = vec3((color.r + color.g + color.b) / 3.0);
		grey *= 1.1;
		color = mix(grey, color, gfade);
		//color.rb /= 3.0;
		//color = vec3(0.0);
	}

	color += vec3(cbright);

	fragColor = vec4(color, 1.0);
}

// Fork of "twisting log spiral" by morisil. https://shadertoy.com/view/slSGDV
// 2021-10-03 21:51:24

const float LOG_SCALE_MAX = 50.;
const float LOG_SCALE_PERIOD = .4;
const float ANGLE_SCALE = 4.;
const float ROTATION_SPEED = 10.;

#define wave(x) (sin(x) + 1.) / 2.

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 st =
        (2.* fragCoord - iResolution.xy)
        / min(iResolution.x, iResolution.y);
    float dist = length(st);
    float angle = atan(st.x, st.y);
    float logDist = log(dist);
    float logScale = sin(iTime * LOG_SCALE_PERIOD) * LOG_SCALE_MAX;
    float luma = wave(
        logDist * logScale + angle * ANGLE_SCALE - iTime * ROTATION_SPEED + sin(dist * 40.) + cos(angle * 40.)
    );
    fragColor = vec4(vec3(luma), 1.);
}

// CLICK MOUSE AND MOVE
void mainImage( out vec4 color, in vec2 o )
{
    o-=iResolution.xy/vec2(2.0);
    o/=iResolution.y/vec2(7.0);

    float x = (mod(abs(o.x),100.0));
    float y = (mod(abs(o.y),100.0));

    float mx = (-iResolution.x/2.0+iMouse.x)/iResolution.x*8.0;
    float my = (-iResolution.y/2.0+iMouse.y)/iResolution.y*8.0;

    float R = x*x+y*y;
    float L=pow(o.x+(mx)/5.0,2.0)+pow(o.y+(my)/5.0,2.0);

    float t = mod(iTime,x/y);
    float f = mod(iTime,y/x);

    float r1 = f*sin(x+sin(x+y)*L)+f*cos(y-sin(x-y)*L);
    float r2 = t*sin(x+sin(y-x)*L)+t*cos(y-sin(-y-x)*L);   

    color = vec4(
        mod(cos(r1),cos(r2)),
        mod(cos(r1),sin(r2)),
        max(sin(r2),sin(r1)),
        //mod(cos(r1),cos(r2)),
        //1.5*mod(cos(r1),sin(r2)),
        //abs(sin(r2)*sin(r1)),
    0)/sin(pow(R,0.5));
}

