const float pi = 3.1415926;
const float pi2 = 2.*pi;
const float rad = pi/180.;

///////////////////////////////////////////////////////////////////////////////
///////////////////////////////////////////////////////////////////////////////

// https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm

float sdSphere( vec3 p, float s )
{
  return length(p)-s;
}

float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
  vec3 pa = p - a, ba = b - a;
  float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
  return length( pa - ba*h ) - r;
}

float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); 
}

float opSmoothSubtraction( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
    return mix( d2, -d1, h ) + k*h*(1.0-h); 
}

///////////////////////////////////////////////////////////////////////////////
///////////////////////////////////////////////////////////////////////////////


mat2 rotate(float x){
    float c = cos(x);
    float s = sin(x);
    return mat2(c,s,-s,c);
}

float dist(vec3 p)
{
    float d = 100.;
    p.xz *= rotate(cos(iTime)*.4);    
    p -= vec3(0.,-0.5,0.);

    float t = cos(iTime*8.)*.05;
    float t2 = cos(iTime*9.)*.05;
    float t3 = cos(iTime*12.)*.05;
    float t4 = cos(iTime*9.)*.2+.8;
    float t5 = cos(iTime*3.)*.2-.2;

    vec3 pr = vec3(sign(p.x),1.0,1.0);  // repeat x
    vec3 pr1 = vec3(sign(p.x),1.+t2,1); // repeat x ani1
    vec3 pr2 = vec3(sign(p.x),1.+t3,1); // repeat x ani2   
    vec3 pa = p*pr2+vec3(0.,-cos(p.x)*0.7+.5,0.); // pos repeat

    vec3 ph = vec3(-2.7,-2.6 + t,0.5); // pos hand
    vec3 pf = p*pr+vec3(-0.7,1.9,0.2); // pos foot
    pf += vec3(0., cos(sign(p.x) + t3)-.5,0.);

    vec3 m1 = vec3(1.,cos(p.x*3.+.2 +t5 )*+t4,1.); // mouth ani 1
    vec3 m2 = vec3(0.,-.2,0.);                     // mouth ani 2
    vec3 pm = vec3(0.,.5,0.);                      // mouth 


    // body
    d = opSmoothUnion(d, sdCapsule(p*vec3(1.,pow(abs(p.y),.5)/1.5,1.), vec3(0.), vec3(0., 1.3, 0.0), 1.5), 1.5);

    // face
    d = opSmoothUnion(d, sdCapsule((p-vec3(0.,1.,0.))*vec3(1.,pow(abs(p.y),1.)/1.5,1.), vec3(0.,-.3,-0.1), vec3(0., .6, -0.1), 1.44), .0);

    //eye
    d = opSmoothUnion(d, sdCapsule(p*pr1, vec3(0.25, 1.2, -1.5), vec3(0.25, 1.0, -1.5), 0.15), .06);

    // eyebrow
    d = opSmoothUnion(d, sdCapsule(p*pr1 + vec3(0.,-0.1+cos(abs(p.x)*2.+2.-t)*.5+.5,0.), vec3(0.6, 1.6, -1.35), vec3(0.25, 1.6, -1.5), 0.1), .03);

    // arm
    d = opSmoothUnion(d, sdCapsule(pa, vec3(2.7, 2.6, -.5), vec3(.55, -.6, -.5), 0.25), .33);

    //clothes
    d = opSmoothUnion(d, sdCapsule(pa, vec3(2.3, 2.0, -.5), vec3(.55, -.6, -.5), 0.3), .01);

    // hand
    d = opSmoothUnion(d, sdSphere((pa+ph),  0.4), .2);

    // foot
    d = opSmoothUnion(d, opSmoothSubtraction(pf.y, sdSphere(max(vec3(-100.,0.,-100.), pf),  0.6), .2 ), .15);

    // mouth
    d = opSmoothSubtraction(sdCapsule((p-pm)/m1 -m2, vec3(0.2, 0.0, -1.5),  vec3(-0.2, 0.0, -1.5), 0.15), d, .06);

    // tooth
    d = opSmoothUnion(d, sdCapsule(p*pr, vec3(0.2, .6, -1.4), vec3(0.2, 0.45, -1.4), 0.06), .02);

    // ear
    d = opSmoothUnion(d, sdSphere((p*pr +vec3(-1.7,-1.4 + t*.1, .2)) * vec3(.4,.4, 1.),  0.1), .13);


    return d;
}

vec3 getNormal(vec3 p)
{
    vec2 d = vec2(0., 0.01);
    float x = dist(p-d.yxx);
    float y = dist(p-d.xyx);
    float z = dist(p-d.xxy);
    return normalize(vec3(x,y,z)-dist(p));
}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv =          ( cross(cu,cw) );
    return mat3( cu, cv, cw );
}


