void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;

    uv = 2.*uv;


    // ANALYTICAL FORMULAS FOR "LINE" LIGHTS ON A PLANE

    // We consider here a line composed of continuously many light sources,
    // which each radiate either uniformly in all directions ("monopoles"),
    // or which are somewhat directional ("dipoles", more realistic for an area light)

    // The formulas below are derived by integrating the contribution of each
    // infinitesimal source along the line.

    // The line is assumed to be at x=0 and y in [0,1].

    // Intensity radiated from "monopole" sources
    // The contribution of the light source at (0,t) to the intensity at (x,y)
    // is assumed to be : 
    // dt / sqrt(x^2 + (y-t)^2)
    //
    // In which case we obtain :
    // \int_0^1 \frac{dt}{\sqrt{x^2 + (y-t)^2}} = \asinh(\frac{1-y}{x}) - \asinh(\frac{-y}{x})
    float umax = (1.-uv.y)/uv.x, umin = -uv.y/uv.x;
    float inten_monopole = asinh(umax) - asinh(umin);

    // Intensity radiated from "dipole" sources
    // The contribution of the light source is assumed to be :
    // x * dt / (x^2 + (y-t)^2)^(3/2)
    // 
    // Then we obtain :
    // (the brackets in the RHS mean we take the difference of the values at the two given points)
    // \int_0^1 \frac{x dt}{(x^2+(y-t)^2)^{3/2}} = \frac{1}{x} \left[ \frac{u}{\sqrt{1+u^2}} \right]_{u_{min}}^{u_{max}}
    // where u_{min} = -y/x and u_{max} = (1-y)/x.
    float inten_dipole = (umax/sqrt(1.+umax*umax) - umin/sqrt(1.+umin*umin))/uv.x;



    float inten = uv.x < 0. ? inten_monopole : inten_dipole;
    inten = abs(inten) * (0.5+0.2*sin(10.*iTime)+0.1*sin(16.18*iTime));


    vec3 col = vec3(1.,0.25,0.05) * inten;
    col = col / (1. + col); // Tone mapping
    col = sqrt(col); // Gamma

    col = abs(uv.x) > 0.02 ? col : vec3(0,0,1);
    fragColor = vec4(col, 1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time=iTime*.5;
    vec2 uv = -1.0 + 2.0 * fragCoord/iResolution.xy;

    uv.x *= iResolution.x/iResolution.y;

	float c=length(uv);

    float r,g,b;

    float d=3.+sin(time*.5)*3.5;

    c=smoothstep(.125,.95,c);

    r=sin(time+(c+sin(1.+time+uv.y*.5))*d);

    g=sin(time+(c+sin(1.75+time+uv.y*.5))*d);

    b=sin(time+(c+sin(2.25+time+uv.y*.5))*d);



    fragColor = vec4(vec3(r,g,b)+(c-.5+sin(time*120.)*.04),1.0);
}

// from https://www.shadertoy.com/view/WtGXDD
mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}


// from https://www.shadertoy.com/view/WtGXDD
vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}


// 2d rotation matrix
vec2 rot2D(vec2 v, float a)
{
    mat2x2 rot = mat2x2(cos(a), -sin(a), sin(a), cos(a));
    return v * rot;
}


// takes a uv coord and mouse pos and converts it to the rays direction
vec3 Transform(vec2 uv, vec2 m, float roll)
{
    vec3 ro = vec3(0, 1, -1) * 20.;
    ro.yz *= Rot(-m.y*3.14+1.);
    ro.xz *= Rot(-m.x*6.2831);
    vec3 rd = GetRayDir(rot2D(uv, roll), ro, vec3(0,0.,0), 1.);
    return rd;
}


// from another shadertoy shader
vec3 hash( vec3 p )
{
	p = vec3( dot(p,vec3(127.1,311.7, 74.7)),
			  dot(p,vec3(269.5,183.3,246.1)),
			  dot(p,vec3(113.5,271.9,124.6)));

	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

// from another shadertoy shader
vec4 noised( in vec3 x )
{
    // grid
    vec3 i = floor(x);
    vec3 w = fract(x);

    // cubic interpolant
    vec3 u = w*w*(3.0-2.0*w);
    vec3 du = 6.0*w*(1.0-w);

    // gradients
    vec3 ga = hash( i+vec3(0.0,0.0,0.0) );
    vec3 gb = hash( i+vec3(1.0,0.0,0.0) );
    vec3 gc = hash( i+vec3(0.0,1.0,0.0) );
    vec3 gd = hash( i+vec3(1.0,1.0,0.0) );
    vec3 ge = hash( i+vec3(0.0,0.0,1.0) );
	vec3 gf = hash( i+vec3(1.0,0.0,1.0) );
    vec3 gg = hash( i+vec3(0.0,1.0,1.0) );
    vec3 gh = hash( i+vec3(1.0,1.0,1.0) );

    // projections
    float va = dot( ga, w-vec3(0.0,0.0,0.0) );
    float vb = dot( gb, w-vec3(1.0,0.0,0.0) );
    float vc = dot( gc, w-vec3(0.0,1.0,0.0) );
    float vd = dot( gd, w-vec3(1.0,1.0,0.0) );
    float ve = dot( ge, w-vec3(0.0,0.0,1.0) );
    float vf = dot( gf, w-vec3(1.0,0.0,1.0) );
    float vg = dot( gg, w-vec3(0.0,1.0,1.0) );
    float vh = dot( gh, w-vec3(1.0,1.0,1.0) );

    // interpolations
    return vec4( va + u.x*(vb-va) + u.y*(vc-va) + u.z*(ve-va) + u.x*u.y*(va-vb-vc+vd) + u.y*u.z*(va-vc-ve+vg) + u.z*u.x*(va-vb-ve+vf) + (-va+vb+vc-vd+ve-vf-vg+vh)*u.x*u.y*u.z,    // value
                 ga + u.x*(gb-ga) + u.y*(gc-ga) + u.z*(ge-ga) + u.x*u.y*(ga-gb-gc+gd) + u.y*u.z*(ga-gc-ge+gg) + u.z*u.x*(ga-gb-ge+gf) + (-ga+gb+gc-gd+ge-gf-gg+gh)*u.x*u.y*u.z +   // derivatives
                 du * (vec3(vb,vc,ve) - va + u.yzx*vec3(va-vb-vc+vd,va-vc-ve+vg,va-vb-ve+vf) + u.zxy*vec3(va-vb-ve+vf,va-vb-vc+vd,va-vc-ve+vg) + u.yzx*u.zxy*(-va+vb+vc-vd+ve-vf-vg+vh) ));
}


vec2 RaySphere(vec3 ro, vec3 rd, vec3 sc, float r)
{
    vec3 offset = ro - sc;
    float b = 2. * dot(offset, rd);
    float c = dot(offset, offset) - r * r;

    float discriminant = b * b - 4. * c;

    if (discriminant > 0.)
    {
        float s = sqrt(discriminant);
        float dstToSphereNear = max(0., (-b - s) / 2.);
        float dstToSphereFar = (-b + s) / 2.;

        if (dstToSphereFar > 0.)
        {
            return vec2(dstToSphereNear, dstToSphereFar - dstToSphereNear);
        }
    }
    return vec2(999999999999999999., 0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * 0.5) / iResolution.y * 1.75;
    vec2 m = iMouse.xy / iResolution.xy;

    if (iMouse.xy == vec2(0.))
    {
        m = vec2(1., 0.4);
    }

    float roll = 0.;

    vec3 rd = Transform(uv, m, roll);
    vec3 ro = vec3(0., 1., iTime * 0.5);

    vec3 sun_dir = normalize(vec3(0.2, 0.7, -0.2));

    vec3 col = vec3(0.);

    col = mix(col, vec3(0.95, 0.75, 0.5) * 2., pow(max(dot(sun_dir, rd), 0.), 275.));

    vec2 dstNebula = RaySphere(ro, rd, vec3(0., 0., 4.), 3.);

    if (dstNebula.y > 0.)
    {
        float transmission = 1.;
        float step_size = dstNebula.y / 16.;
        vec3 step_sizev = rd * step_size;

        vec3 total_color = vec3(0.);
        vec3 p = ro + rd * dstNebula.x;

        for (int i = 0; i < 15; i++)
        {
            p += step_sizev;

            float colorBlend = noised(p * 0.5 + 20. + iTime * 0.75).x * 0.5 + 0.5;
            float density = smoothstep(0., 0.7, max(noised(p).x + noised(p * 2. + 200. + iTime * 0.25).r * 0.5, 0.) / 1.5);
            total_color += mix(vec3(0.8, 0.3, 0.2), vec3(0., 0., 0.9), smoothstep(0., 1., colorBlend)) * transmission * 0.3;
            transmission *= exp(-density * step_size * 6. * smoothstep(3., 2.2, length(p - vec3(0., 0., 4.))));
        }
        col = col * transmission + total_color * 0.7 * (1. - transmission);

        float dst_sun;
        dst_sun = RaySphere(ro, rd, vec3(0.1, 0., 3.), 0.05).x;
        if (dst_sun < 9999999.) col += 4. * transmission;
         dst_sun = RaySphere(ro, rd, vec3(1.7, 0.7, 5.), 0.05).x;
        if (dst_sun < 9999999.) col += 4. * transmission;
        dst_sun = RaySphere(ro, rd, vec3(-0.5, -1.8, 5.3), 0.05).x;
        if (dst_sun < 9999999.) col += 4. * transmission;
        dst_sun = RaySphere(ro, rd, vec3(1.5, 1.73, 4.3), 0.05).x;
        if (dst_sun < 9999999.) col += 4. * transmission;
        dst_sun = RaySphere(ro, rd, vec3(0.4, 1., 4.3), 0.05).x;
        if (dst_sun < 9999999.) col += 4. * transmission;
        dst_sun = RaySphere(ro, rd, vec3(-1.5, -0.72, 2.3), 0.05).x;
        if (dst_sun < 9999999.) col += 4. * transmission;
        dst_sun = RaySphere(ro, rd, vec3(1.2, -0.3, 4.63), 0.05).x;
        if (dst_sun < 9999999.) col += 4. * transmission;
        dst_sun = RaySphere(ro, rd, vec3(-0.82, 0.185, 4.2), 0.05).x;
        if (dst_sun < 9999999.) col += 4. * transmission;
        dst_sun = RaySphere(ro, rd, vec3(0.84, 1.4, 5.1), 0.05).x;
        if (dst_sun < 9999999.) col += 4. * transmission;
        dst_sun = RaySphere(ro, rd, vec3(-0.274, -1.63, 2.84), 0.05).x;
        if (dst_sun < 9999999.) col += 4. * transmission;
        //col = vec3(transmission);
    }

    float a = 2.51f;
    float b = 0.03f;
    float c = 2.43f;
    float d = 0.59f;
    float e = 0.14f;
    col =  clamp((col*(a*col+b))/(col*(c*col+d)+e), 0., 1.);

    fragColor = vec4(col,1.0);
}

