float cross2(vec2 p, vec2 q)
{
    return p.x * q.y - p.y * q.x;
}

bool intri(vec2 uv, vec2 v1, vec2 v2, vec2 v3)
{
    // https://mathworld.wolfram.com/TriangleInterior.html
    // http://www.sunshine2k.de/coding/java/pointInTriangle/pointInTriangle.html
    vec2 w1 = v2 - v1;
    vec2 w2 = v3 - v1;
    float d = determinant(mat2(w1, w2));
    // check for d ≈ 0.0 ?
    float s = determinant(mat2(uv - v1, w2)) / d;
    float t = determinant(mat2(w1, uv - v2)) / d;
    return s >= 0.0 && t >= 0.0 && (s + t) <= 1.0;
}

bool inreg(vec2 uv, vec2 c, float n, float R, float theta)
{
    // break-up regular polygon into triangles
    float dt = radians(360.0 / n);
    for (float i = 0.0, j = 1.0; i < n; i++, j++)
    {
        vec2 a = R * vec2(cos(dt * i + theta), sin(dt * i + theta)) + c;
        vec2 b = R * vec2(cos(dt * j + theta), sin(dt * j + theta)) + c;
        if (intri(uv, a, b, c))
            return true;
    }
    return false;
}

vec2 uv_to_hex(vec2 uv, float R) {
    // get central hex coordinate
    float r = R * COS30;
    float theta = radians(30.0);
    vec2 hvec = vec2(2.0 * r, 0.0);
    vec2 kvec = vec2(r, 1.5 * R);
    mat2 b = mat2(hvec, kvec);
    vec2 cell = b * round(inverse(b) * uv);
    bool inhex = inreg(uv, cell, 6.0, R, radians(theta));
    // adjust hex coordinate due to overlap
    if (!inhex)
        if (cross2(vec2(r, 0.5 * R), cell - uv) < 0.)
            cell += (uv.x > cell.x) ? kvec : -hvec;
        else
            cell += (uv.x > cell.x) ? hvec : -kvec;
    return cell;
}

vec3 hex_display(vec2 uv, vec2 pos, float chr, float R, float B, vec3 col) {
    // uv  the uv
    // pos the centreal display position
    // chr the character code to display
    // R   the hexagon circumradius
    // B   the border width
    // col the default color
    //
    //     0    5    8  -
    //    1         9    |
    //   2    6    A      > display bit mapping
    //  3         B      |
    // 4    7    C      -
    //
    // A a 16127          n 14521        0 16255  _ 08481
    // b   12735          o 14777        1 15873  - 18569
    // C c 09087 10681  P   03839        2 12283  : 16425
    //   d 16313          q 16071        3 16363  ! 11777
    // E   11263          r 18617        4 16015  ? 20219
    // F   19199        S   13287        5 15343  . 08193
    // G g 14719          t 10687        6 15359  ' 16391
    // H h 16063 14527  U u 16191 14649  7 15939  " 17927
    //   i 12289        V   14617        8 16383  [ 16767
    // J   16145          w 23709        9 16079  ] 16193
    // K   14015        X   14007
    // L   8511           y 16263
    // M   15549        Z   10227

    float r = R * COS30;
    float theta = radians(30.0);
    float n = chr;
    // set display according to bit index
    for (int i = 0; i < 13; i++) {
        float q = floor(n / 2.0);
        if (mod(q, 2.0) == 1.0) {
            float ir = 0.0;
            float ic = 0.0;
            switch (i) {
                case  0:
                case  5:
                case  8:
                    ir = +2.0;
                    break;
                case  1:
                case  9:
                    ir = +1.0;
                    break;
                case  3:
                case 11:
                    ir = -1.0;;
                    break;
                case  4:
                case  7:
                case 12:
                    ir = -2.0;
                    break;
            }
            /**/ if (i <= 4) ic = -1.0;
            else if (i >= 8) ic = +1.0;
            float dx = ic * 2.0 * r + ir * r;
            float dy = ir * 1.5 * R;
            if (inreg(uv, pos + vec2(dx, dy), 6.0, R - B, theta)) {
                col = vec3(ON);
                break;
            }
        }
        n = q;
    }
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.y;

    float R = 1.0 / N;
    float r = R * COS30;
    float B = R * WIDTH;

    vec2 hex = uv_to_hex(vec2(iResolution.x / iResolution.y / 2.0, 0.5), R);

    vec3 col = (
        inreg(uv, uv_to_hex(uv, R), 6.0, R - B, radians(30.0)) ? 
        vec3(OFF) : 
        0.5 + 0.5 * cos(iTime + uv.xyx + vec3(0, 2, 4))
    );
    float code = hex_codes[int(mod(float(iFrame / 30), float(hex_codes.length())))];
    col = hex_display(uv, hex, code, R, B, col);

    fragColor = vec4(col, 1.0);
}

#define RES    (iResolution.xy)
#define MINRES (min(RES.x, RES.y))

const vec3 UP  = vec3(0.0, 1.0, 0.0);
const vec3 ONE = vec3(1.0, 1.0, 1.0);
const float Pi = 3.14159265359;

float gTime    = 0.0;
vec3  gPosCam  = vec3(0.0, 4.0, -2.0);
vec3  gPosSph1 = UP;
float gSizSph1 = 1.0;

const float gZoom = 0.8;

float Pow2(float x) {return x*x;}

vec3 posSph1(float time, float noiseAmt) {

    vec3 noise = texture(iChannel1, vec2(time * 0.051, time * 0.05), 0.0).rgb;

    return vec3(cos(time * 1.3), 0.0, sin(time * 0.7)) * 9.0 + UP + noise * noiseAmt;
}

void composeScene(float time) {
    gTime    = time;
    gPosSph1 = posSph1(gTime, 0.0);

    gPosCam  = posSph1(gTime + 0.0, 0.0);
    gPosCam.xz  += normalize(gPosCam).xz * 6.0;
    gPosCam  *= 1.0;
    gPosCam  += UP * 3.0;
}

vec3 sky(in vec3 rd) {
    vec3 ret = rd * 0.5 + 0.5;
    ret.rg *= sin(rd.y * 20.0) * 0.2 + 0.8;
    ret.gb *= sin(atan(rd.z, rd.x) * 10.0) * 0.3 + 0.7;
    ret  = ONE * length(ret);
    return ret;
}

vec3 shootRay(in vec3 ro, in vec3 rd) {
    if (rd.y > -0.001) {
        return sky(rd);
    }

    vec2 planePt2  = -vec2(rd.xz * ro.y / rd.y) + ro.xz;
    vec3 planePt   = vec3(planePt2.x, 0.0, planePt2.y);
    float planeDst = length(planePt.xz);
    float planeAng = atan(planePt.z, planePt.x);
    float camDist  = length(planePt2 - ro.xz);
    float blur     = smoothstep(0.0, 80.0, camDist);

    const float txLim = 8.8;
    float pa = planeAng;
    float pd = planeDst;
    if (pd < txLim) {
        pa += Pow2((pd - txLim) * 0.2);
    }
    float planeCol = smoothstep(-blur, blur, sin(pa * 10.0) * sin(log(pd) * 4.0)) * (0.4 * (1.7 - blur)) + 0.6;


    planeCol *= smoothstep(3.0, 5.0, camDist) * 0.4 + 0.6;

    vec3 ret = vec3(planeCol);

    ret = mix(ret, sky(rd), smoothstep(20.0, 1160.0, planeDst));

    vec2 planePt3  = -vec2(rd.xz * (ro.y - 1.0) / rd.y) + ro.xz;

    float shadwSum = 0.0;
    float snakeSum = 0.0;
    for (float dt = -6.0; dt < 1.0; dt += 0.02) {
        float f = smoothstep(-6.0, 0.0, dt);
        vec2 pos = posSph1(gTime + dt, 1.0 - f).xz;
        float rad = f * gSizSph1;
        rad *= sin(rad * 40.0 + gTime) * 0.1 + 0.9;
        snakeSum += smoothstep(0.5, 0.1, abs(length(planePt3 - pos) - rad));
        shadwSum += smoothstep(rad, 0.0, abs(length(planePt2 - pos) - rad * 0.5));
    }

    ret *= 0.7 + 0.3 * smoothstep(3.0, 0.0, shadwSum);
    ret = mix(ret, vec3(0.0, 0.0, 0.3), 0.9 * smoothstep(0.0, 11.0, snakeSum));

    return ret;
}



// From TinyTexel
// https://www.shadertoy.com/user/TinyTexel
// https://www.shadertoy.com/view/Wt3fzB
// tc ∈ [-1,1]² | fov ∈ [0, π) | d ∈ [0,1]
vec3 PaniniProjection(vec2 tc, float fov, float d)
{
    float d2 = d*d;

    {
        float fo = Pi / 2.0 - fov * 0.5;

        float f = cos(fo)/sin(fo) * 2.0;
        float f2 = f*f;

        float b = (sqrt(max(0.0, Pow2(d+d2)*(f2+f2*f2))) - (d*f+f)) / (d2+d2*f2-1.0);

        tc *= b;
    }

    /* http://tksharpless.net/vedutismo/Pannini/panini.pdf */
    float h = tc.x;
    float v = tc.y;

    float h2 = h*h;

    float k = h2/Pow2(d+1.0);
    float k2 = k*k;

    float discr = max(0.0, k2*d2 - (k+1.0)*(k*d2-1.0));

    float cosPhi = (-k*d+sqrt(discr))/(k+1.0);
    float S = (d+1.0)/(d+cosPhi);
    float tanTheta = v/S;

    float sinPhi = sqrt(max(0.0, 1.0-Pow2(cosPhi)));
    if(tc.x < 0.0) sinPhi *= -1.0;

    float s = inversesqrt(1.0+Pow2(tanTheta));

    return vec3(sinPhi, tanTheta, cosPhi) * s;
}

void mainImage(out vec4 RGBA, in vec2 XY)
{
    composeScene(iTime * 0.6);

    float pixelSize = 1.0 / MINRES / gZoom;
    vec2  xy        = (XY - (RES * 0.5)) * pixelSize * 2.0;

    vec3 camPt      = gPosCam;
    vec3 lukPt      = gPosSph1;
    vec3 cam2luk    = lukPt - camPt;
    vec3 camFw      = normalize(cam2luk);
    vec3 camRt      = normalize(cross(camFw, UP));
    vec3 camUp      = normalize(cross(camRt, camFw));
    mat3 wldFrmCam  = mat3(camRt, camUp, camFw);

    // Ray direction in camera space
    vec2 ms = vec2(0.5);
    if (length(iMouse.xy) > 50.0) {
        ms = iMouse.xy / RES;
    }
    float paniniFOV = mix(0.2, Pi/4.0, ms.x);
    float paniniD   = mix(0.0, 2.0, ms.y);
    vec3 rayDirCam  = PaniniProjection(xy, paniniFOV, paniniD);

    // Ray direction in world space
    vec3 rayDir     = wldFrmCam * rayDirCam;

    vec3 rgb        = shootRay(camPt, rayDir);

    RGBA = vec4(rgb, 1.0);
}

#define ANGLE_ONE 0.628319
#define ANGLE_TWO  1.88496
#define PI         3.14159
#define GOLDEN_RATIO 1.61803398875
#define N 11
#define scale (.02*(sin(iTime/3.)+2.))
float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

float sdTriangle( in vec2 p, in vec2 p0, in vec2 p1, in vec2 p2 )
{
    vec2 e0 = p1-p0, e1 = p2-p1, e2 = p0-p2;
    vec2 v0 = p -p0, v1 = p -p1, v2 = p -p2;
    vec2 pq0 = v0 - e0*clamp( dot(v0,e0)/dot(e0,e0), 0.0, 1.0 );
    vec2 pq1 = v1 - e1*clamp( dot(v1,e1)/dot(e1,e1), 0.0, 1.0 );
    vec2 pq2 = v2 - e2*clamp( dot(v2,e2)/dot(e2,e2), 0.0, 1.0 );
    float s = sign( e0.x*e2.y - e0.y*e2.x );
    vec2 d = min(min(vec2(dot(pq0,pq0), s*(v0.x*e0.y-v0.y*e0.x)),
                     vec2(dot(pq1,pq1), s*(v1.x*e1.y-v1.y*e1.x))),
                     vec2(dot(pq2,pq2), s*(v2.x*e2.y-v2.y*e2.x)));
    return -sqrt(d.x)*sign(d.y);
}

vec3 sdShape( in vec2 p, in vec2 p0, in vec2 p1, in vec2 p2, in vec3 shapeCol, in vec3 lineCol, in float thickness ) {
    vec3 col = lineCol;

    if(sdTriangle(p, p0, p1, p2) < 0.) col = shapeCol;

    if(min(sdSegment(p, p0, p1), sdSegment(p, p0, p2)) < thickness) col = lineCol;

    return col;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy - .5;
    uv.x *= iResolution.x / iResolution.y;
    uv *= scale;
    uv += vec2(sin(iTime/10.), cos(iTime/10.)) *0.1;
    vec3 col = vec3(1);

    int v = clamp(int(5.0 * (atan(uv.x, uv.y) + PI) / PI), 0, 9);
    float offset = ANGLE_ONE /2. + float(v)/10. * PI * 2.;



    vec2 a = vec2(0.0, 0.0);
    vec2 b = a + vec2(sin(ANGLE_ONE / 2. + PI + offset), cos(ANGLE_ONE / 2. + PI + offset)) * 0.3;
    vec2 c = a + vec2(sin(PI - ANGLE_ONE / 2. + offset), cos(PI - ANGLE_ONE / 2. + offset)) * 0.3;

    if(v%2==0) {vec2 _ = b; b = c; c = _;}
    float d;
    bool blue = false;

    for(int i=0;i<N;i++) {
        if(blue)
        {
            vec2 q = b + (a - b) / GOLDEN_RATIO;
            vec2 r = b + (c - b) / GOLDEN_RATIO;
            if(sdTriangle(uv, r, c, a) < 0.) { b = c; c = a; a = r; }
            else if(sdTriangle(uv, r, q, a) < 0.) { blue = false; b = q; c = a; a = r; }
            else { c = b; b = r; a = q; }
        } else {
            vec2 p = a + (b - a) / GOLDEN_RATIO;
            if(sdTriangle(uv, c, p, b) < 0.) { a = c; c = b; b = p; }
            else { blue = true; b = c; c = a; a = p; }
        }

    }

    vec3 sCol = mix(vec3(0.859,0.263,0.263), vec3(0.306,0.306,0.792), float(blue));
    vec3 lCol = vec3(0.353,0.325,0.325);

    col = sdShape(uv, a, b, c, sCol, lCol, 0.0001);
    fragColor = vec4(col, 1.);
}

