  return colOut;
}

vec3 DrawFig(vec3 pColObj, vec3 colOut, float distObj )
{  colOut = mix(colOut,pColObj ,S2( distObj,0.0));
return colOut;
}

float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float sdCircle( vec2 p, float r )
{
    return length(p) - r;
}

float sdBoxRound( in vec2 p,vec2 b, in float r )
{  return sdBox(p,b) - r; }

vec2 hash( vec2 p ) // replace this by something better
{   p = vec2( dot(p,vec2(127.1,311.7)), dot(p,vec2(269.5,183.3)) );
	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

float noise( in vec2 p )
{   const float K1 = 0.366025404; // (sqrt(3)-1)/2;
    const float K2 = 0.211324865; // (3-sqrt(3))/6;

	vec2  i = floor( p + (p.x+p.y)*K1 );
    vec2  a = p - i + (i.x+i.y)*K2;
    float m = step(a.y,a.x); 
    vec2  o = vec2(m,1.0-m);
    vec2  b = a - o + K2;
	vec2  c = a - 1.0 + 2.0*K2;
    vec3  h = max( 0.5-vec3(dot(a,a), dot(b,b), dot(c,c) ), 0.0 );
	vec3  n = h*h*h*h*vec3( dot(a,hash(i+0.0)), dot(b,hash(i+o)), dot(c,hash(i+1.0)));
    return dot( n, vec3(70.0) );
}

// -----------------------------------------------
vec3 nube(vec2 p, vec3 col)
{
    float d1= sdCircle(p,1.0 );
    float d2= sdCircle(p-vec2(0.5,0.5),1.0 );
    float d3= sdCircle(p-vec2(-0.5,0.5),1.0 );
    float d4= sdCircle(p-vec2(0.5,1.5),1.0 );
    col= DrawFig(vec3(1.0)-p.y*0.25,col,d1);
    col= DrawFig(vec3(1.0)-p.y*0.25,col,d2);
    col= DrawFig(vec3(1.0)-p.y*0.25,col,d3);
    col= DrawFig(vec3(1.0)-p.y*0.25,col,d4);

    return col;
}

vec3 bloque1(vec2 p, vec3 col, float esca)
{
    float d1=sdBoxRound(p,vec2(1.5,4.),0.25 );
    float d2=sdBoxRound(p,vec2(0.8,3.5),0.25 );
    d2=d2+ noise( p*0.8-0.5);

    col= DrawFigBordeCol(vec3(0.6)*esca, col, d1, vec3(0.5) );
    col= DrawFigBordeCol(vec3(0.3)*esca, col, d2, vec3(0.4) );
    return col;
}

vec3 grupoBloques(vec2 uv, vec3 col, float esca)
{   col= bloque1(uv-vec2(-12.0,-4.0), col, esca);
    col= bloque1(uv-vec2(-8.5,-5.0), col, esca);
    col= bloque1(uv-vec2(-5.0,-5.0), col, esca);
    col= bloque1(uv-vec2(-5.0,-5.0), col, esca);
    col= bloque1(uv-vec2(-1.5,-6.0), col, esca);
    col= bloque1(uv-vec2( 2.0,-6.0), col, esca);
    col= bloque1(uv-vec2( 5.5,-8.0), col, esca);
    col= bloque1(uv-vec2( 9.0,-8.0), col, esca);
    col= bloque1(uv-vec2( 7.5,-9.0), col, esca);
    col= bloque1(uv-vec2( 12.5,-6.0), col, esca);

   return col;
}    

