// intersects a capsule (single cap)
// https://iquilezles.org/www/articles/intersectors/intersectors.htm
vec4 iCylinder( in vec3 ro, in vec3 rd, 
                in vec3 pa, in vec3 pb, float ra, 
                out float v )
{
    vec4 res = vec4(-1.0);

    v = 0.0;
    vec3 ba = pb-pa;
    vec3 oc = ro-pa;

    float baba = dot(ba,ba);
    float bard = dot(ba,rd);
    float baoc = dot(ba,oc);
    float ocrd = dot(oc,rd);
    float ococ = dot(oc,oc);

    float a = baba - bard*bard;
    float b = baba*ocrd - baoc*bard;
    float c = baba*ococ - baoc*baoc - ra*ra*baba;
    float h = b*b - a*c;
    if( h>0.0 )
    {
        float t = (-b-sqrt(h))/a;

        // body
        float y = baoc + t*bard;
        if( y>0.0 && y<baba )
        {
            v = y/baba;
            res = vec4(t,(oc+t*rd-ba*v)/ra);
        }
        // sphere cap
        else 
        {
            h = ocrd*ocrd - ococ + ra*ra;
            if( h>0.0 )
            {
                t = -ocrd - sqrt(h);
                res = vec4(t,(oc+t*rd)/ra);
            }
        }
    }

    return res;
}

// intersects a capsule
// https://iquilezles.org/www/articles/intersectors/intersectors.htm
bool sCylinder( in vec3 ro, in vec3 rd, 
                in vec3 pa, in vec3 pb, float ra )
{
    vec3 ba = pb-pa;
    vec3 oc = ro-pa;

    float baba = dot(ba,ba);
    float bard = dot(ba,rd);
    float baoc = dot(ba,oc);
    float ocrd = dot(oc,rd);
    float ococ = dot(oc,oc);

    float a = baba - bard*bard;
    float b = baba*ocrd - baoc*bard;
    float c = baba*ococ - baoc*baoc - ra*ra*baba;
    float h = b*b - a*c;
    if( h>0.0 )
    {
        float t = (-b-sqrt(h))/a;

        // body
        float y = baoc + t*bard;
        if( t>0.0 && y>0.0 && y<baba ) return true;
        // sphere cap
        h = ocrd*ocrd - ococ + ra*ra;
        if( h>0.0 )
        {
            //if( h*h<-ocrd*abs(ocrd) ) return true;
            t = -ocrd - sqrt(h);
            if( t>0.0 ) return true;
        }
    }

    return false;
}

// intersects the knot
vec4 intersect( in vec3 ro, in vec3 rd, out float resV )
{
    // subdivide the knot, and find intersections
    float   v = 0.0;
    vec4 tnor = vec4(1e20);
    vec3   op = knot(0.0);
    for( int i=1; i<=kNum; i++ )
    {
        // parameter
        float t = float(i)/float(kNum);

        // evaluate knot
        vec3 p = knot(t);

        // segments
        float tmpv;
        vec4 tmp = iCylinder( ro, rd, op, p, kRad, tmpv );
        if( tmp.x>0.0 && tmp.x<tnor.x ) { tnor = tmp; v=t+(tmpv-1.0)/float(kNum); }

        op = p;
    }

    resV = v;

    return tnor;
}

// intersects the knot
float shadow( in vec3 ro, in vec3 rd )
{
    // subdivide the knot, and find intersections
    vec3 op = knot(0.0);
    for( int i=1; i<=kNum; i++ )
    {
        // parameter
        float t = float(i)/float(kNum);

        // evaluate knot
        vec3 p = knot(t);

        // segments
        if( sCylinder( ro, rd, op, p, kRad ) ) return 0.0;

        op = p;
    }

    return 1.0;
}

// do coloring and lighting
vec3 shade( in vec3 pos, in vec3 nor, in vec3 rd, in float hm )
{
    // material - base color
    vec3 mate = 0.5 + 0.5*cos(hm*6.283185+vec3(0.0,2.0,4.0));

    // material - white stripes
    vec3 cen = knot(hm); 
    vec3 w = normalize(knot(hm+0.001)-cen);
    vec3 v = vec3(w.y,-w.x,0.0)/length(w.xy);
    vec3 u = normalize(cross(v,w));
    float an = atan( dot(pos-cen,u), dot(pos-cen,v) );
    float ar = an - 30.0*hm + iTime;
    mate += 1.5*smoothstep(-0.3,0.8,sin(6.283185*ar));

    // sky lighting
    vec3 ref = reflect(rd,nor);
    float dif = 0.5+0.5*nor.y;
    float spe = smoothstep(0.1,0.2,ref.y);
    spe *= dif;
    spe *= 0.04 + 0.96*pow( clamp(1.0+dot(rd,nor), 0.0, 1.0), 5.0 );
    if( spe>0.001 ) spe *= shadow(pos+nor*0.001, ref);
    vec3 col = 0.6*mate*vec3(1.0)*dif + spe*6.0;

    // sss
    float fre = clamp(1.0+dot(rd,nor),0.0,1.0);
    col += fre*fre*(0.5+0.5*mate)*(0.2+0.8*dif);

    // self occlusion
    float occ = 0.0;
    for( int i=1; i<=kNum/4; i++ )
    {
        float h = float(i)/float(kNum/4);
        vec3  d = knot(h) - pos;
        float l2 = dot(d,d);
        float l = sqrt(l2);
        float f = dot(d/l,nor);
        occ = max(occ, f*exp2(-l2*8.0) );
        occ = max(occ, f*1.5*kRad*kRad/l2 );
    }
    col *= 1.0-occ;

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // camera movement	
	float an = 0.0*iTime;
	vec3 ro = vec3( 1.0*sin(6.283185*an), 0.0, 1.0*cos(6.283185*an) );
    vec3 ta = vec3( 0.0, 0.02, 0.0 );
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));

    // render
    vec3 tot = vec3(0.0);

    #if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (2.0*(fragCoord+o)-iResolution.xy)/iResolution.y;
        #else    
        vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
        #endif

	    // create view ray
        vec3 rd = normalize( p.x*uu + p.y*vv + 1.5*ww );

        // background
        vec3 col = vec3(0.17*(1.0-0.15*dot(p,p))*smoothstep(-1.0,1.0,rd.y));

        // raytrace knot
        float hm;
        vec4 tnor = intersect( ro, rd, hm );
        if( tnor.x<1e19 )
        {
            col = shade( ro+tnor.x*rd, tnor.yzw, rd, hm );
        }

        // gain
        col *= 1.4/(1.0+col);
        // tint
        col = pow( col, vec3(0.8,0.95,1.0) );

        // color to perceptual space	
        col = pow( col, vec3(0.4545) );
	    tot += col;
    #if AA>1
    }
    tot /= float(AA*AA);
    #endif

    // remove color banding through dithering
    tot += (1.0/255.0)*fract(sin(fragCoord.x*7.0+17.0*fragCoord.y)*1.317);

	fragColor = vec4( tot, 1.0 );
}

vec2 hash(vec2 p){   // by Dave_Hoskins: https://www.shadertoy.com/view/4djSRW
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy)*2.-1.;
}


vec3 noised( in vec2 p ){  // by IQ: https://www.shadertoy.com/view/XdXBRH
// return gradient noise (in x) and its derivatives (in yz)
    vec2 i = floor( p );
    vec2 f = fract( p );

    vec2 u = f*f*f*(f*(f*6.-15.)+10.);
    vec2 du = 30.*f*f*(f*(f-2.)+1.);

    vec2 ga = hash( i + vec2(0,0) );
    vec2 gb = hash( i + vec2(1,0) );
    vec2 gc = hash( i + vec2(0,1) );
    vec2 gd = hash( i + vec2(1,1) );

    float va = dot( ga, f - vec2(0,0) );
    float vb = dot( gb, f - vec2(1,0) );
    float vc = dot( gc, f - vec2(0,1) );
    float vd = dot( gd, f - vec2(1,1) );

    return vec3( va + u.x*(vb-va) + u.y*(vc-va) + u.x*u.y*(va-vb-vc+vd),   // value
                 ga + u.x*(gb-ga) + u.y*(gc-ga) + u.x*u.y*(ga-gb-gc+gd) +  // derivatives
                 du * (u.yx*(va-vb-vc+vd) + vec2(vb,vc) - va));
}


void mainImage( out vec4 O, in vec2 P ){
    vec2 p= (P-.5*iResolution.xy)/iResolution.y, pp= p;
    p= p/(.8-p.y);  // perspective
    p*= vec2(.5,1);  // horizontal stretch
    p+= .1*iTime;  // diagonal scroll
    p*= 300.;  // scale

    vec3 nd= .4*noised(p/64.) + .3*noised(p/32.) + .2*noised(p/16.);  // 3-octave noise
    vec2 n= normalize(nd.yz);
    float a= dot(pp,vec2(2));  // changing light dir

    //O= .5*vec4(1.+dot(n,vec2(sin(a),cos(a))));  // basic ("aluminium foil")
    O= .8*dot(n,vec2(sin(a),cos(a)))*(1.+.9/(nd.x-.5)*vec4(1,0,0,0));  // fancy
    O= pow(O,vec4(3.));  // tone-map
}

#define cmul(a,b) vec2(a.x * b.x - a.y * b.y, a.x * b.y + a.y * b.x)
#define r1 vec2(0.5,0.0)
#define r2 vec2(-0.5,0.0)
#define r3 vec2(0.0,0.9)
#define pi 3.14159

#define c1 0xd77a61
#define c2 0xdbd3d8
#define c3 0x223843

#define sqLength(z) z.x*z.x + z.y*z.y

#define rotSpeed 0.03

vec2 f(vec2 z){
    return cmul((r1 + z), (cmul((r2 + z), (r3 + z))));
}

vec2 fp(vec2 z){
    return cmul(((3.0*z)+2.0*(r1+r2+r3)),z) + cmul(r1,r2) + cmul(r2,r3) + cmul(r1,r3);
}

#define inv(z) vec2(z.x,-z.y)/(z.x*z.x + z.y * z.y)
#define iterationNb 100

vec2 newtonReg(vec2 z){

    for (int i = 0; i < iterationNb; i +=1){
            z -= cmul((f(z)), (inv(fp(z))));
    }
    return z;
}

/*
	h : given in radians.
	s : from 0 to 1.
	v : from 0 to 1.
*/
vec3 hsvToRgb(vec3 hsv){/* not understood but from wikipedia :
						https://fr.wikipedia.org/wiki/Teinte_Saturation_Valeur */
    float s = hsv.y;
    float v = hsv.z;

    vec3 rgb;
    hsv.x *= 180.f/pi;
    hsv.x = mod(hsv.x,360.f);
    int state = int(hsv.x/60.f)%6;
    float f = hsv.x/60.f - float(state);

    float l = (1.f - s);
    float m = (1.f - s * f);
    float n = (1.f - (1.f-f) * s);

    return v * vec3[](vec3(1,n,l),
                 vec3(m,1,l),
                 vec3(l,1,n),
                 vec3(l,m,1),
                 vec3(n,l,1),
                 vec3(1,l,m))[state];
}

vec3 hexToCol(int hex){
    return vec3(float((hex & 0xff0000) >> 16),
                float((hex & 0x00ff00) >> 8),
                float((hex & 0x0000ff)))/255.0;
}


vec3 coordToColor(vec2 z){    
    float r1Closeness = sqLength((z+r1));
    float r2Closeness = sqLength((z+r2));
    float r3Closeness = sqLength((z+r3));
    if (r1Closeness < r2Closeness && r1Closeness < r3Closeness){
        return hexToCol(c1);
    }
    if (r3Closeness < r2Closeness){
        return hexToCol(c3);
    }
    return hexToCol(c2);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float loopTime = iTime/4.0 - 25.49 * floor(iTime/(25.49*4.0));
    vec2 ab = (exp(23.0-loopTime)) * (fragCoord/iResolution.xy-vec2(.24,.42));
    ab -= vec2(0.0,.3);
    float rotTime = 2.0*pi*(25.49-loopTime)/25.49;
    ab = vec2(cos(rotTime) * ab.x + sin(rotTime) * ab.y,
             -sin(rotTime) * ab.x + cos(rotTime) * ab.y);
    vec3 col = coordToColor(newtonReg(ab));

    // Output to screen
    fragColor = vec4(col,1.0);
}

// Created by Vinicius Graciano Santos - vgs/2013
// I've learned a lot about fractals in this series of blog posts:
// http://blog.hvidtfeldts.net/index.php/2011/08/distance-estimated-3d-fractals-iii-folding-space/

#define TAU 6.283185

// The range that the vertex count is interpolated within
#define SPAN vec2(1, 10)

// unit circle around polygon
#define UCIRCLE

float fractal(in vec2 uv, float SIDES) {
	float c = cos(1.0/float(SIDES)*TAU);
	float s = sin(1.0/float(SIDES)*TAU);

	mat2 m = mat2(c, s, -s, c);
	vec2 p = vec2(1.0, 0.0), r = p;

	for (int i = 0; i < 7; ++i) {
		float dmin = length(uv - r);
		for (int j = 0; j < int(ceil(SIDES)); ++j) {
			p = m*p;
			float d = length(uv - p); 
			if (d < dmin) {dmin = d; r = p;}
		}
		uv = 2.0*uv - r;
	}

	return (length(uv-r)-0.15)/pow(2.0, 7.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	float vig = 0.15 + pow(uv.x*uv.y*(1.0-uv.x)*(1.0-uv.y), 0.1);

	uv = -1.0+2.0*uv;
	uv.x *= iResolution.x/iResolution.y;

	float d = fractal(uv, .5*(SPAN.x + SPAN.y) + .5*(SPAN.x - SPAN.y)*sin(iTime*0.1));
	d = smoothstep(0.001, 0.015, d);

	#ifdef UCIRCLE
	d *= 1.0-length(uv);
	#endif

	fragColor = vec4(vec3(pow(vig*d, 0.45)),1.0);
}

#define MAX_STEPS 100
#define MAX_DIST 200.
#define SURF_DIST .001

#define S smoothstep
