mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float sinDisplacement(vec3 p, float strength) {
    return sin(strength*-p.y);
}

float smin(float a, float b, float k) {

    /*float h = max(0., min(1., (b - a)/k + .5));
    float m = h * (1. - h) * k;

    return a * k + b * (1. - k) - m * .5;*/

    float h = max( k-abs(a-b), 0.0 )/k;
    return min( a, b ) - h*h*h*k*(1.0/6.0);
}

float sdCapsule(vec3 p, vec3 a, vec3 b, float r) {

    vec3 ab = b - a;
    vec3 ap = p - a;

    // Project ap onto ab 
    float t = dot(ab, ap) / dot(ab, ab);

    // Projection cannot exceed the length of ab
    t = clamp(t, 0., 1.);

    // Closest point in line segment
    vec3 c = a + ab * t;

    // Distance to line segment
    float d = length(p - c);

    // Distance to capsule
    return d - r;
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.)) +min(max(p.x, max(p.y, p.z)), 0.);
}

float sdSphere(vec3 p, float r) {
    return length(p) - r;
}

float sdTorus(vec3 p, vec2 r) {
    float x = length(p.xz) - r.x;
    float y = p.y;
    return sqrt(x*x + y*y) - r.y;
}

float sdPlane(vec3 p, vec3 n, float h) {

    vec2 uv = p.xz;
    float noise = texture(iChannel1, uv * 0.005).r;


    return dot(p, normalize(n)) - (h - noise*6.);
}

float opCheapBend(vec3 p )
{
    const float k = -0.001; // or some other amount
    float c = cos(k*p.x);
    float s = sin(k*p.x);
    mat2  m = mat2(c,-s,s,c);
    vec3  q = vec3(m*p.xy,p.z);


    return sdPlane(q - vec3(0., 0., iTime * 100. + cos(iTime)+1. * 200.), vec3(cos(iTime * 1.5) * .15, 1.,0.),-3.);
}


vec4 GetDist(vec3 p) {
    vec3 c = vec3(7.);
    //p = mod(p+0.5*c,c)-0.5*c;

    float k = sin(iTime * 2.); // or some other amount
    float e = cos(k*p.z);
    float s = sin(k*p.z);
    mat2  m = mat2(e,-s,s,e);
    //p = vec3(m*p.xy,p.y);

    // From -1 to 2
    float pulse = abs(cos(iTime * 4.));

    vec3 sp = p;
    sp *= vec3(1.,3.,.2);
    sp -= vec3(cos(iTime*2.)*2., sin(iTime * 2.)*2., (cos(iTime) - 1.) * 3.);

    //sp. *= Rot(iTime);
    float d = sdSphere(sp, 1.2);
    float ds = sdSphere(p + vec3(0., 0.8 + sin(iTime) * 2., 1.2), 0.7);
    float ds2 = sdSphere(p + vec3(0., 0.8 + sin(iTime) * 2., -1.2), 0.7);

    float d2 = sdBox(p - vec3(0.,-3., 0. ), vec3(10., .1, 10.));

    float d3 = sdCapsule(p, vec3(0., 1., 0.), vec3(.0, -1., 0.), 1.2);

    float d4 = sdTorus(p - vec3(0., pulse, 0.), vec2(1.0, 0.2));

    //float d5 = sdPlane(p - vec3(0., 0., iTime * 100.), vec3(cos(iTime * 1.5) * .15, 1.,0.),-3.);
    float d5 = opCheapBend(p);
    float sk = 1.;

    float mi = min(d, d5);

    return mi == d ? vec4(.5,0.01,.01,d) : vec4(vec3(.1),d5);
}

vec4 RayMarch(vec3 rayOrigin, vec3 rayDirection) {

    // Current marching distance
	float currentDistance=0.;
    vec4 curr;

    for(int i=0; i < MAX_STEPS; ++i) {

        // March in the ray direction
    	vec3 p = rayOrigin + rayDirection*currentDistance;

        // Get direction to solid using the distance function
        curr = GetDist(p);
        float dS = curr.w;

        // Increase marching distance
        currentDistance += dS;

        // Stop ray marching when max distance is surpassed 
        // or when the distance is very small
        if(currentDistance>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return vec4(curr.xyz, currentDistance);
}

float IsInShadow(vec3 rayOrigin, vec3 rayDirection) {

    float currentDistance = 0.;
    float res = 1.;

    for (int i = 0; i < 200; ++i) {

        // Get direction to solid using the distance function
        float dS = GetDist(rayOrigin + rayDirection*currentDistance).w;

        // Stop ray marching when max distance is surpassed 
        // or when the distance is very small
        if(dS < 0.001) return 0.;

        res = min(res, 9.*dS/currentDistance );

        // Increase marching distance
        currentDistance += dS;
    }
    return res;

}

vec3 GetNormal(vec3 p) {
	const float h = 0.1; // replace by an appropriate value
    const vec2 k = vec2(1,-1);
    return normalize( k.xyy*GetDist( p + k.xyy*h ).w + 
                      k.yyx*GetDist( p + k.yyx*h ).w + 
                      k.yxy*GetDist( p + k.yxy*h ).w + 
                      k.xxx*GetDist( p + k.xxx*h ).w );
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {

    // Ray from pixel to the scene origin
    vec3 f = normalize(l-p);

    // Finding the ray direction
    vec3 r = normalize(cross(vec3(0,1,0), f));
    vec3 u = cross(f,r);

    vec3 c = f*z;
    vec3 i = c + uv.x*r + uv.y*u;
    vec3 d = normalize(i);
    return d;
}

