// https://www.desmos.com/calculator/l3buctrt3l

// Error function approximation
float erf(in float x) {
    return sign(x) * sqrt(1.0 - exp2(-1.787776 * x * x));
}

// Gaussian filtered checkerboard
// Works for filter widths between 0 and 3
// Increase k to allow for larger filter widths
const float k = 4.0; // k → ∞
float gchecker(in vec2 p, in vec2 w) {
    p = mod(p, 2.0);
    float s = 1.0 - 2.0 * mod(k, 2.0);
    float u = 0.0, v = 0.0;
    for (float n=-k; n <= k; n++) {
        u += s * erf((p.x - n) / w.x);
        v += s * erf((p.y - n) / w.y);
        s = -s;
    }

    return (1.0 - u * v) / 2.0;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
