		     nor = calcNormal(pos);
		float occ = calcOcclusion(pos, nor);
		vec3 sun_dir = normalize(vec3(.8, .4, .2));
		float sun_sha = step(castRay(pos + nor * .001, sun_dir), 0.),
		      sun_dif = clamp(dot(nor, sun_dir), 0., 1.),
		      sky_dif = clamp(.5 + .5 * dot(nor, vec3(0, 1, 0)), 0., 1.),
		      bou_dif = clamp(.5 + .5 * dot(nor, vec3(0, -1, 0)), 0., 1.);
		col = vec3(.18) * vec3(4) * sun_dif * sun_sha;
		col += vec3(.18) * vec3(.5, .8, .9) * sky_dif * occ;
		col += vec3(.18) * vec3(.7, .3, .2) * bou_dif * occ;
	}
	else col = env_color(rd);

	fragColor = vec4(pow(col, vec3(.4545)), 1);
}

// utils from https://www.shadertoy.com/view/llySRh
#define rot(a)   mat2(cos(a+vec4(0,11,33,0)))                          // rotation
#define H(p)     fract(sin((p)*mat2(127.1,311.7, 269.5,183.3)) *43758.5453123)
#define hue(v)  ( .6 + .6 * cos( 6.3*(v)  + vec4(0,23,21,0)  ) )

float map(vec3 q, out vec2 _I) {      // --- scene. return SDF, out = ID.
    vec2 I = floor(q.yx), F = fract(q.yx), D,H;
    float d, l=9.;
    for( int k=0; k < 9; k++ )        // find closest seed within neihbors
        D = vec2(k%3,k/3) -1.,
        H = H(I+D+.5),                // motion parameters
        d = length( D + H(I+D) - F    // offset to seed
                      + .8*cos( 15.*iTime*(H.y-.5) + 6.3*H.x + vec2(0,11) ) // seed rotation
                  ),
        d < l ? l = d, _I = I+D : I;  // keep closest dist + its id.
    return length(vec2(l,q.z)) - .3;
}

void mainImage(out vec4 O, vec2 U)    // --- renderer
{
    float t=9.; 
    vec3  R = iResolution, N, e = vec3(1,-1,0)/1e2, 
          D = normalize(vec3((U+U-R.xy)/R.y, -2.)),   // ray direction
          p = vec3(0,-2,22), q,                       // marching point along ray 
          M = iMouse.z > 0. ? iMouse.xyz/R -.5: vec3(10,12,0)/1e2*cos(.3*iTime+vec3(0,11,0))+vec3(0,.12,0); 
    vec2 C;
    for ( O=vec4(1); O.x > 0. && t > .005; O-=.005 )  
        q = p,
        q.yz *= rot(.5-6.3*M.y),                      // rotations
        q.xz *= rot(-6.3*M.x),
        t = map(q,C),                                 // SDF
        p += .25*t*D;                                 // step forward = dist to obj

    O *= hue(H(C).x);                 // coloring from ID
                                      // optional: true shading
    if (O.a>0.) N = normalize(   map(q+e.xyy,C)/e.xyy +  map(q+e.yxy,C)/e.yxy
                               + map(q+e.yyx,C)/e.yyx +  map(q+e.xxx,C)/e.xxx ),
                O *= vec4( .3 + .7*max(0.,N.z) )*1.3;
}

