//walk
	float walk(vec3 p){
        float gtime = iTime;
        float sig = sign(p.z);
        float l = 1.;
        p.x += sig/2. - 1.4;
        l += floor((gtime - sig/2.)/2. );
        float s = 2.;
        p.x -=s*clamp(floor(p.x/s + .5),-l,l);

        //p.x = mod(p.x,1.)-.5;
        p.y -=1.8;
        vec3 wp = p;
        wp.x += .01;
        p.x -= clamp(p.x,-.15,.1);
        p.z = abs(p.z)-.3;

        float o = length(p)-.15 - max(0.,sin(p.x/2.-3.2 ));
        o=max(o,p.y-.1);
        o = max(o,-(length(wp.xy)-.04));
        return o;
    }
	vec2 road(vec3 p)
    {
        vec2 o = vec2(bar(p.yz-vec2(12.,0.),vec2(10.,1.),.2),0.);
        o.x = max(o.x,-walk(p));
        if(o.x < .01){
        	o.x+=noise(p*100.)/195.;
        }
        return o;
    }
	vec2 lead(vec3 p)
    {
        float gtime = iTime;
        vec2 o = vec2(10.,1.);
        p.x += gtime;
        p.yz *= rot(sin(gtime*10.)/10.);
        p.y += sin(-gtime*pi + pi/2. + p.x)/12.+.1;
      //  p.xy *= rot(pi/4.);
        p.xy *= rot(-sin(sin(p.x/10.)-.02)+pi/4.);
       // p.xy *= rot(-smoothstep(0.,-3.,p.x));
        vec3 lp = p;
        float l = 30.;
        p.x -= l;
        p.yz *= rot(p.x*13.3);

        p.yz = abs(p.yz)-.02;
        p.x -= clamp(p.x,-l,l*2.);

        o.x = length(p)-.02;
        lp.yz *= rot(2.3);
        lp.x += 0.2;
        lp.x -= clamp(lp.x,-.03,.03);
        o.x = min(o.x,length(vec2(length(lp.xy)-.2,lp.z))-.03);
        if(o.x < 0.01)
        {
            o.x -= noise(p * 40.)/390.;
        }
        return o;
    }
    vec2 map1(vec3 p)
    {
        float gtime = iTime;
        vec2 o = vec2(10.);
        p.x -= TT((gtime+.5),1.5);
		o = min2(o,road(p));
        o = min2(o,lead(p));
        return o;
    }

    vec2 march1(vec3 cp , vec3 rd)
    {
        float depth = 0.;
        for(int i = 0 ; i< 99 ; i++)
        {
            vec3 rp = cp + rd * depth;
            vec2 d = map1(rp);
            if(abs(d.x) < 0.009)
            {
                return vec2(depth,d.y);
            }
            if(depth > 30.)break;
            depth += d.x;
        }
        return vec2(-depth , 0.);

    }

    float shadowmarch1(vec3 cp , vec3 rd)
    {
        float depth = 0.;
        for(int i = 0 ; i< 256 ; i++)
        {
            vec3 rp = cp + rd * depth;
            vec2 d = map1(rp);
            if(d.x < 0.01)
            {
                return -1.;
            }
            if(depth > 60.)break;
            depth += d.x;
        }
        return 1.;
    }
	vec3 getMat1(vec3 pos,float id)
    {
        vec3 col = vec3(1.);
        if(id == 0.)
        {
            col = vec3(0.6);
        }else if(id == 1.){
        	col = vec3(1.5,0.3,0.);
        }
        return col;
    }
    vec4 scene1(vec3 cp,vec3 target,vec2 fragCoord) {
        vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
        p.y -= 0.2;
        vec3 col = vec3(0.);
        vec3 cd = normalize(vec3(target - cp));
        vec3 cs = normalize(cross(cd , vec3(0.,1.,0.)));
        vec3 cu = normalize(cross(cd,cs));
        float fov = 2.5;
        vec3 rd = normalize(cd * fov + cs * p.x + cu * p.y);
        vec2 d = march1(cp,rd);
        if( d.x > 0.)
        {
            vec2 e = vec2(1.0, -1.0) * .003;
            vec3 pos = cp + rd * d.x;
            vec3 N = normalize(
                      e.xyy * map1(pos + e.xyy).x +
                      e.yyx * map1(pos + e.yyx).x +
                      e.yxy * map1(pos + e.yxy).x +
                      e.xxx * map1(pos + e.xxx).x);
            vec3 lightPos = vec3(0.,-1.5,0.);
            vec3 lightDir = vec3(0.,-1.,0.);
            vec3 lightCol = vec3(0.,1.,1.);
            float lpower = 3.;
            float shadow = step(shadowmarch1(pos + N*.01,lightDir),0.);
            vec3 mat = getMat1(pos,d.y);
            //vec3 lightEffect(vec3 mat,float lPower,vec3 N,vec3 rd ,vec3 lightPos, vec3 lightDir,vec3 lightCol,vec3 pos,float shadow)
            col = lightEffect(mat,lpower,N,rd ,lightPos, lightDir,lightCol,pos,shadow);
            float t = 1.-exp(-.001 * d.x * d.x * d.x);
            col = mix(col,vec3(0.),t);
        }
        //col = pow(col,vec3(.4545));
        return vec4(col,abs(d.x));
    }
