vec2 random(in vec2 _st ){
    _st = vec2(
            dot(_st,vec2(127.326,321.324)),
            dot(_st,vec2(15.31,45.332))
                    );
    return 2.0*fract(sin(_st)*432.23) +1.0; //1.0 ~ 3.0
}
float snoise(vec2 st){ //Gradient Noise
    vec2 i = fract(st);//取小数
    vec2 k = floor(st);//向负无穷取整
    vec2 u = i*i*(3.0-2.0*i);//插值函数
    return  mix(
                    mix(dot(random(k + vec2(0.0,0.0)),i - vec2(0.0,0.0)),
                        dot(random(k + vec2(0.0,1.0)),i - vec2(0.0,1.0)),
                         u.y),
                    mix(dot(random(k + vec2(1.0,0.0)),i - vec2(1.0,0.0)),
                        dot(random(k + vec2(1.0,1.0)),i - vec2(1.0,1.0)),
                         u.y),
                       u.x
                   );//二维noise
}
float Model(vec2 st,float N){
    st = st*2.-1.;
    float a = atan(st.x,st.y)+3.14;
    float r = 6.28/N;
    return abs(cos(floor(.5+a/r)*r-a)*length(st));
}
float getModel(vec2 st,vec2 size){
    return Model(st*size,4.);
}
float createModel(in vec2 st,in float num[4]){
    float exy =4.;
    float exx =2.;
    st*=vec2(exx,exy);
    vec2 fi = fract(st);
    vec2 df = floor(st);
    fi.x = mix(fi.x,1.-fi.x,step(1.0,df.x));
    return mix(getModel(fi,vec2(0.83,1.)),getModel(fi - vec2(0.03,0.),vec2(1.0)),num[int(df.y)]);
}
float getWork(vec2 st,float t){
    float exy =4.;
    float exx =2.;
    float num[4];
    float j = floor(mod(t,pow(2.,exy)));
    for(int i=0;i<4;i++){
        num[i] =0.0;
        num[i] = step(1.0,mod(j,2.));
        j = ceil(j/2.);
    }
    return createModel(st,num);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
//和五同样的方法 只是将c的值进行筛选
    vec2 vUv = fragCoord/iResolution.xy;
    vUv*=3.;
    vec2 uv = fract(vUv);
  //  uv.x*=iResolution.x/iResolution.y;
    float t = iTime*0.3;//速度
    float c = mix(getWork(uv,t+floor(vUv.x) +floor(vUv.y) ),getWork(uv,t+floor(vUv.x) +floor(vUv.y) + 1.0),fract(t));
    c += snoise(uv*7.)*0.08;
    float angle = atan(uv.y,uv.x);
    vec3 color = (0.5 + 0.5*cos(6.28*(angle/6.28 +vec3(0.6,0.7,0.9)) ))*step(c,0.77);
    // Output to screen
    fragColor = vec4(color,1.0);
}

//Inspired by wyatt's fractal:
//https://www.shadertoy.com/view/MdKyRw

#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST 0.00001

// The SDF
vec4 GetDist(vec3 p) {
    float bg = p.y;

    float t = iTime * 0.4;
    mat2 m1 = mat2(cos(t),-sin(t),sin(t),cos(t));
    t *= sqrt(2.);
    mat2 m2 = mat2(cos(t),-sin(t),sin(t),cos(t));
    t /= 3.;
    mat2 m3 = mat2(cos(t),-sin(t),sin(t),cos(t));

    float size = 0.9;

    p -= vec3(0,1,0);
    vec3 col = vec3(0.0, 0.0, 0.0);

    for (int i = 0; i < 13; i++) {
        size = size*0.66;
        p.xz = m1*p.xz;
        p.yz = m2*p.yz;
        p.xy = m3*p.xy;

        col -= (p)*size;
        p.xyz = abs(p.xyz) - size;
    }
    float d = min(bg, length(p)-5.8*size);

    col.r = (1.*col.x+2.)/3.;
    col.b = (1.*col.z+0.8)/2.+0.2;
    col.g = (.6*col.y+1.)/2. + 0.4*col.r + 0.4*col.b;
    col *= 0.7;
    col += 0.1;

    col = bg < length(p)-5.8*size ? vec3(1.) : col;
    return vec4(d, col);
}

// Get the distance from camera
vec4 RayMarch( vec3 ro, vec3 rd ) {
    float dO = 0.;
    vec3 col = vec3(1.);

    for( int i = 0; i < MAX_STEPS; i++ ) {
        vec3 p = ro + rd*dO;
        vec4 ds = GetDist(p);
        dO += ds.x;
        col = ds.yzw;
        if (dO > MAX_DIST || dO < SURF_DIST) break;
    }
    return vec4(dO, col);
}

// Normal from SDF
vec3 GetNormal(vec3 p) {
    float d = GetDist(p).x;
    vec2 e = vec2(.0001, 0);

    vec3 n = d - vec3(
        GetDist(p - e.xyy).x,
        GetDist(p - e.yxy).x,
        GetDist(p - e.yyx).x);

    return normalize(n);
}

// Darker light if close to lightray
float softshadow( in vec3 ro, in vec3 rd, float k )
{
    float res = 1.0;
    float dO = 0.;
    for( int i=0; i<MAX_STEPS; i++ )
    {
        float ds = GetDist(ro + rd*dO).x;
        if( ds<SURF_DIST )
            return 0.0;
        res = min( res, k*ds/dO );
        dO += ds;
    }
    return res;
}

// Lighting
vec3 GetLight(vec3 p, vec3 lightPos) {
    vec3 l = normalize(lightPos - p);
    vec3 n = GetNormal(p);

    // Diffuse lighting
    vec3 dif = vec3(clamp(dot(n, l), 0., 1.));
    // Smooth shadow
    //float d = softshadow(p + n*SURF_DIST*2.0, l, 10.);
    //dif *= d;
    // Hard shadow
    vec4 d = RayMarch(p+n*SURF_DIST*2., l);
    if(d.x<length(lightPos-p)) dif *= 0.1*d.x*d.yzw;

    return dif;
}

// The camera
vec3 camDir(vec3 origin, vec2 uv) {
    vec3 up = vec3(0., 0., 1.);
    float zoom = 1.;
    vec3 lookat = vec3(0, 0, 0);

    vec3 f = normalize(lookat - origin);
    vec3 r = cross(up, f);
    vec3 u = cross(f, r);
    vec3 c = origin + f * zoom;
    vec3 i = c + uv.x*r + uv.y*u;
    vec3 rd = i - origin;
    return rd;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0.);  

    // Camera origin
    vec3 ro = vec3(0, 8, 0);
    float t = -iTime * 0.4;
    mat2 m1 = mat2(cos(t),-sin(t),sin(t),cos(t));
    // Camera direction
    vec3 rd = camDir(ro, uv);

    // Distance
    vec4 d = RayMarch(ro, rd);
    // Intersection point
    vec3 p = ro + rd * d.x;
    vec3 lightOff = vec3(4,2, -3);
    vec3 dif = GetLight(p, ro + lightOff);
    col = dif * d.yzw;


    fragColor = vec4(col,1.0);
}

