#define blue vec3(0.34, 0.78, 0.33)
#define MOV(a,b,c,d,e) (vec2(a*cos(t)+b*cos(0.1*t)), c*sin(t)+d*cos(0.1*(t))))
#define SMOOTH(r,R) 1.0 - smoothstep(0.0, 1.0, r)
#define M_PI 3.1415926535897932384626433832795
float _cross(vec2 uv, vec2 center, float radius){

    vec2 d = uv - center;
    //强制转换类型
    int x = int(d.x);
    int y = int(d.y);
    float r = sqrt(dot(d,d));
    if((r<radius)&&((x==y)||(x==-y)||(x==0)||(y==0)))
    {
       return 1.0;
    }
    return 0.0;
}


float movingLine(vec2 uv, vec2 c, float l){
    //速度
    float theta0 = 20.0*iTime;

    vec2 d = uv - c;

    float r = sqrt(dot(d,d));

    if(r < l){
    //计算到line(240)的距离确定线的宽度

        //颜色点坐标
        vec2 p = l * vec2(cos(theta0 * M_PI/180.0), -sin(theta0 * M_PI/180.0));
        //line颜色为设置颜色本身(l2 = 0)
        float l2 = length(d - p*(length(d)/length(p)));
        //d = normalize(d);
        //度数转化
        float theta = mod(180.0 * atan(d.y, d.x)/M_PI+theta0, 360.0);
        float gradient = clamp(1.0 - theta/90.0, 0.0, 1.0);
        return SMOOTH(l2, 1.0) + 0.5*gradient;
    }

    return 0.0;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy;

    vec2 c = iResolution.xy/2.0;

    vec3 finalColor = vec3(_cross(uv, c, 240.0));


    finalColor += movingLine(uv, c, 240.0) * blue;
    // Output to screen
    fragColor = vec4(finalColor,1.0);
}

