#define S(v)  smoothstep(3./R.y,0., v ) 
#define H(p)  fract(sin((p)*vec2(12.9898, 78.233)) * 43758.5453)
#define C(c)  O = mix(O, vec4(c-c*S(abs(d)-.01)), S(d) );
void mainImage(out vec4 O, vec2 u)
{
    vec2  R = iResolution.xy,
          U = ( 2.*u - R ) / R.y, A=U,
          C = sin(40.*U); C*=C.yx;

    O = vec4( .9+.1*clamp(C.x/fwidth(C.x),0.,1.));          // background
    A.x = abs(A.x);

    float a=0., d;
    for ( ; a < 5.; a ++ )                                  // 9 buddies
        d = length(A - vec2(.7,.5)*sin(.67*a+vec2(0,33))*(1.+.05*sin(2.*iTime))) - .25,
        C(.5);                                                     // +6.3*H(a) // asynch variant


    d = length(U+=.01*H(iTime)-.005)-.25;                   // poor guy
    C(1.);

    a = .1+.01*sin(3.*iTime); U.y += a-.1;
    d = abs(U.x+.1)- ( U.y>0. ? a/3.*smoothstep(a,0.,U.y)   // tear
                              : a/.3*sqrt(.01-U.y*U.y*9.) );
    O = abs(U.y) < a ? mix(O, vec4(0,.8,1,1),  S(d) ) : O;

}

vec3 mod289(vec3 x) {return x - floor(x * (1.0 / 289.0)) * 289.0;}
vec2 mod289(vec2 x) {return x - floor(x * (1.0 / 289.0)) * 289.0;}
vec3 permute(vec3 x) {return mod289(((x*34.0)+1.0)*x);}
float snoise(vec2 v)
{

//vec2 mouse=v.xy/iResolution.xy;

 vec2 mouse=iMouse.xy/iResolution.xy;
     vec4 C = vec4(mouse.x,  // (3.0-sqrt(3.0))/6.0
                        mouse.y,  // 0.5*(sqrt(3.0)-1.0)
                        -0.577350269189626,  // -1.0 + 2.0 * C.x
                        iTime/5000.); // 1.0 / 41.0
    // First corner
    vec2 i  = floor(v + dot(v, C.yy) );
    vec2 x0 = v -   i + dot(i, C.xx);

    // Other corners
    vec2 i1;
    //i1.x = step( x0.y, x0.x ); // x0.x > x0.y ? 1.0 : 0.0
    //i1.y = 1.0 - i1.x;
    i1 = (x0.x > x0.y) ? vec2(1.0, 0.0) : vec2(0.0, 1.0);
    // x0 = x0 - 0.0 + 0.0 * C.xx ;
    // x1 = x0 - i1 + 1.0 * C.xx ;
    // x2 = x0 - 1.0 + 2.0 * C.xx ;
    vec4 x12 = x0.xyxy + C.xxzz;
    x12.xy -= i1;

    // Permutations
    i = mod289(i); // Avoid truncation effects in permutation
    vec3 p = permute( permute( i.y + vec3(0.0, i1.y, 1.0 ))
    + i.x + vec3(0.0, i1.x, 1.0 ));

    vec3 m = max(0.5 - vec3(dot(x0,x0), dot(x12.xy,x12.xy), dot(x12.zw,x12.zw)), 0.0);
    m = m*m ;
    m = m*m ;

    // Gradients: 41 points uniformly over a line, mapped onto a diamond.
    // The ring size 17*17 = 289 is close to a multiple of 41 (41*7 = 287)

    vec3 x = 2.0 * fract(p * C.www) - 1.0;
    vec3 h = abs(x) - 0.5;
    vec3 ox = floor(x + 0.5);
    vec3 a0 = x - ox;

    // Normalise gradients implicitly by scaling m
    // Approximation of: m *= inversesqrt( a0*a0 + h*h );
    m *= 1.79284291400159 - 0.85373472095314 * ( a0*a0 + h*h );

    // Compute final noise value at P
    vec3 g;
    g.x  = a0.x  * x0.x  + h.x  * x0.y;
    g.yz = a0.yz * x12.xz + h.yz * x12.yw;
    return 130.0 * dot(m, g);
}
float sdLine( vec2 p, vec2 a, vec2 b, float r )
{
    vec2 pa = p - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h ) - r;
}
vec2 opRep( vec2 p, vec2 c )
{ 
    return mod(p,c)-0.5*c;
}
vec2 opRepFlip( vec2 p, vec2 c )
{ 
    vec2 fpc = floor(p/c);
    float flip = mod((fpc.x + fpc.y), 2.);
    vec2 ret = p - c*(fpc + 0.5);
    if(flip >= 1.)return ret.yx;
    return ret;
}
vec2 opWarp( vec2 p )
{ 
// return vec2(sin(p.x+2.*p.y),sin(p.y+p.x));
return vec2(p.x+0.1*sin(20.*p.y),p.y);
}

float nnoise( in vec2 uv ){return 0.5 + 0.5*snoise(uv);} //norm [0,1]
float rnoise( in vec2 uv ){return 1. - abs(snoise(uv));} //ridge
float fbm( vec2 x , int oct ) 
{
    float f = 1.98;  // could be 2.0
    float s = 0.49;  // could be 0.5
    float a = 0.0;
    float b = .9;
    for( int i=0; i < 10; i++ )
    {
        if(i >= oct) break;
        float n = nnoise(x);
        a += b * n;          // accumulate values		
        b *= s;
        x *= f;
    }
    return a;
}
float fbmr( vec2 x, int oct ) 
{
    float f = 1.98;  // could be 2.0
    float s = 0.9;  // could be 0.5
    float a = 0.0;
    float b = .4; //0.5
    for( int i=0; i < 10; i++ )
    {
        if(i >= oct) break;
        float n = rnoise(x);
        a += b * n;          // accumulate values		
        b *= s;
        x *= f;
    }
    return a;
}
float fbm2( in vec2 p )
{
    vec2 q = vec2( fbm( p + vec2(0.0,0.0) ,2 ),
                    fbm( p + vec2(5.2,1.3) ,2) );

    return fbmr( p + 2.9*q ,3); //4.0
}
float fbm3( in vec2 p )
{
    vec2 q = vec2( fbm( p + vec2(0.0,0.0) ,2),
                    fbm( p + vec2(5.2,1.3) ,2) );

    vec2 r = vec2( fbm( p + 4.0*q + vec2(1.7,9.2) ,2), //4q
                    fbm( p + 4.0*q + vec2(8.3,2.8) ,2) );
    // r = normalize(r);
    return fbmr( p*2.0 + .4*r , 3); //4.0
}

