#define complex vec2
// z.s is real part; z.t is imaginary part

const float infinity = 100000.0;
const int maxSteps = 50;

vec2 rot(vec2 p, float a) {
    float s = sin(a);
    float c = cos(a);
    return vec2 (
        c*p.x - s*p.y,
        s*p.x + c*p.y
    );
}

complex pow2(complex z) {
    return complex(z.s*z.s - z.t*z.t, 2.0*z.s*z.t);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( 2.0*fragCoord - iResolution.xy ) / iResolution.y;
    vec2 m = ( 2.0*iMouse.xy - iResolution.xy ) / iResolution.y;

    complex z = uv * 2.0;
    complex c = complex(1.0, 0.0);
    c = rot(c, iTime * 0.5);

    int i = 0;
    for (; i < maxSteps; i++) {
        z = pow2(z) + c;
        if (z.s >= infinity || z.t >= infinity)
            break;
    }    

    vec3 col = mix(vec3(0.0), vec3(1.0), vec3(float(i) / float(maxSteps)));


    fragColor = vec4(col, 1.0);
}

float len(vec2 v)
{
    return sqrt(v.x * v.x + v.y * v.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float density = exp(-uv.y * 3.0) * (1.0 - uv.y) * 0.2;
    vec3 transmittance = vec3(1,1,1);
    vec3 col = vec3(0,0,0);

    float dotProduct = -cos(iTime * 0.1 - uv.x * 2.0) * (1.0 - uv.y) * 0.35 + 0.35;
    vec3 scatter = vec3(0.25, 0.45, .75) * (1.0 - dotProduct) + vec3(0.3, 0.3, 0.3) * dotProduct; // Net in-scatter.

    float asterDist = 130.0 - iTime * 3.0;
    vec2 asterScreenPos = vec2(iResolution.x * 0.65, iResolution.y * 0.3) - vec2(0.25 * iResolution.x, 0.25 * iResolution.y) / asterDist;
    float dst = (len(fragCoord - asterScreenPos) - iResolution.x / asterDist * .5) / iResolution.x * asterDist * 2.0;

    for (int i = 0; i < 60; i++)
    {
        if (dst < 0.0 && i > int(asterDist * 1.9))
        {
            i = 150;
            continue;
        }
        col += scatter * density * transmittance; 
        transmittance -= transmittance * density * vec3(0.3, 0.55, 1); // Through-scatter: Light scattered into the camera but re-scattered out.
    }

    if (dst < 0.0)
        col += vec3(0.9, 0.94, 0.97) * sqrt(.2-dst) * transmittance; // Asteroid

    fragColor = vec4(col, 1.0);
}

#define ss smoothstep
#define saturate(x) clamp(x, 0.0, 1.0)


//Taken from https://stackoverflow.com/a/17897228
vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord)/iResolution.y;

    vec2 gv = uv * 5.0;
    gv = fract(gv);
    gv -= 0.5;
    gv *= 2.0;

    vec2 gv2 = uv * 5.0 + 0.5;
    gv2 += vec2(sin(iTime), iTime);
    gv2 = fract(gv2);
    gv2 -= 0.5;
    gv2 *= 2.0;


    vec3 col = vec3(gv.x, gv.y, 0.0);
    col = vec3(1.-saturate(length(gv)));
    col += vec3(1.-saturate(length(gv2)));
    float stepThresh = (sin(iTime*0.33) + 1.0) * 0.5;
    float stepOffs = 0.2;
    col = vec3(ss(col.x, stepThresh, stepThresh + stepOffs));
    col = step(col, vec3(0.5));


    col *= hsv2rgb(vec3((iTime * 0.1) + uv.x * 0.1, 1.0, 1.0));

    fragColor = vec4(col,1.0);
}

