//Daniel Piker 2021
/* 
Changed to get the Seifert surface using (1/4πi) log (Q/Q∗)
(added complex log line 85 and new field expression line 163)

Trying here to show different valued Seifert fibers like in 
https://twitter.com/KangarooPhysics/status/1381380025568616459

// Inigo Quilez 2021
/*

An (appoximated) SDF that has a Figure-8 knot shape. It's not very
symmetric or pretty, but it counts (topologically).

The idea is described here https://arxiv.org/pdf/1610.02463.pdf and
goes like this:

From R3, our regular 3D space, we do an inverse stereographic
projection to land in a 4D hyper sphere S3, a subset of R4.

In R4 we define a function F that returns a R2 field (or
two scalar fields, if you want). We find the zero isosurface
(the nodal set, or roots) of F by zeroing its two components.

Because we are only evaluating the subset of R4 that lays on S3,
we are really computing the intersection of the isosurfce of F
with the surface of the sphere S3. This creates a curve on the surface
of the sphere, which thanks to the stereographic projection we started
with becomes a curve in R3, our 3D space. That's the curve we are
raymarching.

The R4 parameter to F can be grouped as two complex numbers u and v.
And in the case of this shader I'm using

F(u,v) = 64v³ − 12v(3+2u²−2u*²) − (14u²+14u*²+u⁴-u*⁴)

where u* is the conjugate of u. This curve is a figure 8 shape, as
described in the paper. You can change the SHAPE define below to
see another shape (a torus knot).

Lastly, there's more than one way to convert F into a R1 scalar field
than taking it's length |F|. For example, you can pass F through any
2D SDF formula you want, such a square. Change the PROFILE define
bellow to explore that.


See: https://www.shadertoy.com/view/tsBGzt
See: https://www.shadertoy.com/view/7sGSRd

*/

#define AA 1

// 0: circular
// 1: square
#define PROFILE 0

// 0: figure 8
// 1: torus 5-2
// 2: something i made
#define SHAPE 0

//------------------------------------------------------------
// oldschool rand() from Visual Studio
//------------------------------------------------------------
int   seed = 1;
void  srand(int s ) { seed = s; }
int   rand(void) { seed = seed*0x343fd+0x269ec3; return (seed>>16)&32767; }
float frand(void) { return float(rand())/32767.0; }
//------------------------------------------------------------
// hash to initialize the random sequence (copied from Hugo Elias)
//------------------------------------------------------------
int hash( int n )
{
	n = (n << 13) ^ n;
    return n * (n * n * 15731 + 789221) + 1376312589;
}

//------------------------------------------------------------
// complex number operations
//------------------------------------------------------------
vec2 cmul( vec2 a, vec2 b )  { return vec2( a.x*b.x - a.y*b.y, a.x*b.y + a.y*b.x ); }
vec2 cdiv( vec2 a, vec2 b )  { float d = dot(b,b); return vec2( dot(a,b), a.y*b.x - a.x*b.y ) / d; }
vec2 csqr( vec2 a ) { return vec2(a.x*a.x-a.y*a.y, 2.0*a.x*a.y ); }
vec2 csqrt( vec2 z ) { float m = length(z); return sqrt( 0.5*vec2(m+z.x, m-z.x) ) * vec2( 1.0, sign(z.y) ); }
vec2 conj( vec2 z ) { return vec2(z.x,-z.y); }
vec2 cpow( vec2 z, float n ) { float r = length( z ); float a = atan( z.y, z.x ); return pow( r, n )*vec2( cos(a*n), sin(a*n) ); }
vec2 clog( vec2 z) {return vec2 (log(sqrt(z.x*z.x+z.y*z.y)),atan(z.y,z.x));}
//------------------------------------------------------------
// https://en.wikipedia.org/wiki/Stereographic_projection
vec4 inversestereographic(vec3 p)
{
    float r2 = 1.0+dot(p,p);
    return vec4(2.0*p,r2-2.0)/r2;
}
//------------------------------------------------------------
// https://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float sdRoundSquare( in vec2 p, in float s, in float r ) 
{
    vec2 q = abs(p)-s+r;
    return min(max(q.x,q.y),0.0) + length(max(q,0.0)) - r;
}
//------------------------------------------------------------
// https://iquilezles.org/www/articles/biplanar/biplanar.htm
vec3 tex( sampler2D sam, in vec4 p, in vec4 n )
{
    n = n*n;
    n = n*n;
    vec3 col = vec3(0.0);
    col += n.x*texture(iChannel0,p.yz).xyz;
    col += n.y*texture(iChannel0,p.zw).xyz;
    col += n.z*texture(iChannel0,p.wx).xyz;
    col += n.w*texture(iChannel0,p.xy).xyz;
    return col / (n.x+n.y+n.z+n.w);
}
//===============================================================================================

vec4 r3_to_s3( in vec3 p )
{
    vec4 q = inversestereographic(p);

    // rotate in yw
    float a = iTime*6.283185/24.0;
    //float a = 0.0;
    q.yw *= mat2(cos(a),sin(a),-sin(a),cos(a));

    return q;
}

vec2 F( in vec2 u, vec2 v )
{
    #if SHAPE==0
    // figure-8
    vec2 a = vec2(3.0,0.0) - 2.0*csqr(v) + 2.0*csqr(conj(v));
    vec2 q = 64.0*cmul(u,csqr(u)) 
           - 12.0*cmul(u,a) 
           + 14.0*cmul(v,v) 
           + 14.0*csqr(conj(v)) 
           -      csqr(csqr(v)) 
           +      csqr(csqr(conj(v)));
    return q;
    #endif

    #if SHAPE==1
    // torus
    return 20.0*(cmul(u,csqr(csqr(u))) + csqr(v));
    #endif    

    #if SHAPE==2
    // something I made
    return    64.0*cmul(v,csqr(csqr(u)))
           + 32.0*cmul(u,csqr(v))
           + 32.0*csqr(v) 
           + 8.0;
    #endif    

}

float field( in vec4 q )
{
    vec2 phi = F(q.xy,q.zw);

    #if PROFILE==0
    //return length(phi) - 10.0;  

    //(1/4πi) log (Q/Q∗)  
  //  return (length(cmul(vec2(0,0.785398163),clog(cdiv(phi,conj(phi))))));    
    return abs(mod(((cmul(vec2(0,0.785398163),clog(cdiv(phi,conj(phi))))).x + iTime/3.14159265359),6.28318530718) - 0.5);


    #else
    return sdRoundSquare( phi, 8.0, 0.5 );
    #endif
}

// only used for texturing, not for distance-estimation
vec4 field_grad( in vec4 q )
{
    float f = field(q);
    const vec2 e = vec2(0.001,0.0);
    return normalize(vec4(
        field(q+e.xyyy),
        field(q+e.yxyy),
        field(q+e.yyxy),
        field(q+e.yyyx))-f );
}

float map( in vec3 p )
{
    float d = field(r3_to_s3(p));

    // could do some automatic differentiation here...
    float eps = 0.0001;
    vec3 g = (vec3(field(r3_to_s3(p+vec3(eps,0.0,0.0))),
                   field(r3_to_s3(p+vec3(0.0,eps,0.0))),
                   field(r3_to_s3(p+vec3(0.0,0.0,eps))))-d)/eps;

    // distance estimator
    return d/length(g);
}

// http://iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 calcNormal( in vec3 pos, in float eps )
{
    vec2 e = vec2(1.0,-1.0)*0.5773*eps;
    return normalize( e.xyy*map( pos + e.xyy ) + 
					  e.yyx*map( pos + e.yyx ) + 
					  e.yxy*map( pos + e.yxy ) + 
					  e.xxx*map( pos + e.xxx ) );
}

// https://iquilezles.org/www/articles/rmshadows/rmshadows.htm
float shadow( in vec3 ro, in vec3 rd, float k )
{
    const float tmax = 5.0;
    float t = 0.001;
    float sh = 1.0;
    for( int i=0; i<256; i++ )
    {
        vec3 pos = ro + rd*t;
        float h = map(pos);
        sh = min( sh, clamp(k*h/t,0.0,1.0) );
        if( sh<0.001 ) break;
        t += clamp(h,0.001,0.1);
        if( t>tmax ) break;
    }
    return sh;
}

float calcAO( in vec3 pos, in vec3 nor )
{
	float ao = 0.0;
	for( int i=0; i<32; i++ )
	{
		float h = frand();
        vec3 kv = normalize( vec3(frand(), frand(), frand()) );
        kv *= sign(dot(kv,nor));
		ao += clamp( map(pos+nor*0.001+kv*h*2.0)*1.0, 0.0, 1.0 );
	}
	ao /= 32.0;

	return clamp( ao*1.5-0.1, 0.0, 1.0 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // init random seed
    ivec2 q = ivec2(fragCoord);
    srand( hash(q.x+hash(q.y+hash(iFrame))));

    // camera movement	
    float an = -0.0*iTime/20.0;
    vec3 ro = vec3( 5.0*sin(6.283185*an), 4.0, 5.0*cos(6.283185*an) );
    #if SHAPE>0
    ro.xz *= 1.5;
    ro.y = 0.0;
    #endif
    vec3 ta = vec3( 0.0, 0.0, 0.0 );
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));


    vec3 tot = vec3(0.0);
#if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (2.0*(fragCoord+o)-iResolution.xy)/iResolution.y;
#else    
        vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
#endif

        // create view ray
        vec3 rd = normalize( p.x*uu + p.y*vv + 3.0*ww );

        // raymarch
        const float tmax = 15.0;
        float t = 0.0;
        for( int i=0; i<8192; i++ )
        {
            vec3 pos = ro + rd*t;
            float d = map(pos);
            if( d<0.001 ) break;
            d = min(d*0.8,0.1);
            t += d;
            if( t>tmax ) break;
        }

        vec3 col = vec3(0.0);
        col = vec3(0.05 + 0.05*rd.y);
        if( t<tmax )
        {
            vec3  pos = ro + rd*t;
            vec3  nor = calcNormal( pos, 0.0001 );
            float occ = calcAO( pos+nor*0.001, nor );

            // we do texturing in R4
            vec4 pos4 = r3_to_s3(  pos );
            vec4 nor4 = field_grad( pos4 );
            vec3 mate = tex(iChannel0,pos4,nor4);
            mate = mate*mate;
            mate *= 0.3;

            // sun
            {
            vec3 lig = normalize(vec3(1.0,1.0,0.2));
            float dif = clamp(dot(nor,lig),0.0,1.0);
            if( dif>0.001 ) dif *= shadow(pos+nor*0.001, lig, 48.0);
            vec3 hal = normalize(lig-rd);
            float spe = pow(clamp(dot(nor,hal),00.,1.0),32.0);
            spe *= 0.04 + 0.96*pow( clamp(1.0-dot(lig,hal), 0.0, 1.0), 5.0 );
            spe *= 10.0;

            col  = 0.6*mate*vec3(9.0,8.0,7.0)*dif;
            col += spe*dif*mate.x*10.0;

            float fre = clamp(1.0+dot(rd,nor),0.0,1.0);
            col += mate*fre*1.0*(0.25+0.75*dif)*vec3(1.0,0.7,0.4);
            }

            // sky
            {
            vec3 ref = reflect(rd,nor);
            float dif = 0.5+0.5*nor.y;
            float spe = smoothstep(0.3,0.5,ref.y);
            spe *= dif;
            spe *= 0.04 + 0.96*pow( clamp(1.0+dot(rd,nor), 0.0, 1.0), 5.0 );
            if( spe>0.001 ) spe *= shadow(pos+nor*0.001, ref, 8.0);
            col += 3.0*occ*mate*dif*vec3(0.5,0.6,1.0)*1.5;
            col += 1.0*spe;
            }

            // sun bounce
            col += mate*(1.0-occ)*vec3(1.2,0.5,0.2)*0.2;
        }

        // gain
        col = col*1.5/(1.0+col);

        // to gamma space
        col = pow( col, vec3(0.4545) );
        tot += col;
#if AA>1
    }
    tot /= float(AA*AA);
#endif

    // remove color banding through dithering
    tot += (1.0/255.0)*frand();

    fragColor = vec4( tot, 1.0 );
}

#define pi acos(-1.)
#define eps 3./iResolution.y
#define MAX_LEVEL 6.
#define FAR 40.
#define reflectiveTileNum pow(2.,floor(mod(iTime/2.,MAX_LEVEL-1.)))*2.


mat2 rot(float a){
    float c = cos(a), s = sin(a); return mat2(c,-s,s,c);}

float rnd(vec2 st){
    return fract(sin(dot(vec2(12.563,19.99),st)*19.)*9439.8);}

float drawPoint(vec2 uv, vec2 p, float r){
     return 1.-smoothstep(r-eps, r+eps, length(uv - p));
}

float distLine(vec2 p, vec2 A, vec2 B){
  vec2 AB = B-A;
  float t =  clamp(dot(AB,p-A)/dot(AB,AB),0.,1.);
  vec2 P3 = A + (B-A)*t;
  return length(P3-p);
}

float drawLine(vec2 uv, vec2 A, vec2 B, float r){
    return smoothstep(r+eps, r-eps, distLine(uv,A,B));
}

// SHORTER, NEATER, BRANCHLESS, UNIQUE, SINGULAR PURPOSE
// This map function returns the largest scaling factor for a given posision
// the inverse of that is the cell size
float map(vec2 p){
  float s = 0.5, level = 1.; vec2 CP;
  for(float i = 1.; i < MAX_LEVEL; i++){
    s *= 2.;
    CP = abs( fract(p * s) - 0.5 );
    //if we don't pass the random check, add max to index so we break
    i += step( rnd(floor(p * s)), 0.5 ) * MAX_LEVEL;
  }
  return s;
}

//SINGULAR // return the minimum distance to an axis in the rd direction
vec4 calcT(vec2 p, vec2 rd, vec2 delta){
    float s = map(p);
    vec2 t;
    t.x = rd.x < 0. ? ((p.x*s - floor(p.x*s))/s)*delta.x : ((ceil(p.x*s) - p.x*s)/s)*delta.x; 
    t.y = rd.y < 0. ? ((p.y*s - floor(p.y*s))/s)*delta.y : ((ceil(p.y*s) - p.y*s)/s)*delta.y; 
    vec2 n = t.x < t.y ? vec2(1.,0.) * -sign(rd.x) : 
                         vec2(0.,1.) * -sign(rd.y) ;

    return vec4(min(t.x,t.y), s, n);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

  // Normalized pixel coordinates (from 0 to 1)
  vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
  uv*=2.;

  //get local uv scale
  float s = map(uv);
  vec2 CP = abs( fract(uv * s) - 0.5 );


  //make scale 0 wherever the localUV would be an edge
  //s *= (1.-step(0.5 - 0.002 * s, max(CP.x, CP.y)));
  //set color to inverse of scale, the cell size at each uv
  float f = sqrt(s)/(MAX_LEVEL);
  vec3 col = clamp(vec3(f*f*f,f*f,f), 0., 1.);
  col = mix(col,vec3(0.,0.,0.1)/max(CP.x, CP.y),float(s==reflectiveTileNum));
  col *= (1.-step(0.5 - eps * s*1., max(CP.x, CP.y)));

 // col /= ;

  vec2 ro = vec2(0.5), rd = vec2(-1.,0.);
  rd *= rot(iTime/8.);
  if(iMouse.z > 0.5) 
      rd = normalize((iMouse.xy*2.-iResolution.xy)*2./iResolution.y - ro);
  //col = mix(col, vec3(0.,1.,0.), drawLine(uv,ro,ro+rd*8.,0.01));
  vec2 delta = 1./max(abs(rd), eps);
  float t = calcT(ro + rd * .01, rd, delta).x;
  col = mix(col, vec3(0.,1.,0.), 1.-step(0.03,length(uv-ro)));
  col = mix(col, vec3(0.,1.,0.), drawLine(uv,ro,ro+rd*t,0.01));
  //ro += rd * t;

  //the tricky part of this algorithm as I have it is
  //I collect t, scale and normal in t_s_n
  //but the scale is for cell of the current position
  //the normal is for the cell that we will hit
  //so for each iteration I have to use the normal found in
  //the previous interation to because that becomes the normal
  //as we hit it, which allows for calculating the reflected ray
  //if we need to reflect
  vec4 t_s_n; vec2 n;
  for(float i = 0.; i < FAR; i++){

    //get t,scale and normal all at once
    t_s_n = calcT(ro+rd*0.01, rd, delta);

    //if the scale of the current cell the ro is on is 2,
    //don't advance the ray, just calculate a relfected ray
    //and a new delta for that ray, 
    //optionally nudge the ro out in the direction of the normal
    if(t_s_n.y == reflectiveTileNum) { 
        rd = reflect(rd,n); 
        delta = 1./max(abs(rd), eps);
        //ro += n*0.01;
        }

    //if we hit any other cell, we advance the ray to that cell
    //and draw the line from oldRo to new o
    else{
        vec2 oldRo = ro;
        ro += rd * t_s_n.x*1.001;
        col = mix(col, vec3(0.,.8,1.), drawLine(uv,oldRo,ro,0.015));
        }

    //save the normal we just approached to use in the nect
    //iteration
    n = t_s_n.zw;
    //draw our hit point
    col = mix(col, vec3(1.,0.,0.), 1.-step(0.03,length(uv-ro)));
    }

    col = pow(col,vec3(0.7));
    // Output to screen
    fragColor = vec4(col,1.0);
}

#define NUM_OCTAVES 5

vec2 random2(vec2 st) {
	st = vec2(dot(st, vec2(127.1, 311.7)),
		dot(st, vec2(269.5, 183.3)));
	return -1.0 + 2.0 * fract(sin(st) * 43758.5453123 * 0.7897);
}

// Gradient Noise by Inigo Quilez - iq/2013
// https://www.shadertoy.com/view/XdXGW8
float noise(vec2 st) {
	vec2 i = floor(st);
	vec2 f = fract(st);

	vec2 u = f * f * (3.0 - 2.0 * f);

	return mix(mix(dot(random2(i + vec2(0.0, 0.0)), f - vec2(0.0, 0.0)),
		dot(random2(i + vec2(1.0, 0.0)), f - vec2(1.0, 0.0)), u.x),
		mix(dot(random2(i + vec2(0.0, 1.0)), f - vec2(0.0, 1.0)),
			dot(random2(i + vec2(1.0, 1.0)), f - vec2(1.0, 1.0)), u.x), u.y);
}

float fbm(vec2 x) {
	float v = 0.0;
	float a = 0.5;
	vec2 shift = vec2(100);
	// Rotate to reduce axial bias
	mat2 rot = mat2(cos(0.5), sin(0.5), -sin(0.5), cos(0.50));
	for (int i = 0; i < NUM_OCTAVES; ++i) {
		v += a * noise(x);
		x = rot * x * 2.0 + shift;
		a *= 0.5;
	}
	return v;
}

mat2 rotate(float angle) {
    float c = cos(angle);
    float s = sin(angle);
    return mat2(c, -s, s, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.x;
    float bhRadius = 0.1;
    float n = fbm(uv*2.0*rotate(iTime/4.0))+0.8;
    float dist = length(uv);

    vec3 col = vec3(smoothstep(0.35, 0.0, dist - bhRadius));
    col += smoothstep(0.3, 0.0, dist - bhRadius) * vec3(1.0, 0.8, 0.5);
    col *= smoothstep(bhRadius, bhRadius + 0.01, dist) * n;
    col *= fbm((uv+1.0/(dist+bhRadius))*2.0*(dist-bhRadius)*rotate(iTime/16.0))+0.8;

    // Output to screen
    fragColor = vec4(col,1.0);
}

vec3 hsv2rgb(vec3 hsv) {
    float hue = mod(hsv.x, 360.);
    float C = hsv.y * hsv.z;
    float X = C * (1. - abs(mod((hue/60.), 2.)-1.));

    vec3 result_rgb;
    if (hue < 60.) result_rgb = vec3(C, X, 0.);
    else if (hue < 120.) result_rgb = vec3(X, C, 0.);
    else if (hue < 180.) result_rgb = vec3(0., C, X);
    else if (hue < 240.) result_rgb = vec3(0., X, C);
    else if (hue < 300.) result_rgb = vec3(X, 0., C);
    else result_rgb = vec3(C, 0., X);

    float m = hsv.z - C;
    result_rgb = (result_rgb+m);
    return result_rgb;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Relative position to center
    vec2 to_center = vec2(uv.x - 0.5, uv.y - 0.5);

    // Calculating the polar coordinates
    vec2 polar_coord = vec2(length(to_center), atan(to_center.y,to_center.x));

    // Coordinate to HSV
    vec3 hsv = vec3((polar_coord.y/6.28+0.5)*360.+180.+iTime*20., length(to_center)*2.5, 1.0);

    // HSV to RGB
    vec3 rgb = hsv2rgb(hsv);

    // Output to screen
    fragColor = vec4(rgb, 1.);
}

float random (in vec2 _st) {
    return fract(sin(dot(_st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}
float noise (in vec2 _st) {
    vec2 i = floor(_st);
    vec2 f = fract(_st);
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    vec2 u = f * f * (3.0 - 2.0 * f);

    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}
vec2 random2(in vec2 _st ){
    _st = vec2(
            dot(_st,vec2(127.326,321.324)),
            dot(_st,vec2(15.31,45.332))
                    );
    return 2.0*fract(sin(_st)*432.23) +1.0; //1.0 ~ 3.0
}
float snoise(vec2 st){ //Gradient Noise
    vec2 i = fract(st);//取小数
    vec2 k = floor(st);//向负无穷取整
    vec2 u = i*i*(3.0-2.0*i);//插值函数
    return  mix(
                    mix(dot(random2(k + vec2(0.0,0.0)),i - vec2(0.0,0.0)),
                        dot(random2(k + vec2(0.0,1.0)),i - vec2(0.0,1.0)),
                         u.y),
                    mix(dot(random2(k + vec2(1.0,0.0)),i - vec2(1.0,0.0)),
                        dot(random2(k + vec2(1.0,1.0)),i - vec2(1.0,1.0)),
                         u.y),
                       u.x
                   );//二维noise
}
#define FBM_COUNT  5
float fbm(vec2 st){

    float result =0.0;//输出值
    float A = 0.5;//振幅
    mat2 rotate = mat2(cos(0.5),sin(0.5),//旋转矩阵
                        -sin(0.5),cos(0.5));
    for(int i= 0;i<FBM_COUNT;i++){//分型布朗运动核心
        result += A * noise(st);
        st *=2.;//频率2倍
        st *= rotate;
        A *=0.5;//振幅1/2倍
    }
    return result;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv*=3.;
    vec2 st = vec2(0.0);
    st.x = fbm(uv +  iTime *0.112);
    st.y = fbm(uv);
    vec2 st2 =vec2(0.0);
    st2.x = fbm(st + uv + iTime *0.15);
    st2.y = fbm(st + uv + iTime *0.26);
    float f = fbm(st + st2);
    vec3 color = vec3(clamp(f*f,0.0,1.0),clamp(mix(0.6784,0.6345,f*f*(f-2.0)),0.0,1.0),clamp(f*f*4.0,0.0,1.0));
    color = mix(color,vec3(0.1543,0.4345,0.7533),f*f*0.76);
    color = mix(color,vec3(0.68777,0.0,0.7),clamp(length(st2.x),0.0,1.0));
    color = mix(color,vec3(0.1543,1.,1.),clamp(length(st),0.0,1.0));
    fragColor = vec4((f*f*f+.6*f*f+.5*f)*color,1.0);

}

// Common


// Hashing function
// Returns a random number in [-1,1]
float Hash(float seed)
{
  return fract(sin(seed)*43758.5453);
}

// Cosine direction
vec3 Cosine(in float seed,in vec3 nor)
{
  float u=Hash(78.233+seed);
  float v=Hash(10.873+seed);

  // method 3 by fizzer: http://www.amietia.com/lambertnotangent.html
  float a=6.2831853*v;
  u=2.*u-1.;
  return normalize(nor+vec3(sqrt(1.-u*u)*vec2(cos(a),sin(a)),u));
}

// Rotation matrix around z axis
// a : Angle
mat3 rotate_z(float a)
{
  float sa=sin(a);float ca=cos(a);
  return mat3(ca,sa,0.,-sa,ca,0.,0.,0.,1.);
}

// Compute the ray
// m : Mouse position
// p : Pixel
// ro, rd : Ray origin and direction
void Ray(in vec2 m,in vec2 p,out vec3 ro,out vec3 rd)
{
  float a=3.*3.14*m.x;
  float le=5.;

  ro=vec3(40.,0.,-1.);
  ro*=rotate_z(3.*3.14*m.x);

  vec3 ta=vec3(0.,0.,1.);
  vec3 ww=normalize(ta-ro);
  vec3 uu=normalize(cross(ww,vec3(0.,0.,1.)));
  vec3 vv=normalize(cross(uu,ww));
  rd=normalize(p.x*uu+p.y*vv+le*ww);
  rd.z -= 0.1;
}

// Main

const int Steps=1000;
const float Epsilon=.005;// Marching epsilon
const float T=.5;

const float rA=10.;// Maximum and minimum ray marching or sphere tracing distance from origin
const float rB=40.;

// Transforms
vec3 rotateX(vec3 p,float a)
{
  float sa=sin(a);
  float ca=cos(a);
  return vec3(p.x,ca*p.y-sa*p.z,sa*p.y+ca*p.z);
}

vec3 rotateY(vec3 p,float a)
{
  float sa=sin(a);
  float ca=cos(a);
  return vec3(ca*p.x+sa*p.z,p.y,-sa*p.x+ca*p.z);
}

vec3 rotateZ(vec3 p,float a)
{
  float sa=sin(a);
  float ca=cos(a);
  return vec3(ca*p.x+sa*p.y,-sa*p.x+ca*p.y,p.z);
}

vec3 translateX(vec3 p,float a)
{
  return vec3(p.x+a,p.y,p.z);
}

vec3 translateY(vec3 p,float a)
{
  return vec3(p.x,p.y+a,p.z);
}

vec3 translateZ(vec3 p,float a)
{
  return vec3(p.x,p.y,p.z+a);
}

// Smooth cubic falloff function
// x : distance
// R : radius
float falloff(float x,float R)
{
  float u=clamp(x/R,0.,1.);
  float v=(1.-u*u);
  return v*v*v;
}

// Primitives

// Point skeleton
// p : point
// c : center of skeleton
// e : energy associated to skeleton
// R : radius
float point(vec3 p,vec3 c,float e,float R)
{
  return e*falloff(length(p-c),R);
}

// Operators

// Blending
// a,b : field function of the sub-trees
vec4 ColorBlend(vec4 a,vec4 b)
{
  return vec4(a.x+b.x, ((a.yzw*a.x+b.yzw*b.x)/(a.x+b.x)));
}

// Union
// a : field function of left sub-tree
vec4 ColorIntersection(vec4 a,vec4 b)
{
  vec3 color;
  if(a.x<b.x)
  {
    color = a.yzw;
  }
  else
  {
    color = b.yzw;
  }
  return vec4(max(a.x,b.x), color);
}


vec4 ColorSubstraction(vec4 a,vec4 b)
{
  vec3 color;
  if(a.x>-b.x)
  {
    color = a.yzw;
  }
  else
  {
    color = b.yzw;
  }
  return vec4(max(a.x,-b.x), color);
}

vec4 ColorUnion(vec4 a,vec4 b)
{
  vec3 color;
  if(a.x<b.x)
  {
    color = a.yzw;
  }
  else
  {
    color = b.yzw;
  }
  return vec4(min(a.x,b.x), color);
}


vec3 turbulence(in vec3 p)
{
    return vec3(0.01*sin(p.x*60.), 0.1*cos(p.y*20.)*sin(p.x*60.), 0.1*sin(p.z*5.));
}


