// https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm

vec4 SDFSphere( vec3 p, vec3 c, float s, vec3 color)
{
  return vec4(length(p-c)-s, color);
}

vec4 SDFEllipsoid(vec3 p, vec3 c, vec3 r, vec3 color)
{
  float k0 = length((p-c)/r);
  float k1 = length((p-c)/(r*r));
  return vec4(k0*(k0-1.0)/k1, color);
}

vec4 SDFTorus(vec3 p, vec2 t, vec3 c, vec3 color)
{
  vec2 q = vec2(length(p.xz-c.xz)-t.x,p.y-c.y);
  return vec4(length(q)-t.y, color);
}

vec4 SDFSegment(vec3 p, vec3 a, vec3 b, float r, vec3 color)
{
  vec3 pa = p - a;
  vec3 ba = b - a;
  float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
  return vec4(length( pa - ba*h ) - r, color);
}

vec4 SDFBox(vec3 p, vec3 b, vec3 color)
{
  vec3 q = abs(p) - b;
  return vec4(length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0), color);
}


vec4 SDFCappedCone(vec3 p, vec3 a, vec3 b, float ra, float rb, vec3 color)
{
    float rba  = rb-ra;
    float baba = dot(b-a,b-a);
    float papa = dot(p-a,p-a);
    float paba = dot(p-a,b-a)/baba;
    float x = sqrt( papa - paba*paba*baba );
    float cax = max(0.0,x-((paba<0.5)?ra:rb));
    float cay = abs(paba-0.5)-0.5;
    float k = rba*rba + baba;
    float f = clamp( (rba*(x-ra)+paba*baba)/k, 0.0, 1.0 );
    float cbx = x-ra - f*rba;
    float cby = paba - f;
    float s = (cbx < 0.0 && cay < 0.0) ? -1.0 : 1.0;
    return vec4(s*sqrt( min(cax*cax + cay*cay*baba,
                       cbx*cbx + cby*cby*baba) ), color);
}

vec4 SDFPlane( vec3 p, vec3 n, float h, vec3 color)
{
  return vec4(dot(p,n) + h, color);
}

// Potential field of the object
// p : point
vec4 Object(vec3 p)
{
  vec3 floorP = translateZ(p, 8.5);

  p = rotateY(p, -.2);

  // Tete
  vec3 headCenter = vec3(0.,0.,0.);
  vec3 headP = translateX(p, -0.5);
  headP = rotateY(headP, -0.2);
  headP = rotateX(headP, 0.1);
  headP = rotateZ(headP, 0.2);
  vec3 eyesP = headP;
  headP = rotateZ(headP, -1.5);
