// Calculate object normal
// p : point
vec3 ObjectNormal(in vec3 p,out vec3 obj_color)
{
  float eps=.0001;
  vec3 n;
  vec4 V=Object(p);
  float v = V.x;
  obj_color = V.yzw;
  n.x=Object(vec3(p.x+eps,p.y,p.z)).x-v;
  n.y=Object(vec3(p.x,p.y+eps,p.z)).x-v;
  n.z=Object(vec3(p.x,p.y,p.z+eps)).x-v;
  return normalize(n);
}

// Trace ray using ray marching
// o : ray origin
// u : ray direction
// h : hit
// s : Number of steps
float Trace(vec3 o,vec3 u,float rB,out bool h,out int s)
{
  h=false;

  // Don't start at the origin, instead move a little bit forward
  float t=rA;

  for(int i=0;i<Steps;i++)
  {
    s=i;
    vec3 p=o+t*u;
    float v=Object(p).x;
    // Hit object
    if(v>0.)
    {
      s=i;
      h=true;
      break;
    }
    // Move along ray
    t+=Epsilon;
    // Escape marched far away
    if(t>rB)
    {
      break;
    }
  }
  return t;
}

// Trace ray using ray marching
// o : ray origin
// u : ray direction
// h : hit
// s : Number of steps
float SphereTrace(vec3 o,vec3 u,float rB,out bool h,out int s)
{
  h=false;

  // Don't start at the origin, instead move a little bit forward
  float t=rA;

  for(int i=0;i<Steps;i++)
  {
    s=i;
    vec3 p=o+t*u;
    float v=Object(p).x;
    // Hit object
    if(v<0.001)
    {
      s=i;
      h=true;
      break;
    }
    // Move along ray
    t+=max(Epsilon,abs(v)/4.);
    // Escape marched far away
    if(t>rB)
    {
      break;
    }
  }
  return t;
}

// Ambient occlusion
// p : Point
// n : Normal
// a : Number of smaples
float AmbientOcclusion(vec3 p,vec3 n,int a)
{
  if(a==0){return 1.;}

  float ao=0.;

  for(int i=0;i<a;i++)
  {
    vec3 d=Cosine(581.123*float(i),n);

    int s;
    bool h;
    float t=SphereTrace(p,d,10.,h,s);
    if(!h){ao+=1.;}
    else if(t>5.)
    {
      ao+=1.;
    }
  }

  ao/=float(a);
  return ao;
}

// Background color
vec3 background(vec3 rd)
{
  return mix(vec3(.14,.11,.55),vec3(.13,.20,.18),rd.z*.5+.5);
}

float Light(vec3 p,vec3 n)
{
  // point light
  const vec3 lp=vec3(5.,10.,25.);

  vec3 l=normalize(lp-p);

  // Not even Phong shading, use weighted cosine instead for smooth transitions
  float diff=pow(.5*(1.+dot(n,l)),2.);

  bool h;
  int s;
  float t=SphereTrace(p+.1*n,l,100.,h,s);
  if(!h)
  {
    return diff;
  }
  return 0.;
}

float SmoothLight(vec3 p,vec3 n,int a)
{
  if(a==0)
  return 1.;

  // point light
  const vec3 lp=vec3(5.,10.,25.);

  vec3 l=normalize(lp-p);

  float lo=0.;

  for(int i=0;i<a;i++)
  {
    vec3 d=Cosine(581.123*float(i),n);
    d=normalize(l+d*.15);
    int s;
    bool h;
    float t=SphereTrace(p,d,10.,h,s);
    if(!h){lo+=1.;}
    else if(t>100.)
    {
      lo+=1.;
    }
  }

  lo/=float(a);
  return lo;

}

// Shading and lighting
// p : point,
// n : normal at point
vec3 Shade(vec3 p,vec3 n)
{
  vec3 c=.25+.25*background(n);
  c+=.15*AmbientOcclusion(p+.1*n,n,0)*vec3(1.,1.,1.);
  c+=.35*Light(p,n);
  return c;
}

// Shading with number of steps
vec3 ShadeSteps(int n)
{
  float t=float(n)/(float(Steps-1));
  return .5+mix(vec3(.05,.05,.5),vec3(.65,.39,.65),t);
}

// Picture in picture
// pixel : Pixel
// pip : Boolean, true if pixel was in sub-picture zone
vec2 Pip(in vec2 pixel,out bool pip)
{
  // Pixel coordinates
  vec2 p=(-iResolution.xy+2.*pixel)/iResolution.y;
  if(pip==true)
  {
    const float fraction=1./4.;
    // Recompute pixel coordinates in sub-picture
    if((pixel.x<iResolution.x*fraction)&&(pixel.y<iResolution.y*fraction))
    {
      p=(-iResolution.xy*fraction+2.*pixel)/(iResolution.y*fraction);
      pip=true;
    }
    else
    {
      pip=false;
    }
  }
  return p;
}

// Image
void mainImage(out vec4 color,in vec2 pxy)
{
  // Picture in picture on
  bool pip=true;

  // Pixel
  vec2 pixel=Pip(pxy,pip);

  // Mouse
  vec2 m=iMouse.xy/iResolution.xy;

  // Camera
  vec3 ro,rd;
  Ray(m,pixel,ro,rd);

  // Trace ray

  // Hit and number of steps
  bool hit;
  int s;

  float t=SphereTrace(ro,rd,100.,hit,s);

  // Position
  vec3 pt=ro+t*rd;

  // Shade background
  vec3 rgb=background(rd);

  if(hit)
  {
    // Compute normal
    vec3 obj_color;
    vec3 n=ObjectNormal(pt, obj_color);

    // Shade object with light
    rgb=Shade(pt,n) * obj_color;
  }

  // Uncomment this line to shade image with false colors representing the number of steps
  if(pip==true)
  {
    rgb=ShadeSteps(s);
  }

  color=vec4(rgb,1.);
}

// 
// Somewhere I made full replication of some plugins/effects from After Effects.
// All plugins are written in C++ and use Metal for rendering.
// So, I would like to demonstrate some of them.
//
// This shader shows possible implementation of Tint effect from After Effects.
//
// Effect Inputs:
// - Map Black To: Color
// - Map White To: Color
// - Amount to Tint: 0...100
// 
// The link about effect. The video is not mine, it's just for effect demonstration.
// https://www.youtube.com/watch?v=f7EOumHGyMs
// 

const float amountMin = 0.0;
const float amountMax = 100.0;

const vec3 luminanceWeighting = vec3(0.2125, 0.7154, 0.0721);

vec3 tintAdjust(vec3 color, vec3 mapBlackTo, vec3 mapWhiteTo, float amount) {
    float luminance = dot(color, luminanceWeighting);
    return mix(color, mix(mapBlackTo, mapWhiteTo, luminance), amount);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 c = texture(iChannel0, uv);

    float value = abs(sin(iTime * 0.5));
    float amount = mix(amountMin, amountMax, value);
    float amountNormal = amount/(amountMax - amountMin);
    float targetAmount = mix(0.0, 1.0, amountNormal);

    vec4 mapBlackTo = vec4(1, 0, 0, 1);
    vec4 mapWhiteTo = vec4(0, 0, 1, 1);

    c.rgb = tintAdjust(c.rgb, mapBlackTo.rgb, mapWhiteTo.rgb, targetAmount);

    fragColor = c;
}

mat2 rot(float a){return mat2(cos(a),sin(a),-sin(a),cos(a));}
float pi = acos(-1.);
vec2 pmod(vec2 p, float r){float a = atan(p.x,p.y)+pi/r;float n=(pi*2.)/r;return p*rot(-(floor(a/n)*n));}
float sdRoundedCylinder( vec3 p, float ra, float rb, float h )
{
  vec2 d = vec2( length(p.xz)-2.0*ra+rb, abs(p.y) - h );
  return min(max(d.x,d.y),0.0) + length(max(d,0.0)) - rb;
}
float bo(vec3 p,vec3 s){p=abs(p)-s;return max(p.x,max(p.y,p.z));}
float rand(vec3 p)
{
    p  = fract( p*0.3183099+.1 );
	p *= 17.0;
    return fract( p.x*p.y*p.z*(p.x+p.y+p.z) );
}

float noise(vec3 p)
{
    vec3 fp = floor(p);
	vec3 fs = fract(p);
    vec3 sf = vec3(1.,0.,0.);

    float a = rand(fp);
    float b = rand(fp + sf);
    float c = rand(fp + sf.yxz);
    float d = rand(fp + sf.xxz);
    float e = rand(fp + sf.yyx);
    float f = rand(fp + sf.xyx);
    float g = rand(fp + sf.yxx);
    float h = rand(fp + sf.xxx);

    vec3 u = smoothstep(0.,1.,fs);
  //  u = pow(fs,vec3(3.));

    return mix(mix(
        		mix(a,b,u.x),
        		mix(c,d,u.x),u.y),
               mix(
                mix(e,f,u.x),
                mix(g,h,u.x),u.y),u.z
            );
}
vec2 pumpkin(vec3 p,float y , float st)
{
    float o = 10.;
    float id = 0.;

    p.y *= y;
    float t = (iTime+ st)/10.;
    t = floor(t) + pow(fract(t),3.);
    p.y += abs(sin(t * pi * 2.)) * 10.;
    vec3 pp = p;

    p.xz = pmod(p.xz,13.);
    p.z -= .5;
    float yy = (sin(p.y+iTime*5.)*.5+.5)/1.;

    yy = sin(t * pi * 2.);
    o = length(vec2(length(p.yz)-.3,p.x))-.5 + yy*.1;
    pp.y *= mix(1., yy*1.5,.1);
    float cyl = sdRoundedCylinder(pp+vec3(0.,.8,0.),0.05,0.02,0.3);

    if(o > cyl)
    {
        o = cyl;
        id = 1.;
    }


    return vec2(o,id);
}
vec2 map(vec3 p)
{
    float o = 10.;
    float id = 0.;
    vec3 pp = p;

    vec2 pump = pumpkin(pp,1.,-3.);
    //pp.xz *= (sin(p.y)*.5+.5 + .8);
    pp.x += 3.;
    pp.z -= 2.;
    pp.y += .3;
    // pp.x -= 2.3;
    // pp.z -= .1;
    // pp.y += 0.3;
    //pp.xy *= rot(0.8);
    //pp.yz *= rot(0.4);
    vec2 pump2 = pumpkin(pp,.7,3.);
    if(pump.x > pump2.x)
    {
        pump = pump2;
    }
    o = pump.x;
    id = pump.y;
    p.y += sin(p.x * .5 + p.z * .3 + .3)/3.;
    float b = bo(p-vec3(0.,1.45*2.,0.),vec3(13.,2.,13.));

    if(o > b)
    {
        o = b;
		if(o < .1)
        {
            o -= noise(p*10.)/30.;
            o -= noise(p * 30.)/70.;
        }
        id = 2.;
    }
    else if(o < 0.03)
    {
        o -= noise(p*100.)/100.;
    }
    return vec2(o,id);
}

vec2 march(vec3 cp , vec3 rd)
{
    float depth = 0.;
    for(int i = 0 ; i< 99 ; i++)
    {
        vec3 rp = cp + rd * depth;
        vec2 d = map(rp);
        if(abs(d.x) < 0.001)
        {
            return vec2(depth,d.y);
        }
        depth += d.x;
    }
    return vec2(-depth , 0.);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
	vec3 cp = vec3(-3.,-2.,-12.);
    cp.xz *= rot(iTime/12.);
    cp.y += sin(iTime/6.);
    vec3 target = vec3(0.,-1.3,0.);
    target.xz += sin(iTime/7.)/1.;
    cp.x += step(sin(iTime/2.),0.3) * -10.;
    cp.y += step(sin(iTime/2.),0.3) * -10.;
    vec3 col = vec3(0.);

    vec3 cd = normalize(vec3(target - cp));
    vec3 cs = normalize(cross(cd , vec3(0.,1.,0.)));
    vec3 cu = normalize(cross(cd,cs));

    float fov = 2.5;

    vec3 rd = normalize(cd * fov + cs * p.x + cu * p.y);

    vec2 d = march(cp,rd);
    if( d.x > 0.)
    {
       	vec2 e = vec2(1.0, -1.0) * .05;
        vec3 pos = cp + rd * d.x;
        vec3 N = normalize(
                  e.xyy * map(pos + e.xyy).x +
                  e.yyx * map(pos + e.yyx).x +
                  e.yxy * map(pos + e.yxy).x +
                  e.xxx * map(pos + e.xxx).x);
        vec3 sun = normalize(vec3(5.,4.,2.));
       // sun.xz *= rot(iTime/2.);
        float diff = max(0.,dot(-sun,N));
        diff = mix(diff , 1.,.1);
        float sp = max(0.,dot(rd,reflect(N,sun)));
        sp = pow(sp,1.6) * .3;
        vec3 mat = mix(vec3(1.,0.5,0.),vec3(0.7,1.,0.),d.y);
        if(d.y > 1.)mat = mix(vec3(.5,.15,0.2),vec3(0.2,0.23,0.),noise(pos*100.*(1./(d.x*.3)) ));
        float shadow = step(march(pos + N * 0.01,-sun).x,0.);
        float rim = pow(clamp(1. - dot(N, -rd), 0., 1.), 4.) * (.2+step(d.y,1.)*.2);
        float up = max(0.,dot(N,vec3(0.,-1.,0.)));
        float down = max(0.,dot(N,vec3(0.,1.,0.)));

        col = sp * mat + diff * mat + up * vec3(0.1) + down * vec3(0.1,0.12,.1);
        col += rim;
        col = floor(col * 3.)/2.4;
        if(d.y > 1.)
        { 
            col =mix( vec3(0.),col,shadow);
        }
    }

    fragColor = vec4(col, 1.0);
}

float sig(float x) {
    return x / (1. + abs(x));
}

float h21 (vec2 a) {
    return fract(sin(dot(a.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}

vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d ) {
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
