     float a = atan(uv.y, uv.x);

    float k = 2. * tanh(4. * cos(0.5 * iTime));
    float time = tanh(4. * cos(k * uv.x + iTime)) + iTime;

    uv.y += 0.025 * tanh(0.5 * cos(16. * uv.x + 2. * time));

    float rand = h21(vec2(floor(100. * uv.y)));

    float f = 0.5 * tanh(4. * cos(rand * 0.5 + time));

    vec2 p = vec2(f, 0.);

    float d = length(uv-p) - 0.1 + 0.08 * tanh(1.5 * cos(100. * pow(h21(uv),4.) + 3. * time));
    float s = step(d, 0.1 + 0.025 * tanh(-4.*cos(0.4 * rand - 2.*time)));

