// a simple (smooth) union / subtraction / paint CSG test with some color blend tightness control for some voxel stuff - 10/10/2021

#define AA 1	// make this 2 if you are feeling cold...

vec3 _basecol = vec3(0.0);	// pure filth

// prims
float sdCylinder( vec3 p, vec2 h )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - h;
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}
float sdSphere(vec3 p, float r)
{
    return length(p)-r;
}
float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

// min/max polynomial
float smin( float a, float b, float k )
{
	float h = clamp( 0.5 + 0.5*(b-a)/k, 0.0, 1.0 );
	return mix( b, a, h ) - k*h*(1.0-h);
}
float smax(float a, float b, float k)
{
    return smin(a, b, -k);
}

float sminCol( float a, float b, float k, vec3 col1,float coltightness)
{
    if (k<=0.0)
    {
        // no smooth...
        if (a>b)
        {
            _basecol = col1;
            return b;
        }
        return a;
    }
    // smoothed...
	float h = clamp( 0.5 + 0.5*(b-a)/k, 0.0, 1.0 );
    float colblend = clamp( 0.5 + 0.5*(b-a)/(k*(coltightness)), 0.0, 1.0 );
    _basecol = mix(col1,_basecol,colblend);
	return mix( b, a, h ) - k*h*(1.0-h);
}

float smaxCol( float a, float b, float k, vec3 col1, float coltightness)
{
    if (k<=0.0)
    {
        if (a<=-b)
        {
            _basecol = col1;
            return -b;
        }
        return a;
    }
    //return smin(a, -b, -k);// subtract
    //return smin(a, b, -k); // intersection
    b=-b;
    k=-k;
	float h = clamp( 0.5 + 0.5*(b-a)/k, 0.0, 1.0 );
    float colblend = clamp( 0.5 + 0.5*(b-a)/(k*(coltightness)), 0.0, 1.0 );
    _basecol = mix(col1,_basecol,colblend);
	return mix( b, a, h ) - k*h*(1.0-h);
}

// same as smaxCol but without the distance blend (so, just paint a shape)
void PaintSmooth(float a,float b, float k, vec3 col1,  float coltightness)
{
    if (k<=0.0)
    {
        // no smooth...
        if (a<=-b)
            _basecol = col1;
        return;
    }
    // smoothed...
    b=-b;
    k=-k;
	float h = clamp( 0.5 + 0.5*(b-a)/k, 0.0, 1.0 );
    float colblend = clamp( 0.5 + 0.5*(b-a)/(k*(coltightness)), 0.0, 1.0 );
    _basecol = mix(col1,_basecol,colblend);
}




float map( in vec3 pos )
{
    float dist_cylinder = sdCylinder(pos+vec3(2.0,1.0,0.0),vec2(1.2,2.5));
    float dist_sphere1 = sdSphere(pos+vec3(0.0,0.0,0.0),1.8);
    float dist_sphere2 = sdSphere(pos+vec3(2.0,-1.0,0.0),1.8);
    float dist_box = sdBox(pos+vec3(-.5,0.0,0.0),vec3(2.1,0.4,0.4));

    // paint shapes
    float dist_box2 = sdBox(pos+vec3(2.0,0.5,0.0),vec3(0.2,0.5,0.2));
    float dist_cylinder2 = sdCylinder(pos+vec3(2.0,1.8,0.0),vec2(2.0,0.3));


   float _basedist = 1000.0f;
    _basecol = vec3(0.0);
    vec3 redcol = vec3(1.0,0.1,0.1);
    vec3 greencol = vec3(0.1,1.0,0.1);
    vec3 bluecol = vec3(0.1,0.1,1.0);
    vec3 yellowcol = vec3(1.0,1.0,0.1);
    vec3 whitecol = vec3(1.0,1.0,1.0);

    float _coltightness = 0.5+sin(iTime)*0.5;//0=tight, 1=loose

    _basedist = sminCol(_basedist, dist_cylinder, 0.0, redcol, _coltightness);  // +red cylinder
    PaintSmooth(_basedist, dist_cylinder2, 0.5, whitecol, _coltightness);// PAINT white cylinder (band)
    _basedist = sminCol(_basedist, dist_sphere1, 0.5, greencol, _coltightness); // +green sphere
    _basedist = smaxCol(_basedist, dist_box, 0.5, bluecol, _coltightness);      // -blue box
    _basedist = smaxCol(_basedist, dist_sphere2, 0.5, yellowcol, _coltightness);// -yellow sphere
    PaintSmooth(_basedist, dist_box2, 0.5,whitecol, _coltightness); // PAINT white cube (square)
    return _basedist;
}

vec3 calcNormal( in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.5773;
    const float eps = 0.01;	//0.0005
    return normalize( e.xyy*map( pos + e.xyy*eps ) + 
					  e.yyx*map( pos + e.yyx*eps ) + 
					  e.yxy*map( pos + e.yxy*eps ) + 
					  e.xxx*map( pos + e.xxx*eps ) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     // camera movement	
        float an = radians(-120.0);
        float yoff = 3.0f;
        if (iMouse.z>0.5)
        {
         an = an+(iMouse.x/iResolution.x)*6.28;
         yoff = yoff+(5.0-((iMouse.y/iResolution.y)*15.0));
        }

    vec3 ro = vec3( 7.0*cos(an), yoff, 7.0*sin(an) );
    vec3 ta = vec3( 0.0, 0.0, 0.0 );
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));

    vec3 tot = vec3(0.0);

    #if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
        #else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
        #endif

	    // create view ray
        vec3 rd = normalize( p.x*uu + p.y*vv + 1.5*ww );

        // raymarch
        const float tmax = 20.0;
        float t = 0.0;
        for( int i=0; i<200; i++ )// overkill
        {
            vec3 pos = ro + t*rd;
            float h = map(pos);
            if( h<0.001 || t>tmax ) break;
            t += h;
        }

        // shading/lighting	
        float v = 1.0-abs(p.y);
        vec3 col = vec3(v*0.1);

        if( t<tmax )
        {
            vec3 mycol = _basecol;
            vec3 pos = ro + t*rd;
            vec3 nor = calcNormal(pos);

            vec3 dir = normalize(vec3(1.0,0.7,0.0));
	        vec3 ref = reflect(rd, nor);
	        float spe = max(dot(ref, dir), 0.0);
	        vec3 spec = vec3(1.0) * pow(spe, 64.);
            float dif = clamp( dot(nor,dir), 0.05, 1.0 );
            col =  mycol*dif;
            col+=spec;
        }
        // gamma        
        col = sqrt( col );
	    tot += col;
    #if AA>1
    }
    tot /= float(AA*AA);
    #endif

	fragColor = vec4( tot, 1.0 );
}

float opSmoothUnion( float d1, float d2, float k )
{
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h);
}

float sdSphere( vec3 p, float s )
{
  return length(p)-s;
} 

float map(vec3 p)
{
	float d = 2.0;
	for (int i = 0; i < 16; i++) {
		float fi = float(i);
		float time = iTime * (fract(fi * 412.531 + 0.513) - 0.5) * 2.0;
		d = opSmoothUnion(
            sdSphere(p + sin(time + fi * vec3(52.5126, 64.62744, 632.25)) * vec3(2.0, 2.0, 0.8), mix(0.5, 1.0, fract(fi * 412.531 + 0.5124))),
			d,
			0.4
		);
	}
	return d;
}

vec3 calcNormal( in vec3 p )
{
    const float h = 1e-5; // or some other value
    const vec2 k = vec2(1,-1);
    return normalize( k.xyy*map( p + k.xyy*h ) + 
                      k.yyx*map( p + k.yyx*h ) + 
                      k.yxy*map( p + k.yxy*h ) + 
                      k.xxx*map( p + k.xxx*h ) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    // screen size is 6m x 6m
	vec3 rayOri = vec3((uv - 0.5) * vec2(iResolution.x/iResolution.y, 1.0) * 6.0, 3.0);
	vec3 rayDir = vec3(0.0, 0.0, -1.0);

	float depth = 0.0;
	vec3 p;

	for(int i = 0; i < 64; i++) {
		p = rayOri + rayDir * depth;
		float dist = map(p);
        depth += dist;
		if (dist < 1e-6) {
			break;
		}
	}

    depth = min(6.0, depth);
	vec3 n = calcNormal(p);
    float b = max(0.0, dot(n, vec3(0.577)));
    vec3 col = (0.5 + 0.5 * cos((b + iTime * 3.0) + uv.xyx * 2.0 + vec3(0,2,4))) * (0.85 + b * 0.35);
    col *= exp( -depth * 0.15 );

    // maximum thickness is 2m in alpha channel
    fragColor = vec4(col, 1.0 - (depth - 0.5) / 2.0);
}

