vec4 rectangle(vec2 uv, vec2 pos, float width, float height, vec3 color)
{
	float t = 0.;
	if ((uv.x > pos.x - width / 2.0) && (uv.x < pos.x + width / 2.)
		&&
        (uv.y > pos.y - height / 2.) && (uv.y < pos.y + height / 2.)) 
      {
		t = 1.;
	  }
	return vec4(color, t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;;
    float kx=max(iResolution.x, iResolution.y)/iResolution.x;
    float ky=max(iResolution.x, iResolution.y)/iResolution.y;

    // Time varying pixel color
    vec3 col1 = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec3 col2=vec3(1.-col1.r,1.-col1.g, 1.-col1.b);

    vec4 rect = rectangle(uv, vec2(.5), .25*kx , .25*ky, col2 );
    // Output to screen 
    fragColor = mix(vec4(col1, 1), rect, rect.a);
}

