float romb(vec3 p) 
{

vec4 oct = vec4(2.5, .7, 0.0, 0.45);


  p = abs(p-oct.xyz);


  float m = p.x+p.y+p.z-oct.w*0.5554;
  vec3 q;
       if(-100.0*p.x < m ) q = p.xyz;
  else if( 3.0*p.y < m ) q = p.yzx;
  else if( -3.5*p.z < m ) q = p.zxy;
  else return m*0.57735027;
     q.xyz -= clamp(sin(iTime)/2.0, 0.0, .19);

  float k = clamp(0.5*(q.z-q.y+oct.w),0.0,oct.w); 
  return length(vec3(q.x,q.y-oct.w+k,q.z-k)*p*4.0);
  }


float octahedron(vec3 p)
{


vec4 s= vec4(2.5, 0.35*2.0 - sin(iTime*2.0)/55.0, 0.0, 0.25);

  p = abs(p - s.xyz);
  vec3 q = p-1.0*clamp(round(p/1.0),-1.0,.3);
  return (q.x+q.y+q.z-s.w)*0.57735027;

}















vec2 map(vec3 pos) {


vec2 res = vec2(1e10, -1.0);

vec2 romb = vec2(romb(pos), 1.0);
vec2 plane = vec2(plane(pos), 2.0);
vec2 octahedron= vec2(octahedron(pos), 3.0);


if(romb.x < res.x) {res = romb;}
if(octahedron.x < res.x) {res = octahedron;}
if(plane.x < res.x) {res = plane;}




return res; 


}

vec2 raycast(vec3 ro, vec3 rd) {
float epsilon = 0.01; 

vec2 res;

float t = 0.0; 
for(int i = 0; i < 180; ++i) 
{
vec2 res = map(ro+rd*t);
if(res.x < epsilon)
{
return vec2(t, res.y);
}
t += res.x;
} 
return res;

}


vec3 calcNormal(vec3 p)
{

   const float h = 0.001; // replace by an appropriate value
    const vec2 k = vec2(1,-1);
    return normalize( k.xyy*map( p + k.xyy*h ).x + 
                      k.yyx*map( p + k.yyx*h ).x + 
                      k.yxy*map( p + k.yxy*h ).x + 
                      k.xxx*map( p + k.xxx*h ).x );  
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
