void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float pi = 3.1415926535;
    float k = -3.0 + 20.0 * abs((asin(sin(iTime*0.055)))/pi*2.0);
    //float k = -3.0 + 20.0 * abs(sin(iTime * 0.055));
    float m = pow(2.0, k);
    vec2 s = vec2(-0.792897, 0.160994);
    float index = 0.0;

    vec2 xy = vec2((fragCoord.x - iResolution.x / 2.0) / iResolution.x / m + s.x, 
                   (fragCoord.y - iResolution.y / 2.0) / iResolution.x / m + s.y);
    vec2 xy1 = xy;
    vec2 tmp = vec2(0.0, 0.0);

    float count = 0.0;

    for (int i = 0; i < 256; i++) {
        if (xy1.x*xy1.x+xy1.y*xy1.y>4.0) break;
        tmp.x = xy.x + xy1.x*xy1.x - xy1.y*xy1.y;
        tmp.y = xy.y + 2.0 * xy1.x * xy1.y;
        xy1 = tmp;

        count++;
    } 
    /*
    if (count < 129.0) index = count / 128.0;
    else if (count < 193.0) index = (count - 128.0) / 64.0;
    else if (count < 225.0) index = (count - 192.0) / 32.0;
    else if (count < 241.0) index = (count - 224.0) / 16.0;
    else if (count < 249.0) index = (count - 240.0) / 8.0;
    else if (count < 252.0) index = (count - 248.0) / 4.0;
    else if (count < 256.0) index = (count - 252.0) / 2.0;
    */

    index = count / 256.0;
    if (count == 256.0) index = 0.0;


    fragColor = vec4(0.0, index, index, 1.0);
}

