}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float t = iTime;
    vec2 q = fragCoord.xy / iResolution.xy;
    vec2 p = -1.0 + 2.0 * q;
    p.x *= iResolution.x/iResolution.y;


    // camera

    vec3 ro = vec3(2.);

    ro.yz*=rot(-1.5); 
    ro.y +=3.;
    ro.xz*=rot(0.1*t);

    vec3 ta = vec3( 0.0 , 0.0, 0.0 );
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
    vec3 rd = normalize( p.x*uu + p.y*vv + 4.0*ww );
    ro.x -=t*.4;

	// raymarch 

    vec3 col = raycast(ro,rd);


	// shade

    col =  .5 *(log(1.+col));
    col = clamp(col,0.,1.);
    fragColor = vec4( col, 1.0 );

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float c = texture(iChannel0, uv).r;

    // https://www.color-hex.com/color-palette/114761
    vec3 palette1[5] = vec3[5](
        vec3(  84,  13, 110 ) / 255.,
        vec3( 238,  66, 102 ) / 255.,
        vec3( 255, 210,  63 ) / 255.,
        vec3(  66, 191, 221 ) / 255.,
        vec3( 187, 230, 228 ) / 255.
    );

    // https://www.color-hex.com/color-palette/114398
    vec3 palette2[5] = vec3[5](
        vec3(  55,  90, 125) / 255.,
        vec3( 190, 110, 130) / 255.,
        vec3( 250, 175, 150) / 255.,
        vec3( 190, 110, 130) / 255.,
        vec3( 245, 115, 130) / 255.
    );


    vec3 col1 = palette1[0];
    vec3 col2 = palette2[0];
    float n = 1./4.;

    for (float i = 0.; i < 5.; i++) {
        float cut = smoothstep(n * i, n * (i + 1.), c);
        col1 = mix(col1, palette1[int(i)], cut);
        col2 = mix(col2, palette2[int(i)], cut);
    }

    float t = sin(iTime) * .5 + .5;
    fragColor = vec4(mix(col1, col2, t),1.0);
}

